vec3 rdr(vec2 uv)
{
    vec3 col = texture(iChannel0, uv).xyz;
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = rdr(uv);
    vec2 off = (vec2(1., -1.)/(iResolution.xy*1.5));
    if (true) // Not so cheap antialiasing
    {
        //col = vec3(1.,0.,0.);
        vec3 acc = col;
        acc += rdr(uv+off.xx);
        acc += rdr(uv+off.xy);
        acc += rdr(uv+off.yy);
        acc += rdr(uv+off.yx);
        col = acc/5.;   
    }
    fragColor = vec4(col,1.0);
}

