vec2 g_random(vec2 ip){
	return fract(sin(
		vec2(dot(ip,vec2(127.1,311.7)), dot(ip,vec2(269.5,183.3)))
	)* 44753.976967) *2. - 1.;
}

float g_noise(vec2 st){
	vec2 ip = floor(st);
	vec2 fp = fract(st);

	vec2 u = fp*fp*fp*(fp*(fp*6.-15.)+10.);//使用三次多项式进行插值
	return mix(
		mix(dot(g_random(ip) , fp-vec2(0.,0.)) , dot(g_random(ip+vec2(1.,0.)) , fp-vec2(1.,0.)) ,u.x),
		mix(dot(g_random(ip+vec2(0.,1.)) , fp-vec2(0.,1.)) , dot(g_random(ip+vec2(1.,1.)) ,fp-vec2(1.,1.)) , u.x),
		u.y
	);
}


float circle(vec2 st, vec2 center){
    return distance(st, center);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec4 beforeColor = vec4(1.0);

    uv.x -= abs(sin(iTime/4.0));
    uv.y -= abs(sin(iTime/2.0))/1.5;
    if(circle(uv, vec2(0,0)) <=0.15){

        beforeColor = vec4(1.000,smoothstep(0.566, 0.98, circle(uv, vec2(0,0))+0.69),0.3, 1.0);
    } 
    uv -= 0.5;


    //To prevent clouds and the sun from moving together, they need a separate coordinate system
    vec2 copy = fragCoord/iResolution.xy;
    copy -= 0.5;
    copy *= vec2(4.,4.);
    copy += g_noise(copy*3.);
    copy.x = sin(iTime/2.0);


    beforeColor += (g_noise(copy) -.5)*.19;
    // Output to screen
    fragColor = beforeColor;
}
