///------------------------------------
// object transformation
vec3 rotate_x(vec3 p, float phi)
{
    float c = cos(phi);	float s = sin(phi);
    return vec3(p.x, c*p.y - s*p.z, s*p.y + c*p.z);
}
vec3 rotate_y(vec3 p, float phi)
{
	float c = cos(phi);	float s = sin(phi);
	return vec3(c*p.x + s*p.z, p.y, c*p.z - s*p.x);
}
vec3 rotate_z(vec3 p, float phi)
{
	float c = cos(phi);	float s = sin(phi);
	return vec3(c*p.x - s*p.y, s*p.x + c*p.y, p.z);
}
///------------------------------------
vec2 rotatev2(vec2 p, float ang)
{
    float c = cos(ang);
    float s = sin(ang);
    return vec2(p.x*c - p.y*s, p.x*s + p.y*c);
}
///------------------------------------
vec3 GetDist(vec3 p  ) 
{	
	vec3 res= vec3(9999.0, -1.0,-1.0);
    vec3 pp=p;
	float planeDist1 = p.y+0.0;  //piso inf
    float planeDist2 = 50.0-p.y;  //piso sup
    float planeDist3 = p.x+30.0; //pared izq
    float planeDist4 = 30.0-p.x;  //pared der
    float planeDist5 = -p.z+40.0;  //pared frente
    float planeDist6 = p.z+40.0;  //pared atras
    res =opU3(res, vec3(planeDist1,-1.0,6.0)); //inf
    float t1=mod(iTime,2.0);
    float t2=mod(iTime,3.0);

    if (t1<t2)
    {
     res =opU3(res, vec3(planeDist2,14.0,MATERIAL_NO)); 
     res =opU3(res, vec3(planeDist3,100.0,MATERIAL_NO)); 
     res =opU3(res, vec3(planeDist4,100.0,MATERIAL_NO)); 
     res =opU3(res, vec3(planeDist5,100. ,MATERIAL_NO)); 
     res =opU3(res, vec3(planeDist6,16.0,MATERIAL_NO)); 
    }    
      p.y=p.y-5.0;
    //rotar centro origen
     float posx=2.5; //es el centro del objeto
     pp=p;
     pp.x=pp.x-posx;
     pp.xz = rotatev2(pp.xz, iTime);
     pp.x=pp.x+posx;

    float sdrb3=  sdRoundBox( pp-vec3(posx,-3.5,0.0), vec3(2.0,1.0,1.0), 1.0 ); //3 in
    float sdrb3A= sdRoundBox(pp-vec3(posx,0.0,0.0), vec3(2.0,1.0,1.0), 1.0 ); //3 inf
    float sdrb3B= sdRoundBox(pp-vec3(posx,3.5,0.0), vec3(2.0,1.0,1.0), 1.0 ); //3 inf

    res =opU3(res, vec3(sdrb3,13.0,-1)); 
    res =opU3(res, vec3(sdrb3A,14.0,-1)); 
    res =opU3(res, vec3(sdrb3B,15.0,-1)); 

   float sdsp1= sdSphere( p-vec3(-10.0,0.0,0.0), 6.0 );
    res =opU3(res, vec3(sdsp1,201.0,-1)); 
    float sdsp2= sdSphere( p-vec3(14.0,3.0,10.0), 6.0 );

   res =opU3(res, vec3(sdsp2,32.0,-1)); 
    //return (dist, id_color, id_material)
    return res;
}
//------------------------------------------------
vec3 GetNormal(vec3 p)
{
	float d = GetDist(p).x;
    vec2 e = vec2(.001, 0);
    vec3 n = d - vec3(
        GetDist(p-e.xyy).x,
        GetDist(p-e.yxy).x,
        GetDist(p-e.yyx).x);
    return normalize(n);
}
float RayMarch(vec3 ro, vec3 rd, int PMaxSteps)
{
    float dO = 0.; 
    vec3 dS=vec3(9999.0,-1.0,-1.0);
    float marchCount = 0.0;
    vec3 p;
    float minDist = 9999.0; 

    for(int i=0; i <= PMaxSteps; i++) 
    {  	p = ro + rd*dO;
        dS = GetDist(p);
        if ( abs(dS.x)<MIN_DIST  || i == PMaxSteps)
        {   mObj.hitbln = true; minDist = abs(dO); break; }

        if(dO>MAX_DIST)
        { mObj.hitbln = false;    minDist = dO;    break; } 
        dO += dS.x;
        marchCount++;
    }
    mObj.dist = minDist;
    mObj.id_color = dS.y;
    mObj.marchCount=marchCount;
    mObj.id_material=dS.z;
    mObj.normal=GetNormal(p);
    mObj.phit=p;
    return dO;
}
//---------------------------------------------------
float GetShadow(vec3 p, vec3 plig)
{
    vec3 lightPos = plig;
    vec3 l = normalize(lightPos-p);
    vec3 n = GetNormal(p);
    float dif = clamp(dot(n, l), 0., 1.);
    float d = RayMarch(p+n*MIN_DIST*2., l , MAX_STEPS/2);
    if(d<length(lightPos-p)) dif *= .1;
    return dif;
}
///============================================================
vec3 amb(vec3 c, float k)
{  return c * k; }

float diff(vec3 p,vec3 lp,vec3 n )
{   vec3 l = normalize(lp - p);
    float dif = clamp(dot(l, n), 0.0, 1.0);
    return dif;
}
float spec( vec3 p, vec3 lp,vec3 rd,vec3 n )
{   vec3 l = normalize(lp - p);
    vec3 r = reflect(-l, n);
    float spe =pow(clamp(dot(r, -rd), 0.0, 1.0), 8.0);
    return spe;
}

vec3 lightingv3(vec3 normal,vec3 p, vec3 lp, vec3 rd, vec3 ro,vec3 col) 
{
    vec3 l = lp - p;
    vec3 ldir = normalize(p-rd);

    float dist = max(length(l), 0.01);
    float atten = min(1./(1. + dist*0.5), 0.2);
    l /= dist;

    vec3 n = normal;
   	vec3 r = reflect(-l, n);

    vec3 amb=amb(col, 0.5);
    float dif = diff( p, lp, n );
    float diff=max(0.,dot(ldir,-n));
    vec3 ln=normalize(lp);

    float spe= spec(  p,  lp, rd, n );
    float fre = pow(clamp(1.0 + dot(n, rd), 0.0, 1.0), 2.0);
    float dom = smoothstep(-1.0, 1.0, r.y);
     float bac=clamp(dot(n,-l),0.0,1.0);
    float rim=pow(1.0+dot(n,rd),3.0);
    float dn=.15*max(0.,dot(normalize(rd),-n));

    float fshadow;
    float sh = clamp(dot(n,normalize(lp)),0.0,1.0);

    if (mObj.blnShadow==true)
    { fshadow=GetShadow(p,lp);}
    else
    {   fshadow=0.5; }
    vec3 lin = amb;
    lin += 1.0*(dif+diff)*sh;
    lin += 2.5*spe;
    lin += 2.5*fre*vec3(1.);
    lin += 0.5*dom*vec3(1.);
    lin += 0.35*bac*vec3(1.);
    lin += 0.35*rim*vec3(1.);
    lin += 0.35*rim*dn*vec3(1.);
    lin *= atten*0.8*col*fshadow;
    lin = pow(lin,vec3(0.35));
    return lin;
}
