//-------------------------------
vec3 Getluz(vec3 p, vec3 ro, vec3 rd, vec3 nor , vec3 colobj ,vec3 plight_pos)
{
    float intensity=1.0;
     vec3 result;

    result = lightingv3( nor, p, plight_pos,  rd,ro, colobj);
    return result;
}
///-------------------------------------
vec3 render_sky_color(vec3 rd)
{   float t = (rd.x + 1.0) / 2.0;
    vec3 col= vec3((1.0 - t) + t * 0.3, (1.0 - t) + t * 0.5, (1.0 - t) + t);
	vec3  sky = mix(vec3(.0, .1, .4)*col, vec3(.3, .6, .8), 1.0 - rd.y);
	return sky;
}
//------------------------------------------------
vec3 getMaterial( vec3 pp, float id_material)
{ vec3 col=vec3(1.0);
  vec3 p=pp;
  vec3 l1;

    if (id_material==5.0)
    {
        float escala=0.25;
    	float d = mod(floor(p.x*escala)+floor(p.z*escala*2.0),2.0);
	    return vec3( clamp(d,0.0,1.0) );
    }

    if (id_material==6.0)
    {
        float escala=0.25;
    	float d = mod(floor(p.x*escala)+floor(p.z*escala*2.0),2.0);
	    vec3 col= vec3( clamp(d,0.0,1.0) );
        l1= amb(col, 0.5) + col*diff( p,light_pos1, mObj.normal) + col*spec( p, light_pos1,mObj.rd, mObj.normal);
        return l1;
    }
}
//-------------------------------------------------
vec3 GetColorYMaterial(vec3 p,  vec3 n, vec3 ro,  vec3 rd, int id_color, float id_material)
{  	vec3 colobj; 

    if( mObj.hitbln==false) return  render_sky_color(rd);

    if (id_color<100)
		{ colobj=getColor(int( id_color)); }

    if ( float( id_color)>=100.0  && float( id_color)<=199.0 ) 
 	{  vec3 coltex=getColorTextura(p, n, int( id_color)); colobj=coltex;}

    if (id_material>-1.0 && id_color==-1)
    { colobj=vec3(0.5); colobj*=getMaterial(p, id_material); return colobj; }
    return colobj;
}

vec3 renderReflect(TObj Obj, vec3 ro, vec3 rd, vec3 col )
{
   vec3 p;
   float t;
   vec3 colobj;  vec3 n;
  /////-----------REFLECT--------------
    if (int( Obj.id_color) ==201)
    {
     vec3 colref;
     for(int i=0; i<2; ++i)
      {  
        t=RayMarch(ro,rd, MAX_STEPS);
        Obj=mObj;

       if(t>MAX_DIST)
       { colobj= render_sky_color(rd);return  colobj;}  

        if( t<0.0 || t>MAX_DIST ) break;
        p=ro+rd*t;

        n=GetNormal(p);
        //produce ondas de mar
        //n=normalize(n+0.01*sin(10.0* p+2.0*iTime));

        rd=reflect(rd,n);
        ro =p+rd * MIN_DIST * 3.;

        if (t<MAX_DIST)
        colobj=GetColorYMaterial( p, n, ro, rd,  int( Obj.id_color), Obj.id_material)*2.0;

        colref=  Getluz( p,ro,rd, n, colobj ,light_pos1);
        colref+= Getluz( p,ro,rd, n, colobj ,light_pos2);
       }
     col+=colref/(float(REFLECT));
   } 
   return col;
} 

vec3 Render(vec3 ro, vec3 rd)
{
  vec3 col = vec3(0);
   TObj Obj;
   mObj.rd=rd;mObj.ro=ro;
   vec3 p ;

   float d=RayMarch(ro,rd, MAX_STEPS);

    Obj=mObj;
    if(mObj.hitbln) 
    {
        p = (ro + rd * d );  
        vec3 nor=mObj.normal;
        vec3 colobj=GetColorYMaterial( p, nor, ro, rd,  int( Obj.id_color), Obj.id_material);
        vec3 result=  Getluz( p,ro,rd, nor, colobj ,light_pos1);
        result+= Getluz( p,ro,rd, nor, colobj ,light_pos2);
        col= result;
    }
    else if(d>MAX_DIST)
    col= render_sky_color(rd);

    col= renderReflect(Obj,  ro,  rd,  col );

   return col;
}
