#define rot(a) mat2(cos(a),sin(a),-sin(a),cos(a))

float map(vec3 p){
    p.xy*=rot(iTime*.5);
    p.xz*=rot(iTime*.3);
    float h=sin(iTime)*.5+.5;

    // repeat x=0 slice between -h to h which means this line stretches simple circle align x-axis 
    p.x-=clamp(p.x,-h,h);

    // This block itself makes a dounut by the following steps:
    // 1. calculate distance from edge of circle(radius is 0.3)
    // 2. SDF of circle in a space of Z and distance from the circle which looks a donut in 3D space.
    return length(vec2(
        length(p.xy)-.3, // 1
        p.z))-.1; // 2
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv=(gl_FragCoord.xy-.5*iResolution.xy)/iResolution.y;
    vec3 rd=normalize(vec3(uv,1));
    vec3 p=vec3(0.0,0,-3);
    float d=1.,i;
    for(;++i<99.&&d>.001;)p+=rd*(d=map(p));
    if(d<.001)fragColor+=3./i;
}
