 	light_pos1= vec3(5.0, 3.0, 5.0 );  light_color1 =1.25*vec3( 1.0 ,0.1,0.1);
 	light_pos2= vec3( -5.0, 6.0, -5.0 ); light_color2 =1.25* vec3( .1 ,0.1,1.0); //light_color1; //vec3( 1.0, 1.0, 1.0 );


    vec3 ro=vec3(1.0,0.5,-2.6+2.0*abs(sin(t*0.5)) );
    vec3 rd=normalize(vec3(uv,1.0));

    light_pos1+=ro;
    light_pos2+=ro;


    vec3 col = vec3(0);
    TObj Obj;
    mObj.rd=rd;
    mObj.ro=ro;

