        //col *= 1.0 - pow(d / 100.0, 1.5);
        col *= 1.0 - pow(d /(MAX_DIST) , 3.5);    
        col = pow(col, vec3(1.0/2.2));

    }
    else if(d>MAX_DIST)
    //col= getSkyColB(rd, uv, vec3(0.0));
     col= getSkyColA(rd);


    fragColor = vec4(col,1.0);
}
