//change these constants to get different patterns!
#define c1 vec3(1.,0.5,1.5)

vec2 triangle_wave(vec2 a,float scale){
    return abs(fract((a+c1.xy)*scale)-.5);
    //return abs(fract((a+c1.xy)*scale+iTime/500.)-.5); //morphing
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col = vec3(0.);
    float t1 = 8.*8.;
    vec2 uv = (fragCoord)/iResolution.y/t1/2.0;
    uv += vec2(iTime/2.0,iTime/3.0)/t1/8.0;
    float scale = c1.z;

    vec2 t2 = vec2(0.);
        vec2 t3 = vec2(0.);   
        for(int k = 0; k < 12; k++){

            uv -= (t2.yx);
            //uv -= (t2.yx)/(1.+(t2.x-t2.y)/4.);
            vec2 temp = t2;
            t2 = triangle_wave(uv.yx+.5,scale);
            t3 = triangle_wave(uv,scale);
            //t2 = triangle_wave(uv.yx+.5,scale)/(1.+t2);
            //t3 = triangle_wave(uv,scale)/(1.+t3);

            uv.yx = t3-t2;

        col.x = 1.-abs(uv.x+uv.y+col.x);
        col = col.yzx;

        //col.x = abs(uv.x+uv.y-col.x*2.);
        //col = abs(1.-col.yzx);

        uv /= scale*scale;
        //uv /= 1.+temp.yx;
      }
    fragColor = vec4(col,1.0);   
}
