//https://www.shadertoy.com/user/Catzpaw
float snow(vec2 uv,float scale)
{
	float w=1.;//smoothstep(1.,0.,uv.y*(scale/10.));
    float t=iTime*0.5;
	if(w<.1)return 0.;
	uv+=t/scale;
    uv.y+=t*2./scale;
    uv.x+=sin(uv.y+t*.5)/scale;
	uv*=scale;
    vec2 s=floor(uv),f=fract(uv),p;
    float k=3.,d;
	p=.5+.35*sin(11.*fract(sin((s+p+scale)*mat2(7,3,6,5))*5.))-f;
    d=length(p);
    k=min(d,k);
	k=smoothstep(0.,k,sin(f.x+f.y)*0.01);
    	return k*w;
}

vec3 Nieve(vec2 puv)
{
    vec2 uv=puv;
	vec3 finalColor=vec3(0);
	float c=smoothstep(1.,0.3,clamp(uv.y*.3+.8,0.,.75));
	c+=snow(uv,30.)*.3;
	c+=snow(uv,20.)*.5;
	c+=snow(uv,15.)*.8;
	c+=snow(uv,10.);
	c+=snow(uv,8.);
	c+=snow(uv,6.);
	finalColor=(vec3(c));
    return  finalColor;
}

vec3 render_sky_color(vec3 rd)
{

    float t = (rd.x + 1.0) / 2.0;
    vec3 col= vec3((1.0 - t) + t * 0.3, (1.0 - t) + t * 0.5, (1.0 - t) + t);

	vec3  sky = mix(vec3(.0, .1, .4)*col, vec3(.3, .6, .8), 1.0 - rd.y);
    vec3 nieve= Nieve(rd.xy);
    sky=mix(sky,nieve, 0.5);
	return sky;
}

