vec3 GetColorYMaterial(vec3 p,  vec3 n, vec3 ro,  vec3 rd, int id_color, float id_material)
{
   	vec3 colobj; 

     if( mObj.hitbln==false) return  getSkyColA(rd);

    if (id_color<100)
		{ colobj=getColor(int( id_color)); }

if (id_material>-1.0 && id_color==-1)
        { 
            colobj=vec3(0.5);
            colobj*=getMaterial(p, id_material); 
            return colobj;
        }
    return colobj;
}

vec3 renderReflect(TObj Obj, vec3 ro, vec3 rd, vec3 col )
{  vec3 p;
   float t;
   vec3 colobj;  vec3 n;
    /////-----------REFLECT--------------
    if (int( Obj.id_color) ==201)
    {

     vec3 colref;
     for(int i=0; i<2; ++i)
      {  

        t=RayMarch(ro,rd, MAX_STEPS);

        Obj=mObj;


       if(t>MAX_DIST)
       {
         colobj= render_sky_color(rd);
         return  colobj;
       }  

        if( t<0.0 || t>MAX_DIST ) break;
        p=ro+rd*t;

        n=GetNormal(p);
        //produce ondas de mar
        //n=normalize(n+0.01*sin(10.0* p+2.0*iTime));

        rd=reflect(rd,n);
        ro =p+rd * MIN_DIST * 3.;

        if (t<MAX_DIST)
        colobj=GetColorYMaterial( p, n, ro, rd,  int( Obj.id_color), Obj.id_material)*2.0;
        colref=  Getluz( p,ro,rd, n, colobj ,light_pos1);
        colref+= Getluz( p,ro,rd, n, colobj ,light_pos2);
       }
     col+=colref/(float(REFLECT));
   } 
   return col;
} 

vec3 Render(vec3 ro, vec3 rd)
{
  vec3 col = vec3(0);
   TObj Obj;
   mObj.rd=rd;
   mObj.ro=ro;
   vec3 p ;

   //float d = RayMarch(ro, rd);
   float d=RayMarch(ro,rd, MAX_STEPS);

    Obj=mObj;
    if(mObj.hitbln) 
    {
        p = (ro + rd * d );  


        vec3 nor=mObj.normal;

        vec3 colobj;
        colobj=GetColorYMaterial( p, nor, ro, rd,  int( Obj.id_color), Obj.id_material);

        float dif1=1.0;
        vec3 result;
        result=  Getluz( p,ro,rd, nor, colobj ,light_pos1);
        result+= Getluz( p,ro,rd, nor, colobj ,light_pos2);
        col= result;
    }
    else if(d>MAX_DIST)
    col= render_sky_color(rd);

    col= renderReflect(Obj,  ro,  rd,  col );

   return col;
}

