Main {
    vec4 n = A(U+vec2(0,1))*B(U+vec2(0,1)).w;
    vec4 e = A(U+vec2(1,0))*B(U+vec2(1,0)).w;
    vec4 s = A(U-vec2(0,1))*B(U-vec2(0,1)).w;
    vec4 w = A(U-vec2(1,0))*B(U-vec2(1,0)).w;
    Q = (A(U)+(n+e+s+w))/5.;
    float b = length(Q.xy);
    Q = sin(3.6+0.2*b+vec4(1,2,3,4));
    //Q = A(U).xyxy;
}

#define R iResolution.xy
#define A(U) texture(iChannel0,(U)/R)
#define B(U) texture(iChannel1,(U)/R)
#define C(U) texture(iChannel2,(U)/R)
#define Main void mainImage(out vec4 Q, in vec2 U)


float sdLine (vec2 p, vec2 a, vec2 b) {
    return length(p-a-(b-a)*clamp(dot(p-a,b-a)/dot(b-a,b-a),0.,1.));
}

Main {
    Q = A(U);
    float G = .2;
    vec4 dn=vec4(0),de=vec4(0),ds=vec4(0),dw=vec4(0);
    vec4 dQ = vec4(0);
    { // north, so s = Q
        vec2 u = vec2(0,1);
        vec4 n = A(U+u+vec2(0,1));
        vec4 e = A(U+u+vec2(1,0));
        vec4 s = Q;//A(U+u-vec2(0,1));
        vec4 w = A(U+u-vec2(1,0));
        float nb = B(U+u+vec2(0,1)).w;
        float eb = B(U+u+vec2(1,0)).w;
        float sb = B(U+u-vec2(0,1)).w;
        float wb = B(U+u-vec2(1,0)).w;
    // curl H = J
    // div uH = 0

        dn.x += -((e-w).y-(n).x-B(U+u).x)-Q.x;
        dn.y += +((e*eb-w*wb).x+(n*nb).y)/sb-Q.y;
        dn.y = dn.y/(1.+G*(abs(nb-sb)+abs(eb-sb)+abs(wb-sb)));;
    }
    { // south, so n = Q
        vec2 u = vec2(0,-1);
        vec4 n = Q;//A(U+u+vec2(0,1));
        vec4 e = A(U+u+vec2(1,0));
        vec4 s = A(U+u-vec2(0,1));
        vec4 w = A(U+u-vec2(1,0));
        float nb = B(U+u+vec2(0,1)).w;
        float eb = B(U+u+vec2(1,0)).w;
        float sb = B(U+u-vec2(0,1)).w;
        float wb = B(U+u-vec2(1,0)).w;

    // div B = 0
    // curl B = J
        ds.x = +((e-w).y-(-s).x-B(U+u).x)-Q.x ;
        ds.y = -((e*eb-w*wb).x+(-s*sb).y)/nb-Q.y;
        ds.y = ds.y/(1.+G*(abs(eb-nb)+abs(sb-nb)+abs(wb-nb)));
    }
    { // east, so w = Q
        vec2 u = vec2(1,0);
        vec4 n = A(U+u+vec2(0,1));
        vec4 e = A(U+u+vec2(1,0));
        vec4 s = A(U+u-vec2(0,1));
        vec4 w = Q;//A(U+u-vec2(1,0));
        float nb = B(U+u+vec2(0,1)).w;
        float eb = B(U+u+vec2(1,0)).w;
        float sb = B(U+u-vec2(0,1)).w;
        float wb = B(U+u-vec2(1,0)).w;
    // div B = 0
    // curl B = J
        de.y = ((e).y-(n-s).x-B(U+u).x)-Q.y ;
        de.x = ((e*eb).x+(n*nb-s*sb).y)/wb-Q.x;
        de.x = de.x/(1.+G*(abs(nb-wb)+abs(sb-wb)+abs(eb-wb)));
    }
    { // west, so e = Q
        vec2 u = vec2(-1,0);
        vec4 n = A(U+u+vec2(0,1));
        vec4 e = Q;//A(U+u+vec2(1,0));
        vec4 s = A(U+u-vec2(0,1));
        vec4 w = A(U+u-vec2(1,0));
        float nb = B(U+u+vec2(0,1)).w;
        float eb = B(U+u+vec2(1,0)).w;
        float sb = B(U+u-vec2(0,1)).w;
        float wb = B(U+u-vec2(1,0)).w;
    // div B = 0
    // curl B = J
        dw.y += -((-w).y-(n-s).x-B(U+u).x)-Q.y ;
        dw.x += -((-w*wb).x+(n*nb-s*sb).y)/eb-Q.x;
        dw.x = dw.x/(1.+G*(abs(nb-eb)+abs(sb-eb)+abs(wb-eb)));
    }

     Q += 0.25*(dn+de+ds+dw);

    if (iFrame < 1) Q = vec4(0);
}

