vec4 sampleAvg(int size, int resdiv, vec2 uv) {

    vec4 sum = vec4(0.);
    int iter = 0;

    for (int y = -size/2; y <= size/2; y += resdiv) {
        for (int x = -size/2; x <= size/2; x += resdiv) {
            sum += texture(iChannel0, uv + vec2(x, y)/iResolution.xy);
            iter++; // figures out how many things get added
        }
    }

    return sum / vec4(iter);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    //vec3 col = texture(iChannel0, uv).rgb;

    // Output to screen
    fragColor = sampleAvg(int(50. * (sin(3. * iTime) + 1.)), 5, uv);
}
