// Cole Peterson

// This is the boot screen of your brain.


#define R iResolution.xy
#define ss(a, b, t) smoothstep(a, b, t)


mat2 rot(float a) {return mat2(cos(a), -sin(a), sin(a), cos(a));}

float rbox( vec3 p, vec3 b, float r ){
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0) - r;
}

float box2d( in vec2 p, in vec2 b ){
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

float map(vec3 rp){
    float d = 999.;

    float h = 0.5 - texture(iChannel1, rp.xz*.8).x * .065;
    h += texture(iChannel1, rp.xz*2.3).x * .0088;

    d = rp.y + h;
    d = min(rp.y + .49, d);

    rp.y -= .5;
    vec3 b = vec3(3., 0., 2.);
    vec3 id = floor(rp / b);
    rp.y += cos(id.z*10. + id.x*33. + iTime)*.03;
    vec3 ruv = mod(rp, b) - b*0.5;

    d = min(rbox(ruv, vec3(.3, .3, .3), .01), d);

    return d;
}


vec3 normal( in vec3 pos ){
    vec2 e = vec2(0.002, -0.002);
    return normalize(
        e.xyy * map(pos + e.xyy) + 
        e.yyx * map(pos + e.yyx) + 
        e.yxy * map(pos + e.yxy) + 
        e.xxx * map(pos + e.xxx));
}


void mainImage( out vec4 f, in vec2 u ){
    vec2 uv = vec2(u - .5*R) / R.y;
    vec3 rd = normalize(vec3(uv, 0.72));
    vec3 rd2 = rd;
    vec3 ro = vec3(0., -0.3, 0.);

    vec2 m = (iMouse.xy - .5*R) / R.y;

    rd.zy *= rot(.15);

    if(iMouse.z > 0.){
        rd.zy *= rot(m.y*3.);
        rd.zx *= rot(m.x*3.);
    }

    rd.yz *= rot(-cos(iTime*3.)*.01);
    ro.y += cos(iTime*3.)*.004;
    ro.z += iTime*.1;

    float d = 0.0, t = 0.0, ns = 0.;

    for(int i = 0; i < 80; i++){
    	d = map(ro + rd*t); 
        if(d < 0.002 || t > 40.) break;
        t += d * .75;
        ns++;
    }

    vec3 p = ro + rd*t;

    vec3 n = normal(p);
    vec3 ref = reflect(n, rd);
    vec3 lp = ro + vec3(.6, 0.5, 2.4);
    vec3 ld = normalize(lp-p);

    float dif = max(dot(n, ld), .0);
    float spec = pow(max(dot( reflect(-ld, n), -rd), 0.), 28.);

    vec3 objCol = vec3(0);

    vec3 rock = vec3(.8, .7, .6) * .97 * max(texture(iChannel1, p.xz*1.4).x, .4);
    vec3 water = vec3(.7, .8, .9) * .8;
    vec3 sky = vec3(.9, .97, .99);
    float clouds = octnse(rd.xy, 28.38, 5) * .8;

    vec2 suv = mod(p.xz + vec2(0., 0.6), vec2(3., 2.)) - vec2(3., 2.)*.5;
    float sdw = ss(0., .1, box2d(suv, vec2(.5, .5)));

    rock *= sdw;

    water += texture(iChannel0, ref).xyz;
    water *= .8;
    water *= sdw;

    vec2 auv = p.xz;

    auv.x += cos(iTime + auv.y*32.)*.01;
    auv.y += sin(iTime + auv.x*32.)*.01;
    auv += iTime*.01;

    water *= max(texture(iChannel2, auv*0.98).x, .2);

    float nt = .08, nt2 = -.35;
    float blend = ss(-.4 - nt, -.405 - nt, p.y);
    objCol = mix(rock, water, blend);
    objCol = mix(vec3(1), objCol, ss(nt2 + .03, nt2, p.y));

    vec3 col = (objCol * dif) + vec3(.9, .8, .7)*spec*.4;

    col = mix(vec3(.99, .97, .9) * clouds, col,  exp(-t*t*t*.08));

    col.r += .015;

    col *= ss(.4301, .43, abs(uv.y));

    col *= min(iTime * 0.6, 1.);

    f = vec4(col, 1.0);
    //f = vec4(sqrt(clamp(col, .0, 1.)), 1.);
}

vec2 hash22( vec2 x ){
    const vec2 k = vec2( 0.3183099, 0.3678794 );
    x = x*k + k.yx;
    return -1.0 + 2.0*fract( 16.0 * k*fract( x.x*x.y*(x.x+x.y)) );
}


float gdns(vec2 p ){
    vec2 i = floor( p );
    vec2 f = fract( p );

	vec2 u = smoothstep(0., 1., f);

    vec2 a = hash22(i);
    vec2 b = hash22( i + vec2(1.,0.));
    vec2 c = hash22( i + vec2(.0,1.));
    vec2 d = hash22( i + vec2(1));

    //rotAll(a, b, c, d);

    float nse = mix( mix( dot( a, f - vec2(0.0,0.0) ), 
                     dot( b, f - vec2(1.0,0.0) ), u.x),
                mix( dot( c, f - vec2(0.0,1.0) ), 
                     dot( d, f - vec2(1) ), u.x), u.y);
    return nse + 0.5;
}

float octnse(vec2 p, float t, int oct){
    float a = 1.;
    float n = 0.;

    for(int i = 0; i < oct; i++){
        p.x+=t;
     	n += gdns(p) * a;	
        p*=2.;
        a *= .5;
    }

    return n;
}
