#define PI  3.14159265359
#define TAU 6.28318530717

//HELPER
float Clamp01(float value)
{
    return clamp(value,0.0,1.0);
}

///Inigo Quilez Signed Distance Field
//https://iquilezles.org/articles/distfunctions2d
///Playlist shadertoy SDF : 
//https://www.shadertoy.com/playlist/MXdSRf&from=36&num=12
float sdCircle( vec2 p, float r )
{
    return length(p) - r;
}
//2DShape
float Disk(vec2 uv, vec2 center, float radius, float blur) 
{
    float sdfCircle = length(uv-center)-radius;
	return Clamp01(sdfCircle * (10.0/blur));
}

///UV
vec2 CenterUV(vec2 fragCoord)
{
    ///Centering Operation
	vec2 uv = vec2( fragCoord.xy - 0.5*iResolution.xy );
	// [0, iResolution.x] -> [-0.5*iResolution.x, 0.5*iResolution.x]
	// [0, iResolution.y] -> [-0.5*iResolution.y, 0.5*iResolution.y]
    return uv;
}
vec2 AspectRatioUV(vec2 fragCoord)
{	
    vec2 uv = CenterUV(fragCoord.xy);
	uv = 2.0 * uv.xy / iResolution.y;
    // AspectRatio = 1920/1080 = 1.778
    // [-iResolution.x/2, iResolution.x/2] ->[-1.778, 1.778]
	// [-iResolution.y/2, iResolution.y/2] ->[-1, 1]
    return uv;
}

///COLOR
vec3 HSVcolor(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // UV manipulation
    vec2 uv = AspectRatioUV(fragCoord.xy);

    //Visu
    vec3 pix = vec3(uv,0);
    pix = vec3(0.1);
    for(int i = 0; i < 10; i++)
    {
        float t = (iTime * 1.0) + abs(sin(float(i)/128.0* TAU + iTime));
        vec2 pos = vec2(cos( float(i)/2.0 * TAU + t),sin(float(i)/2.0* TAU+t)) * sin(float(i)/2.0* TAU+t)
        + vec2(0.0,-.5);
        float circleMask = Disk(uv,pos, 0.1, 0.2);
        pix = mix(HSVcolor(vec3(float(i)/30.0, 0.95,0.95)), pix, circleMask); 
    }

	fragColor = vec4(pix, 1.0);
}
