void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 st = fragCoord/iResolution.xy;
    vec3 color=vec3(0.0);
    vec3 down_color = vec3(1.000,0.970,0.851);

    //red
	 vec3 red_color=vec3(1.000,0.010,0.010);
    float rbottom=smoothstep(-0.032,0.600,st.y);
    float rleft=smoothstep(0.,0.00,st.x);
    float rtop=1.0-smoothstep(0.99999,1.0,st.y);
    float rright=1.0-smoothstep(0.1999,0.20,st.x);
	 float red=rbottom*rleft*rtop*rright;
    vec3 red_zone=red_color*red;
   //yellow
	 vec3 yellow_color=vec3(1.000,0.869,0.125);
    float ybottom=smoothstep(0.59999,0.60,st.y);
    float yleft=smoothstep(0.028,0.90,st.x);
    float ytop=1.0-smoothstep(0.99,1.0,st.y);
    float yright=1.0-smoothstep(0.99999,1.0,st.x);
	 float yellow=ybottom*yleft*ytop*yright;
    vec3 yellow_zone=yellow_color*yellow;
    //blue
	 vec3 blue_color=vec3(0.105,0.310,0.685);
    float bbottom=smoothstep(0.0,0.000,st.y);
    float bleft=smoothstep(0.692,0.70,st.x);
    float btop=1.0-smoothstep(0.09999,0.636,st.y);
    float bright=1.0-smoothstep(0.99999,1.0,st.x);
	 float blue=bbottom*bleft*btop*bright;
    vec3 blue_zone=blue_color*blue;
    //line
    vec3 line_color=vec3(0.014,0.014,0.015);
    float lw=0.018;
    float vline1=0.072;


    vec2 v1_lb = step(vec2(vline1,0.6),vec2(st));
    vec2 v1_rt= vec2(1.)-step(vec2(vline1+lw,1),st);

    vec2 h1_lb = step(vec2(0.2,0.1),vec2(st));
    vec2 h1_rt= vec2(1.)-step(vec2(1,0.1+lw),st);

    vec2 h2_lb = step(vec2(0,0.8),vec2(st));
    vec2 h2_rt=vec2(1.)-step(vec2(1,0.8+lw),st);

    vec2 h3_lb = step(vec2(0,0.6-lw),vec2(st));
    vec2 h3_rt=vec2(1.)-step(vec2(1,0.6),st);

    float b=step(0.20,st.x)-step(0.20+lw,st.x)+step(0.70-lw,st.x)-step(0.70,st.x)+step(0.90-lw,st.x)-step(0.90,st.x);
    float line_zone=
        v1_lb.x*v1_lb.y*v1_rt.x*v1_rt.y
        +h1_lb.x*h1_lb.y*h1_rt.x*h1_rt.y
        +h2_lb.x*h2_lb.y*h2_rt.x*h2_rt.y
        +h3_lb.x*h3_lb.y*h3_rt.x*h3_rt.y
        +b;
	//mix color
	color=red_zone
        +yellow_zone
        +blue_zone




        ;
    color+=(1.-yellow-red-blue)*down_color;
    color*=(1.0-line_zone);

    //color=line_zone*vec3(1.000,0.830,0.454);
    fragColor = vec4(color,1.0);
}
