// Slide the mouse cursor over the image to compare the 
// results of the bloom effect with the original image.


// The size of the most significant blur kernel
// Note: blurring is done for free with mipmaps!
#define LEVELS 12
// The size of the least significant blur kernel
#define BLURSIZE 3

// Uncomment the line below to see how the bloom effect contributes to the image.
//#define ONLY_SHOW_BLOOM 1


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //float minDim = min(iResolution.x, iResolution.y);
    //vec2 fc2 = fragCoord - iResolution.xy/2.0;
	//vec2 uv = fc2 / vec2(minDim, minDim);
	//vec2 uv = fragCoord.xy / vec2(minDim, minDim);
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;


    //float power = iMouse.x/iResolution.x*3.0;
    //float threshold = iMouse.y/iResolution.y;
    //float mixAmount = 0.3;

    float power = 1.0;
    float threshold = 2.5;
    float mixAmount = 0.6;
    float subAmount = 0.1;


	vec4 col = vec4(0.0);
    float highP = 0.0;
    for(int i = LEVELS; i > BLURSIZE; i-=2){
        col = col + texture( iChannel0, uv, float(i) );
        highP = highP + 1.0;
        col = col * power;
        highP = highP * power; // TODO calculate highP at compile-time.
    }
    col = max(col-vec4(threshold), vec4(0.0)); 
    highP = highP - threshold;

    col = col / vec4(highP);

    #ifndef ONLY_SHOW_BLOOM
    col = col*vec4(mixAmount) + texture( iChannel0, uv) - vec4(subAmount);
    #endif

    //col = col * vec4(step(uv.x, iMouse.z/iResolution.x));

    if(uv.x<iMouse.x/iResolution.x){
        col = texture( iChannel0, uv);
    }
    //col = mix(texture( iChannel0, uv), col, mixAmount);


	fragColor = col;
}
