#define pi 3.14159

// https://www.laurivan.com/rgb-to-hsv-to-rgb-for-shaders/
vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Slow it down, geez.
    float slow = iTime * 0.1;

    vec4 shader = texture(iChannel0, uv);

    vec4 shader2 = texture(iChannel1, uv).brga;

    vec4 col = vec4(0);

    uv = uv * 2.0 - 1.0;

    uv = floor(uv * (cos(slow*0.1) + 16.0 + cos(slow) * 16.0));
    uv.x += sin(iTime);

    float t = atan(uv.y, uv.x);

    vec3 hsv = vec3(
        (shader * mod(sin(t * pi) + slow, pi)).r,
        (shader2 * mod(sin(t * pi) + 1.3478164 + 3.0*slow, pi)).r,
        1.0
    );

    vec3 rgb = hsv2rgb(hsv);

    // Output to screen
    fragColor = vec4(rgb.r,rgb.g,rgb.b,0);
}

vec2 rotate(vec2 uv, float th) {
    return mat2(cos(th), sin(th), -sin(th), cos(th)) * uv;
}

