// Color Grade Filter by Charles Fettinger for obs-shaderfilter plugin 4/2020
//https://github.com/Oncorporation/obs-shaderfilter
//OBS messed up the LUT system, this is basically the old LUT system





void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
     float lut_amount_percent = iTime*100.;
     float lut_scale_percent = 100.;
     float lut_offset_percent = 0.;

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float lut_amount = clamp(lut_amount_percent *.01, -2.0, 2.0);
	float lut_scale = clamp(lut_scale_percent *.01,-2.0, 2.0);
	float lut_offset = clamp(lut_offset_percent *.01,-2.0, 2.0);

	vec4 textureColor = texture(iChannel0, uv);
	const vec3 coefLuma = vec3(0.2126, 0.7152, 0.0722);
	float lumaLevel = dot(coefLuma, textureColor.rgb);
	float blueColor = lumaLevel;//textureColor.b * 63.0;

	vec2 quad1;
	quad1.y = floor(floor(blueColor) / 8.0);
	quad1.x = floor(blueColor) - (quad1.y * 8.0);

	vec2 quad2;
	quad2.y = floor(ceil(blueColor) / 8.0);
	quad2.x = ceil(blueColor) - (quad2.y * 8.0);

	vec2 texPos1;
	texPos1.x = (quad1.x * 0.125) + 0.5/512.0 + ((0.125 - 1.0/512.0) * textureColor.r);
	texPos1.y = (quad1.y * 0.125) + 0.5/512.0 + ((0.125 - 1.0/512.0) * textureColor.g);

	vec2 texPos2;
	texPos2.x = (quad2.x * 0.125) + 0.5/512.0 + ((0.125 - 1.0/512.0) * textureColor.r);
	texPos2.y = (quad2.y * 0.125) + 0.5/512.0 + ((0.125 - 1.0/512.0) * textureColor.g);

	vec4 newColor1 = texture(iChannel1, texPos1);
	newColor1.rgb = newColor1.rgb * lut_scale + lut_offset;
	vec4 newColor2 = texture(iChannel1, texPos2);
	newColor2.rgb = newColor2.rgb * lut_scale + lut_offset;
	vec4 luttedColor = mix(newColor1, newColor2, fract(blueColor));

	vec4 final_color = mix(textureColor, luttedColor, lut_amount);
	fragColor= vec4(final_color.rgb, textureColor.a);
}
