void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = fragCoord/iResolution.xy;
    float sl = uv.y*.25-uv.x + iTime*.1;
    float slc = 1./6.;
    float sl2 = fract(sl*5.);
    sl = fract(sl);
    vec3 g = vec3(0.15,0.63,0.2);
    vec3 col = textureLod(iChannel0,uv,0.).rgb;
    float a = 1.-dot(col-g,col-g)*5.;
    a = smoothstep(0.,1.,clamp(a,0.,1.));
    if(sl>slc){
        col = mix(col,vec3(0.),a);
    }
    if(sl>slc&&sl<1.75*slc){
        col = vec3(a);
    }
    if(abs(sl-slc)<0.02){
        col = g;
    }
    float a2 = (((col.r+col.b)*.5-col.g)+0.15)*5.;
    vec3 gr = vec3(col.r+col.g+col.b)/3.;
    a2 = smoothstep(0.,1.,a2);
    gr *= vec3(1.,0.85,0.55);
    if(sl>3.75*slc){
        col = mix(gr,col,a2)*pow(clamp(a2+.05,0.,1.),0.1);
    }
    if(sl>3.*slc&&sl<3.75*slc){
        col = vec3((((col.r+col.b)*.5-col.g)+0.15)*5.);
    }
    col*=smoothstep(0.,1.,abs(fract(sl-0.5)-slc*0.05-0.5)*150.-1.);
    float xt = fract(uv.x-iTime*.1);
    if(abs(xt-0.14)<0.008 && abs(uv.y-0.88)<0.08){
        col = vec3(1.,.6,.8);
    }
    if((abs(fract(xt+0.008))-0.008<0.008||abs(xt-0.97)<0.008) && abs(uv.y-0.88)<0.08){
        col = vec3(0.1,0.2,0.5);
    }
    if((abs(xt-0.86)<0.008||abs(xt-0.82)<0.008||abs(xt-0.78)<0.008) && abs(uv.y-0.88)<0.08){
        col = vec3(1.,.75,.4);
    }
    if(abs((-abs(xt-0.685)+(uv.y+0.01)*.2)-0.17)<0.008&&uv.y<.96||abs(xt-0.64)<0.008 && abs(uv.y-0.88)<0.08){
        col = vec3(.1,.5,.5);
    }
    if(abs((-abs(xt-0.4)+(uv.y+0.01)*.2)-0.17)<0.008&&uv.y<.96){
        col = vec3(0.7,1.,.7);
    }
    fragColor = vec4(col,1.0);
}
