// object transformation
vec3 rotate_x(vec3 p, float phi)
{   float c = cos(phi);	float s = sin(phi);
    return vec3(p.x, c*p.y - s*p.z, s*p.y + c*p.z);
}
vec3 rotate_y(vec3 p, float phi)
{	float c = cos(phi);	float s = sin(phi);
	return vec3(c*p.x + s*p.z, p.y, c*p.z - s*p.x);
}
vec3 rotate_z(vec3 p, float phi)
{	float c = cos(phi);	float s = sin(phi);
	return vec3(c*p.x - s*p.y, s*p.x + c*p.y, p.z);
}

vec2 fig002rot(vec3 p)
{  vec2 res= vec2(9999.0, -1.0);
    float num=6.0;
    float ang=360.0/num;
    vec3 p0= rotate_x(p, radians(90.0)); 
    p0= rotate_y(p0, radians(-iTime*10.0)); 

    for (float i=0.0; i<num;i++)
    {   vec3 p1= rotate_y(p0, radians(ang*i)); 
        float d1= sdCylinderYZ(p1-vec3(5.0,0.0,0.0), vec2(1.0,4.0) );
        vec3 p2=rotate_y(p1-vec3(-13.0,0.0,2.4), radians(-30.0));
        float d2= sdCylinderYZ( p2, vec2(1.0,5.0) );
        float uni1= opSmoothUnion(d1,d2, 2.5);
        res=opU2(res,vec2(uni1,100.0));

    }    
  return res;
}

vec2 figtrin(vec3 p)
{
  vec2 res= vec2(9999.0, -1.0);
  float d1= sdCylinderXZ(p-vec3(0.0,0.0,0.0), vec2(0.5,4.0) );
  float d2= sdCylinderXZ(p-vec3(2.0,-5.0,0.0),vec2(0.5,9.0) );
  float d3= sdCylinderXZ(p-vec3(4.0,0.0,0.0), vec2(0.5,4.0) );
  float d4= sdCylinderYZ(p-vec3(2.0,-3.0,0.0), vec2(0.5,3.0) );
  res=opU2(res,vec2(d1,2.0));
  res=opU2(res,vec2(d2,2.0));
  res=opU2(res,vec2(d3,2.0));
  res=opU2(res,vec2(d4,2.0));

  return res;
}


vec2 GetDist(vec3 p  ) 
{	vec2 res= vec2(9999.0, -1.0);vec3 p0=p;
    p.y=p.y-5.0;

    float pt=mod(iTime,7.0);
    float px1, px2;
    vec2 rf1,rf2;

    if(pt<3.5)
    {    
     px1=mod(iTime*10.0,70.);
     px2=0.0;
      rf1= fig002rot(p-vec3(px1,0.0,0.0));
      rf2= figtrin(p-vec3(-2.0-px2,0.0,5.0));
    }
    else
    {
     px1=100.0;
     px2=mod(iTime*10.,35.0);
     rf1= fig002rot(p-vec3(px1,0.0,0.0));
     rf2= figtrin(p-vec3(-2.0-px2,0.0,5.0));
    }
    res =opU2(res, rf1);
    res =opU2(res, rf2);
    return res;
}

vec3 GetNormal(vec3 p)
{   float d = GetDist(p).x;
    vec2 e = vec2(.001, 0);
    vec3 n = d - vec3(GetDist(p-e.xyy).x,GetDist(p-e.yxy).x,GetDist(p-e.yyx).x);
    return normalize(n);
}

vec2 RayMarch(vec3 ro, vec3 rd, int PMaxSteps)
{   float t = 0.1, tmax = 40.0;
    vec3 p;
    vec2 hit, object=vec2(0.1,0.0);
   for(int i=0; i <= PMaxSteps; i++) 
    { p = ro + rd*object.x;
      hit = GetDist(p);
      object.x += hit.x;
      object.y = hit.y;
      if (abs(hit.x) < EPSILON || object.x > MAX_DIST) break;
    }
    return object;
}


float getSoftShadow(vec3 p, vec3 lightPos) {
    float res = 1.0;
    float dist = 0.01;
    float lightSize = 0.03;
    for (int i = 0; i < MAX_STEPS; i++) {
      float hit = GetDist(p + lightPos * dist).x;
      res = min(res, hit / (dist * lightSize));
      dist += hit;
      if (hit < 0.0001 || dist > 60.0) break;
    }
    return clamp(res, 0.0, 1.0);
}

float occlusion(vec3 pos, vec3 nor)
{   float sca = 2.0, occ = 0.0;
    for(int i = 0; i < 10; i++) {    
      float hr = 0.01 + float(i) * 0.5 / 4.0;
      float dd = GetDist(nor * hr + pos).x;
      occ += (hr - dd)*sca;
      sca *= 0.6;
    }
    return clamp( 1.0 - occ, 0.0, 1.0 );    
}

vec3 lightingv3(vec3 normal,vec3 p, vec3 lp, vec3 rd, vec3 ro,vec3 lightColor, float t) 
{   vec3 lightPos=lp;
    vec3 worldPos = ro + rd * t;
    vec3 V = -rd;
    vec3 N = GetNormal(worldPos);
    vec3 L = normalize (lightPos - worldPos);
    vec3 R = reflect (-L, N);

    float lightDist = max(length(L), .001);
    float atten=1.0 / (1.0 + lightDist * 0.125 + lightDist * lightDist * .05);
    L /= (lightDist*atten);

    float shadow = getSoftShadow(worldPos, normalize(lightPos));// shadows
    float occ = occlusion(worldPos, N);// occ
    vec3 ambient= 0.5*lightColor*clamp(0.5 + 0.5*N.y, 0.0, 1.0)*N.y;
    vec3 fresnel =  lightColor *  pow(clamp(1.0 + dot(rd, N), 0.0, 1.0), 2.0);;
    float diff       = clamp(dot(N, L), 0.0, 1.0);

    diff = pow(diff, 4.) * 2.;
    vec3 diffuse =  lightColor * diff;
    float shininess=4.0;
    float specular    = pow(max(dot(R, V), 0.0), shininess);  
    vec3 back = 0.5 * lightColor * clamp(dot(N, -L), 0.0, 1.0);
    vec3 colOut = occ*lightColor*(ambient+diffuse*shadow+.35 +back) + vec3(.7,.9,1)*specular*specular;       
    return colOut;
}

