    "Ingenuity Mk.2 ?"   (NsXXRj)
    "Engine Sketches"    (sdt3R7)
    "Gear Projection"    (ssjGWy)
    "Mercedes Sketches"  (fl33zM)
*/

#define AA  0   // (= 0/1) optional antialiasing (and motion)

#if 1
#define VAR_ZERO min (iFrame, 0)
#else
#define VAR_ZERO 0
#endif

float PrBoxDf (vec3 p, vec3 b);
float PrRoundBoxDf (vec3 p, vec3 b, float r);
float PrBox2Df (vec2 p, vec2 b);
float PrRoundBox2Df (vec2 p, vec2 b, float r);
float PrCylDf (vec3 p, float r, float h);
float PrCapsDf (vec3 p, float r, float h);
float PrCaps2Df (vec2 p, float r, float h);
float PrRoundCylDf (vec3 p, float r, float rt, float h);
float SmoothMax (float a, float b, float r);
float SmoothBump (float lo, float hi, float w, float x);
float Minv3 (vec3 p);
float Maxv3 (vec3 p);
mat3 StdVuMat (float el, float az);
vec2 Rot2D (vec2 q, float a);
vec2 Rot2Cs (vec2 q, vec2 cs);
float Noisefv2 (vec2 p);

vec4 csWm, csWt;
vec3 qHit, pgSize, ltDir, ltDirS, flPos, dwf, dwr;
vec2 csFl;
float tCur, dstFar, angWm, drpWm, angWt, drpWt, szFac;
int idObj;
bool wlDown;
const int idFus = 1, idCkp = 2, idEng = 3, idWngM = 4, idWngT = 5, idFin = 6,
   idWhl = 7, idLeg = 8, idFr = 9;
const float pi = 3.1415927;

#define DMINQ(id) if (d < dMin) { dMin = d;  idObj = id;  qHit = q; }

float ObjDf (vec3 p)
{
  vec3 q;
  vec2 ww;
  float dMin, d, s, w, t, wg;
  dMin = dstFar / szFac;
  p /= szFac;
  p -= flPos;
  p.xz = Rot2Cs (p.xz, csFl);
  q = p;
  w = 1.;
  s = 0.;
  if (q.z < -1.5) {
    s = q.z + 1.5;
    s *= s;
    w *= 1. - 0.025 * s;
    s *= 0.026;
  } else if (q.z > 3.5) {
    s = q.z - 3.5;
    s *= s;
    w *= 1. - 0.05 * s;
    s *= -0.02;
  }
  d = PrCapsDf (q - vec3 (0., s, 0.), w, 7.);
  if (wlDown) d = max (d, - min (PrBoxDf (vec3 (abs (q.x) - 0.45, q.y + 1., q.z - 0.4),
     vec3 (0.2, 0.25, 1.6)), PrBoxDf (vec3 (abs (q.x) - 0.2, q.y + 1., q.z - 4.8),
     vec3 (0.1, 0.3, 0.2))));
  DMINQ (idFus);
  q = p;
  q.x = abs (q.x);
  q -= vec3 (0.65, 0.1, 6.);
  d = PrCapsDf (q, 0.02, 0.3);
  DMINQ (idFr);
  q = p;
  q.z -= -0.7;
  q.xz = abs (q.xz);
  q -= vec3 (0.2, 0.8, 4.);
  d = PrCapsDf (q.xzy, 0.07, 0.2);
  DMINQ (idFr);
  q = p;
  q.yz -= vec2 (0.4, 3.5);
  d = PrCapsDf (q, 0.6, 1.5);
  DMINQ (idCkp);
  ww = vec2 (4., 1.4);
  q = p;
  q.x = abs (q.x);
  q.yz -= vec2 (0.7, 1.3);
  q.xy = Rot2Cs (q.xy, csWm.xy);
  q.xz = Rot2Cs (q.xz, csWm.zw);
  t = ww.y * (1. - 0.3 * q.x / ww.x);
  s = 1.1 * (q.z - 0.2) / ww.y;
  d = SmoothMax (PrCaps2Df (q.yz, 0.15 * (t - s * s), t), abs (abs (p.x) - ww.x) - ww.x, 0.06);
  d = SmoothMax (d, - abs (PrBox2Df (vec2 (abs (p.x) - 6.9, q.z - 0.085 * q.x + 1.2),
     vec2 (0.85, 0.2))), 0.03);
  DMINQ (idWngM);
  for (float k = 0.; k < 4.; k ++) {
    q = p;
    q.x = abs (q.x);
    q -= (k + 1.1) * dwr + vec3 (0., 0.65, 0.1);
    d = PrCapsDf (q, 0.05, 0.25);
    DMINQ (idFr);
  }
  for (float k = 0.; k < 3.; k ++) {
    q = p;
    q.x = abs (q.x);
    q -= (k + 1.3) * dwf + vec3 (0., 0.22, 2.8);
    d = max (PrRoundCylDf (q.yzx - vec3 (0.25, -0.1, 0.), 0.25, 0.05, 0.), 0.3 - q.y);
    DMINQ (idFr);
    d = min (SmoothMax (abs (PrCapsDf (q, 0.3 * (1. - 0.5 * q.z * q.z), 0.9)) - 0.02,
       abs (q.z + 0.1) - 0.55, 0.02), PrCylDf (q, 0.28, 0.25));
    s = q.z + 0.2;
    d = max (min (d, PrCapsDf (q + vec3 (0., 0., 0.2), 0.25 * (1. - 2. * s * s), 0.7)),
       - PrCylDf (vec3 (q.xy, q.z + 0.8), 0.07, 0.2));
    DMINQ (idEng);
  }
  ww = vec2 (1.3, 0.85);
  q = p;
  q.x = abs (q.x);
  q.yz -= vec2 (0.8, -5.8);
  q.xy = Rot2Cs (q.xy, csWt.xy);
  q.xz = Rot2Cs (q.xz, csWt.zw);
  d = min (ww.x - abs (abs (p.x) - ww.x), abs (PrBox2Df (vec2 (abs (p.x) - 1.7,
     q.z - 0.07 * q.x + 0.6), vec2 (0.7, 0.25))));
  t = ww.y * (1. - 0.25 * q.x / ww.x);
  s = (q.z - 0.1) / ww.y;
  d = SmoothMax (PrCaps2Df (q.yz, 0.12 * (t - s * s), t), - d, 0.03);
  DMINQ (idWngT);
  ww = vec2 (1.5, 0.65);
  q = p;
  q.x = abs (q.x);
  q.xy = Rot2Cs (q.xy, sin (drpWt + vec2 (0.5 * pi, 0.)));
  q -= vec3 (2.7, 0.8, -6.9);
  t = ww.y * (1. - 0.25 * abs (q.y) / ww.x);
  s = (q.z + 0.1 * abs (q.y) / ww.x - 0.2) / ww.y;
  d = SmoothMax (PrCaps2Df (vec2 (q.x, q.z + 0.1 * abs (q.y) / ww.x), 0.1 * (t - s * s), t),
     abs (q.y - 0.35 * ww.x) - 0.65 * ww.x, 0.06);
  d = SmoothMax (d, - abs (PrBox2Df (vec2 (q.y - 0.8, q.z + 0.3), vec2 (0.5, 0.2))), 0.03);
  DMINQ (idFin);
  q.z -= -0.3;
  d = PrCapsDf (q, 0.05, 0.2);
  DMINQ (idFr);
  if (wlDown) {
    q = p;
    q.yz -= vec2 (-1., 0.4);
    t = 0.45;
    s = abs (q.z) - 3.5 * t;
    q.z = mod (q.z + 0.5 * t, t) - 0.5 * t;
    q.x = abs (q.x) - 0.5;
    d = max (PrRoundCylDf (vec3 (q.yz, abs (q.x) - 0.09), 0.16, 0.03, 0.02), s);
    DMINQ (idWhl);
    d = max (min (PrCylDf (q.yzx, 0.05, 0.09), PrCylDf (vec3 (q.xz, q.y - 0.2), 0.03, 0.2)), s);
    DMINQ (idLeg);
    q = p;
    q.x = abs (q.x);
    q -= vec3 (0.2, -1.06, 4.8);
    d = PrRoundCylDf (vec3 (q.yz, abs (q.x) - 0.07), 0.1, 0.03, 0.01);
    DMINQ (idWhl);
    d = min (PrCylDf (q.yzx, 0.05, 0.07), PrCylDf (vec3 (q.xz, q.y - 0.2), 0.03, 0.2));
    DMINQ (idLeg);
  }
  return 0.6 * szFac * dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 160; j ++) {
    d = ObjDf (ro + dHit * rd);
    if (d < 0.001 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float ObjSShadow (vec3 ro, vec3 rd)
{
  float sh, d, h;
  sh = 1.;
  d = 0.01;
  for (int j = VAR_ZERO; j < 40; j ++) {
    h = ObjDf (ro + d * rd);
    sh = min (sh, smoothstep (0., 0.1 * d, h));
    d += h;
    if (sh < 0.05 || d > dstFar) break;
  }
  return 0.5 + 0.5 * sh;
}

float ExObjDf (vec3 p)
{
  vec3 q;
  float dMin, d;
  dMin = dstFar;
  q = p;
  q -= vec3 (14.5, 0.35, 8.);
  d = PrRoundCylDf (q, 0.28, 0.04, 4.);
  d = max (d, - dot (vec2 (q.z, dot (q.xy, cos (2. * pi * floor (16. * (atan (q.y, - q.x) / (2. * pi) +
     0.5) + 0.5) / 16. + vec2 (0., 0.5 * pi)))), sin (-0.4 * pi + vec2 (0.5 * pi, 0.))) - 1.27);
  DMINQ (1);
  q = p;
  q.yz -= vec2 (-0.1, 4.);
  d = max (PrRoundBoxDf (q, vec3 (pgSize + vec3 (1., 0.1, 1.)), 0.03), - PrBox2Df (q.xz, pgSize.xz));
  DMINQ (2);
  return dMin;
}

float ExObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 80; j ++) {
    d = ExObjDf (ro + dHit * rd);
    if (d < 0.001 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 ExObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ExObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float ExObjSShadow (vec3 ro, vec3 rd)
{
  float sh, d, h;
  sh = 1.;
  d = 0.01;
  for (int j = VAR_ZERO; j < 30; j ++) {
    h = ExObjDf (ro + d * rd);
    sh = min (sh, smoothstep (0., 0.1 * d, h));
    d += h;
    if (sh < 0.05 || d > dstFar) break;
  }
  return 0.5 + 0.5 * sh;
}

vec3 ShGrid (vec2 p, vec2 g, int stag)
{
  vec2 q, sq, ss;
  q = p * g;
  if (stag > 0 && 2. * floor (0.5 * floor (q.y)) != floor (q.y)) q.x += 0.5;
  sq = smoothstep (0.05, 0.1, abs (fract (q + 0.5) - 0.5));
  q = fract (q) - 0.5;
  ss = 0.3 * smoothstep (0.3, 0.5, abs (q.xy)) * sign (q.xy);
  if (abs (q.x) < abs (q.y)) ss.x = 0.;
  else ss.y = 0.;
  return vec3 (ss.xy, 1. - 0.1 * sq.x * sq.y).xzy;
}

vec2 ExBlkHit (vec3 ro, vec3 rd)
{
  vec3 v, tm, tp, u, fcBlk;
  vec2 qBlk;
  float dn, df, bSize;
  bSize = dstFar;
  if (rd.x == 0.) rd.x = 0.001;
  v = ro / rd;
  tp = bSize / abs (rd) - v;
  tm = - tp - 2. * v;
  dn = Maxv3 (tm);
  df = Minv3 (tp);
  if (df > 0. && dn < df) {
    fcBlk = - sign (rd) * step (tm.zxy, tm) * step (tm.yzx, tm);
    u = (v + dn) * rd;
    qBlk = vec2 (dot (u.zxy, fcBlk), dot (u.yzx, fcBlk)) / bSize;
  } else qBlk = vec2 (0.);
  return qBlk;
}

vec4 BlkHit (vec3 ro, vec3 rd, vec3 bSize)
{
  vec3 v, tm, tp, fcBlk;
  vec2 qBlk;
  float dMin, dn, df;
  dMin = dstFar;
  if (rd.x == 0.) rd.x = 0.001;
  v = ro / rd;
  tp = bSize / abs (rd) - v;
  tm = - tp - 2. * v;
  dn = Maxv3 (tm);
  df = Minv3 (tp);
  if (df > 0. && dn < df) {
    dMin = dn;
    fcBlk = - sign (rd) * step (tm.zxy, tm) * step (tm.yzx, tm);
  }
  return vec4 (dMin, fcBlk);
}

vec4 ObjCol ()
{
  vec4 col4, colY4, colB4;
  vec2 s;
  float t;
  col4 = vec4 (1., 1., 0.95, 0.2);
  colY4 = vec4 (0.8, 0.8, 0., 0.2);
  colB4 = vec4 (0.2, 0.2, 1., 0.2);
  if (idObj == idFus) {
    col4 = mix (colB4, col4, smoothstep (0.07, 0.09, abs (qHit.y + 0.6)));
    col4 = mix (colY4, col4, smoothstep (0.07, 0.09, abs (qHit.y + 0.35)));
    if (PrCylDf (qHit - vec3 (0., 0., 0.4), 0.98, 1.7) < 0. ||
       abs (qHit.x) < 0.4 && qHit.y < 0. && PrCylDf (qHit - vec3 (0., 0., 4.7), 0.93, 0.3) < 0.)
       col4 *= vec4 (vec3 (0.2), 0.);
    if (! wlDown && qHit.y < 0.) col4 *= 0.7 + 0.3 * smoothstep (0.008, 0.012, 
       min (abs (PrBox2Df (vec2 (abs (abs (qHit.x) - 0.45) - 0.1, qHit.z - 0.4), vec2 (0.1, 1.6))),
       abs (PrBox2Df (vec2 (abs (abs (qHit.x) - 0.2) - 0.05, qHit.z - 4.8), vec2 (0.05, 0.2)))));
    col4 *= 0.7 + 0.3 * smoothstep (0.008, 0.01, abs (PrRoundBox2Df (qHit.yz - vec2 (-0.4, 3.5),
       vec2 (0.3, 0.15), 0.05)));
  } else if (idObj == idCkp) {
    if (qHit.z > 0.4 && abs (qHit.y - 0.45) < 0.07 && abs (abs (qHit.x) - 0.2) > 0.03 &&
       abs (abs (qHit.z - 1.35) - 0.2) > 0.03) col4 = vec4 (0.5, 0.5, 0.5, -1.);
  } else if (idObj == idEng) {
    t = length (qHit.xy);
    if (PrCapsDf (qHit, 0.3 * (1. - 0.5 * qHit.z * qHit.z), 0.85) < 0.) {
      if (qHit.z > 0. && t < 0.14) col4 = vec4 (1., 0., 0., 0.2);
      else col4 *= vec4 (vec3 (0.3), 0.);
    } else {
      if (abs (qHit.z + 0.1) > 0.5) col4 = colY4;
      else col4 = mix (colB4, col4, smoothstep (0.02, 0.04, abs (qHit.z - 0.3)));
    }
  } else if (idObj == idWngM) {
    t = qHit.z + 0.1 * abs (qHit.x);
    col4 = mix (colB4, col4, smoothstep (0.03, 0.05, abs (t - 1.2)));
    col4 = mix (colY4, col4, smoothstep (0.03, 0.05, abs (t - 1.)));
    if (qHit.y < 0.) {
      if (abs (abs (qHit.x) - 3.9) < 2.1) col4 *= 0.8 + 0.2 *
         smoothstep (0.008, 0.012, abs (qHit.z - 0.1 * abs (qHit.x) + 1.));
    } else {
      s = Rot2D (qHit.xz - vec2 (3.5, -0.3), -0.1 * pi);
      s.x = abs (abs (s.x) - 0.8) - 0.3;
      col4 *= 0.8 + 0.2 * smoothstep (0.008, 0.012, abs (PrBox2Df (s, vec2 (0.3, 0.15))));
    }
  } else if (idObj == idWngT) {
    t = qHit.z + 0.15 * abs (qHit.x);
    col4 = mix (colB4, col4, smoothstep (0.02, 0.04, abs (t - 0.7)));
    col4 = mix (colY4, col4, smoothstep (0.02, 0.04, abs (t - 0.6)));
  } else if (idObj == idFin) {
    t = qHit.z + 0.15 * abs (qHit.y);
    col4 = mix (colB4, col4, smoothstep (0.02, 0.04, abs (t - 0.4)));
    col4 = mix (colY4, col4, smoothstep (0.02, 0.04, abs (t - 0.3)));
  } else if (idObj == idWhl) {
    col4 = mix (vec4 (0.7, 0.7, 0.75, 0.1), vec4 (0.3, 0.3, 0.3, 0.),
       step (0.07, length (qHit.yz)));
  } else if (idObj == idLeg) {
     col4 = vec4 (0.8, 0.8, 0.85, 0.1);
  }
  return col4;
}

void FlyerParms ()
{
  angWm = 0.1 * pi;
  drpWm = 0.02 * pi;
  csWm = vec4 (sin (drpWm + vec2 (0.5 * pi, 0.)), sin (angWm + vec2 (0.5 * pi, 0.)));
  angWt = 0.12 * pi;
  drpWt = -0.03 * pi;
  csWt = vec4 (sin (drpWt + vec2 (0.5 * pi, 0.)), sin (angWt + vec2 (0.5 * pi, 0.)));
  dwr = vec3 (1.4, 0., 0.);
  dwr.xy = Rot2Cs (dwr.xy, sin (- drpWm + vec2 (0.5 * pi, 0.)));
  dwr.xz = Rot2Cs (dwr.xz, sin (-0.7 * angWm + vec2 (0.5 * pi, 0.)));
  dwf = vec3 (1.5, 0., 0.);
  dwf.xy = Rot2Cs (dwf.xy, sin (-0.9 * drpWm + vec2 (0.5 * pi, 0.)));
  dwf.xz = Rot2Cs (dwf.xz, sin (-1.3 * angWm + vec2 (0.5 * pi, 0.)));
  wlDown = true;
}

vec3 SkShowScene (vec3 ro, vec3 rd, vec2 uv)
{
  vec4 col4;
  vec3 col, vn;
  float dstObj;
  dstObj = ObjRay (ro, rd);
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
    col4 = ObjCol ();
    col = vec3 (0.95) * Maxv3 (col4.rgb);
    col *= (0.95 + 0.05 * Noisefv2 (Rot2D (uv, 0.2 * pi) * vec2 (8., 128.))) *
       (0.5 + 0.5 * max (dot (vn, ltDirS), 0.));
  } else {
    col = vec3 (1.);
  }
  return clamp (col, 0., 1.);
}

vec3 SkMain (vec2 fCoord, float vp)
{
  mat3 vuMat;
  vec3 ro, rd, col, flPosT;
  vec2 uv, csFlT;
  float el, az, szFacT;
  szFacT = szFac;
  uv = fCoord / vec2 (5., 5.5);
  flPosT = flPos;
  flPos = vec3 (0.);
  csFlT = csFl;
  csFl = sin (0. + vec2 (0.5 * pi, 0.));
  if (vp == 0.) {
    szFac = 0.75;
    uv -= vec2 (0., -1.);
    el = -0.11 * pi;
    az = pi;
  } else if (vp == 1.) {
    szFac = 0.6;
    uv -= vec2 (0.25, -1.1);
    el = 0.42 * pi;
    az = 0.25 * pi;
  } else if (vp == 2.) {
    szFac = 0.75;
    uv -= vec2 (-0.1, -1.3);
    el = -0.1 * pi;
    az = 0.5 * pi;
  } else if (vp == 3.) {
    szFac = 0.6;
    uv -= vec2 (0.25, -1.05);
    el = -0.5 * pi;
    az = 0.25 * pi;
  }
  vuMat = StdVuMat (el, az);
  ro = vuMat * vec3 (0., -5., -20.);
  rd = vuMat * normalize (vec3 (uv, 4.2));
  ltDirS = vuMat * normalize (vec3 (0.5, 1., -0.5));
  col = SkShowScene (ro, rd, uv);
  szFac = szFacT;
  flPos = flPosT;
  csFl = csFlT;
  return clamp (col, 0., 1.);
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4, db4;
  vec3 col, vn, qHitEx;
  vec2 qBlk, q, sq;
  float dstObj, dstExObj, sh, r, z, a;
  int idObjEx;
  bool isBg;
  FlyerParms ();
  szFac = 1.3;
  a = 1.3 * pi;
  flPos = vec3 (1.1, 1.2, 1.);
#if AA
  flPos.xz += 3. * SmoothBump (0.25, 0.75, 0.25, mod (0.04 * tCur, 1.)) *
     Rot2D (vec2 (0., 1.), - a);
#endif
  csFl = sin (a + vec2 (0.5 * pi, 0.));
  pgSize = vec3 (15., 0.1, 10.);
  isBg = false;
  dstExObj = ExObjRay (ro, rd);
  idObjEx = idObj;
  qHitEx = qHit;
  dstObj = ObjRay (ro, rd);
  db4 = BlkHit (ro - vec3 (0., -0.1, 4.), rd, pgSize);
  if (min (dstObj, dstExObj) < min (db4.x, dstFar)) {
    if (dstObj < dstExObj) {
      ro += dstObj * rd;
      vn = ObjNf (ro);
      qBlk = mod (2. * ExBlkHit (ro, reflect (rd, vn)), 1.);
      col4 = ObjCol ();
    } else {
      ro += dstExObj * rd;
      vn = ExObjNf (ro);
      if (idObjEx == 1) {
        r = length (qHit.xy);
        col4 = mix (vec4 (0.9, 0.7, 0.1, 0.1), vec4 (1., 0.9, 0.1, 0.01), step (0.34, r));
        col4 = mix (vec4 (0.3, 0.3, 0.3, 0.1), col4, step (0.18, r));
        col4 = mix (vec4 (0.8, 0.4, 0.1, 0.), col4, step (qHit.z, 3.5));
        z = abs (qHit.z - 3.2);
        col4 = mix (vec4 (0.9, 0.9, 1., 0.3), col4, step (0.3, z));
        col4 = mix (vec4 (0.5, 0.5, 0.5, 0.3), col4, step (0.03, abs (z - 0.27)));
      } else if (idObjEx == 2) {
        col4 = vec4 (0.5, 0.3, 0.2, 0.) * (0.9 + 0.1 * Noisefv2 (32. * qHit.xz));
      }
    }
  } else if (db4.x < dstFar) {
    ro += db4.x * rd;
    vn = db4.yzw;
    q = ro.xz - vec2 (0., 4.);
    if (vn.y > 0.5 && PrBox2Df (q, pgSize.xz - 0.96) < 0.) {
      sq = sign (q);
      col = SkMain (q - 0.5 * sq * pgSize.xz, sq.y + 1. + 0.5 * (sq.x + 1.));
      if (Minv3 (col) > 0.95) {
        q = smoothstep (0.02, 0.05, abs (mod (q + 0.5, 1.) - 0.5));
        col = mix (vec3 (0.6, 0.9, 0.8), vec3 (0.97, 0.97, 1.), min (q.x, q.y));
      }
    } else {
      col = vec3 (0.97, 0.97, 1.) * (0.8 + 0.2 * step (0.5, vn.y));
      sh = 1.;
    }
  } else {
    isBg = true;
  }
  if (! isBg) {
    sh = min (ObjSShadow (ro + 0.01 * vn, ltDir), ExObjSShadow (ro + 0.01 * vn, ltDir));
    if (min (dstObj, dstExObj) < min (db4.x, dstFar)) {
      if (col4.a < 0.) col4.a = 0.2;
      col = col4.rgb * (0.2 + 0.8 * sh * max (dot (vn, ltDir), 0.)) +
         col4.a * step (0.95, sh) * pow (max (0., dot (ltDir, reflect (rd, vn))), 32.);
      col += col4.a * vec3 (0.2) * (0.5 + 0.5 * SmoothBump (0.25, 0.75, 0.05, qBlk.x) *
         SmoothBump (0.25, 0.75, 0.05, qBlk.y));
    } else {
      col *= 0.3 + 0.7 * sh * max (dot (vn, ltDir), 0.);
    }
  } else {
    qBlk = mod (8. * ExBlkHit (ro, rd), 1.);
    col = vec3 (0.5, 0.4, 0.4) * (0.05 + 0.245 * (rd.y + 1.) * (rd.y + 1.)) +
       vec3 (0.2) * (0.9 + 0.1 * SmoothBump (0.25, 0.75, 0.1, qBlk.x) *
       SmoothBump (0.25, 0.75, 0.1, qBlk.y));
  }
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col;
  vec2 canvas, uv;
  float el, az, sr;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  az = 0.;
  el = -0.25 * pi;
  if (mPtr.z > 0.) {
    az += 2. * pi * mPtr.x;
    el += 0.8 * pi * mPtr.y;
  }
  el = clamp (el, -0.5 * pi, -0.05 * pi);
  vuMat = StdVuMat (el, az);
  ro = vuMat * vec3 (0., 1.5, -40.);
  dstFar = 100.;
  ltDir = vuMat * normalize (vec3 (0.7, 1., -0.7));
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = float (VAR_ZERO); a < naa; a ++) {
    rd = vuMat * normalize (vec3 (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
       sr * (0.667 * a + 0.5) * pi), 4.7));
    col += (1. / naa) * ShowScene (ro, rd);
  }
  fragColor = vec4 (col, 1.);
}

float PrBoxDf (vec3 p, vec3 b)
{
  vec3 d;
  d = abs (p) - b;
  return min (max (d.x, max (d.y, d.z)), 0.) + length (max (d, 0.));
}

float PrRoundBoxDf (vec3 p, vec3 b, float r)
{
  return length (max (abs (p) - b, 0.)) - r;
}

float PrBox2Df (vec2 p, vec2 b)
{
  vec2 d;
  d = abs (p) - b;
  return min (max (d.x, d.y), 0.) + length (max (d, 0.));
}

float PrRoundBox2Df (vec2 p, vec2 b, float r)
{
  return length (max (abs (p) - b, 0.)) - r;
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrCapsDf (vec3 p, float r, float h)
{
  return length (p - vec3 (0., 0., clamp (p.z, - h, h))) - r;
}

float PrCaps2Df (vec2 p, float r, float h)
{
  return length (p - vec2 (0., clamp (p.y, - h, h))) - r;
}

float PrRoundCylDf (vec3 p, float r, float rt, float h)
{
  return length (max (vec2 (length (p.xy) - r, abs (p.z) - h), 0.)) - rt;
}

float Minv3 (vec3 p)
{
  return min (p.x, min (p.y, p.z));
}

float Maxv3 (vec3 p)
{
  return max (p.x, max (p.y, p.z));
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b - h * r, a, h);
}

float SmoothMax (float a, float b, float r)
{
  return - SmoothMin (- a, - b, r);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

vec2 Rot2Cs (vec2 q, vec2 cs)
{
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

const float cHashM = 43758.54;

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (dot (p, cHashVA2) + vec2 (0., cHashVA2.x)) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}
