#define time iTime*0.6
mat2 r2d(float a) {
    return mat2(cos(a),sin(a),-sin(a),cos(a));
}

//from iqs sdf functions page
vec3 opRepLim( in vec3 p, in float c, in vec3 l)
{
    vec3 q = p-c*clamp(round(p/c),-l,l);
    return q;
    //return primitive( q );
}
// All components are in the range [0…1], including hue.
vec3 rgb2hsv(vec3 c)
{
    vec4 K = vec4(0.0, -1.0 / 3.0, 2.0 / 3.0, -1.0);
    vec4 p = mix(vec4(c.bg, K.wz), vec4(c.gb, K.xy), step(c.b, c.g));
    vec4 q = mix(vec4(p.xyw, c.r), vec4(c.r, p.yzx), step(p.x, c.r));

    float d = q.x - min(q.w, q.y);
    float e = 1.0e-10;
    return vec3(abs(q.z + (q.w - q.y) / (6.0 * d + e)), d / (q.x + e), q.x);
}


// All components are in the range [0…1], including hue.
vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}
//Taken from http://lolengine.net/blog/2013/07/27/rgb-to-hsv-in-glsl.

vec4 map(vec3 p) {
    float pd = 1.;
    //p.z += time*2.1;
    vec3 o = p;
    float c = length(p);
    //p.xy *= r2d(sin(time*1.)+time);
    //p.xz *= r2d(sin(time*1.+c*13.));
    p.xy *= r2d(sin(c*50.+time)*0.2);
    //p = (fract(p*pd)-0.5)/pd;
    //float ramnt = clamp((sin(time*1.)),0.,1.);
    float rt = time*0.5;
    float ramnt = sin((3.14159/2.)*cos(rt)+rt);
    ramnt -= 1.;
    p.xz *= r2d(sin(c*0.5+time*0.3)*4.*ramnt);
    p.yz *= r2d(cos(c*0.5+time*0.3)*4.*ramnt);
    p.xy = vec2(length(p.xy),atan(p.x,p.y));
    //p.y = abs(p.y);
    //p.y *= 8./3.14159;
    p.y *= 1./3.1415;
    p.y *= (sin(time*0.13-2.)*0.5+0.5)*10.;
    p.y *= 0.5;
    p.y += p.z*sin(time);
    p.y = (fract(p.y)-0.5);
    p.y = abs(p.y);
    p.xy = vec2(p.x*sin(p.y),p.x*cos(p.y));
    p = opRepLim(p,0.21,vec3(1.));
    p.xz *= r2d(sin(time*1.+c*13.));
    p = opRepLim(p,0.018,vec3(2.));
    p.xy *= r2d(sin(time*1.)+time);
    p = opRepLim(p,mix(0.01,0.003,(sin(time*0.35+15.)*0.5+0.5)),vec3((0.5-c)*10.));

    float d = length(p)+0.005*sin(time*0.1+c*5.);
    return vec4(p,d);
}



vec2 RM(vec3 ro, vec3 rd) {
    float dO = 0.;
    float ii = 0.;

    for (int i=0;i<200;i++) {
        vec3 p = ro+rd*dO;
        float dS = map(p).w;
        dO += dS*0.5;
        ii += 0.1;
        if (dS < 0.001 || dO > 1000.) {break;}
    }
    return vec2(dO,ii);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = -1. + 2.* fragCoord/iResolution.xy;

    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    //vec2 uv = -1. + 2. * inData.v_texcoord;
    vec2 R = iResolution.xy;
    float ar = R.x/R.y;
    uv.x *= ar;
    vec3 col = vec3(0.);
    float c = length(uv*0.9);
    //uv *= r2d(c*4.+time);
    //uv *= r2d(uv.y*time*.1-time*4.3);
    vec3 ro = vec3(0.,0.,-0.5);
    //ro.z += time*0.2;
    vec3 rd = normalize(vec3(uv,1.));
    vec2 d = RM(ro,rd);

    //col = sin(uv.xyy*39.);
    col = vec3((d.y*2.15)-0.3);
    col = sin(d.yyy*0.1);
    //col -= d.y*0.3;
    vec3 hsv = vec3(
    sin(sin(d.x)*40.)*0.3+time*0.1,
    sin(d.x*300.)*0.5+0.5,
    (d.y*0.1)
    );
    if (d.x > 100.) {
        hsv.y *= 0.2;
    }
    col = hsv2rgb(hsv);

    fragColor = vec4(col,1.);
}
