//----------image
//por jorge2017a2-
#define MAX_STEPS 100
#define MAX_DIST 100.
#define MIN_DIST 0.001
#define EPSILON 0.001
//Pyramid Spin--6-abril-2022
#define Ka 0.5
#define Kd 0.4
vec3 GetColorYMaterial(vec3 p,  vec3 n, vec3 ro,  vec3 rd, int id_color, float id_material);
vec3 getMaterial( vec3 pp, float id_material);
vec3 light_pos1;  vec3 light_color1 ;
vec3 light_pos2;  vec3 light_color2 ;

//operacion de Union  por FabriceNeyret2
#define opU2(d1, d2) ( d1.x < d2.x ? d1 : d2 )

float sdBox( vec3 p, vec3 b )
	{ vec3 d = abs(p) - b;   return length(max(d,0.0))+ min(max(d.x,max(d.y,d.z)),0.0); }
float Intersect(float distA, float distB)
	{ return max(distA, distB);}
float Union(float distA, float distB)
	{ return min(distA, distB);}
float Difference(float distA, float distB)
	{ return max(distA, -distB);}

float opRep1D( float p, float c )
	{ float q = mod(p+0.5*c,c)-0.5*c; return  q ;}

vec3 rotate_x(vec3 p, float phi)
{   float c = cos(phi);	float s = sin(phi);
    return vec3(p.x, c*p.y - s*p.z, s*p.y + c*p.z);
}
vec3 rotate_y(vec3 p, float phi)
{	float c = cos(phi);	float s = sin(phi);
	return vec3(c*p.x + s*p.z, p.y, c*p.z - s*p.x);
}
vec3 rotate_z(vec3 p, float phi)
{	float c = cos(phi);	float s = sin(phi);
	return vec3(c*p.x - s*p.y, s*p.x + c*p.y, p.z);
}
vec2 rotatev2(vec2 p, float ang)
{   float c = cos(ang); float s = sin(ang);
    return vec2(p.x*c - p.y*s, p.x*s + p.y*c);
}


float sdTriangleIsosceles( in vec2 p, in vec2 q )
{   p.x = abs(p.x);
    vec2 a = p - q*clamp( dot(p,q)/dot(q,q), 0.0, 1.0 );
    vec2 b = p - q*vec2( clamp( p.x/q.x, 0.0, 1.0 ), 1.0 );
    float s = -sign( q.y );
    vec2 d = min( vec2( dot(a,a), s*(p.x*q.y-p.y*q.x) ),
                  vec2( dot(b,b), s*(p.y-q.y)  ));
    return -sqrt(d.x)*sign(d.y);
}

vec2 CubosBx(vec3 p)
{   vec2 res= vec2(9999.0, -1.0); 
    vec3 p0=p;
    float py=2.0;
    p= rotate_z(p-vec3(-8.5,py,0.0),radians(45.0));
    float d3a= sdBox( p-vec3(0.0,0.0,0.0),   vec3(5.0,0.5,5.0) );
    p=p0;
    p= rotate_z(p-vec3(8.5,py,0.0),radians(-45.0));
    float d3b= sdBox( p-vec3(0.0,0.0,0.0),   vec3(5.0,0.5,5.0) );
    p=p0;
    p= rotate_x(p-vec3(8.5,py,0.0),radians(-45.0));
    float d3c= sdBox( p-vec3(-8.0,-6.0,-6.0),vec3(5.0,0.5,5.0) );
    p=p0;
    p= rotate_x(p-vec3(8.5,py,0.0),radians(45.0));
    float d3d= sdBox( p-vec3(-8.0,-6.0, 6.0),vec3(5.0,0.5,5.0) );

    res =opU2(res, vec2(d3a,100.0));
    res =opU2(res, vec2(d3b,100.0));
    res =opU2(res, vec2(d3c,100.0));
    res =opU2(res, vec2(d3d,100.0));
    return res;
}

vec2 PiramideBase(vec3 p)
{   vec2 res= vec2(9999.0, -1.0); 
    float d1a=sdTriangleIsosceles( vec2(p.x,-p.y)-vec2(0.0,-26.0), vec2(5.0,30));
    float d1b=sdTriangleIsosceles( vec2(p.z,-p.y)-vec2(0.0,-26.0), vec2(5.0,30));
    float d2=sdBox( p-vec3(0.0,10.0,0.0), vec3(10.0,22.0,10.0) );
    float d1=  Intersect(d1a, d2);
    d1=  Intersect(d1, d1b);
    res =opU2(res, vec2(d1,100.0));
   return res;
}

vec2 GetDist(vec3 p  ) 
{	vec2 res= vec2(9999.0, -1.0); 
    //p= rotate_y(p,radians(iTime*10.0));
    vec3 p0=p;
    float small=0.045;
	float planeDist1 = p.y-1.0;  //piso inf
    res =opU2(res, vec2(planeDist1,101.0));
    p.y=p.y-5.0;
    vec2 p1= PiramideBase(p-vec3(-20.0,0.0,0.0));
    //p1-= small*texture(iChannel0,p.xy).r;
    //float h= tex3D(iChannel1, p/16., vec3(0.0,1.0,0.0)).r;
    //p1-=small*h;
    vec2 p2= PiramideBase(p);
    vec2 p3= PiramideBase(p-vec3(20.0,0.0,0.0));
    res =opU2(res, p1);
    res =opU2(res, p2);
    res =opU2(res, p3);

    vec2 res2=CubosBx(p);
    res =opU2(res, res2);

    vec2 res4=CubosBx(p-vec3(0.0,10.0,0.0));
    res =opU2(res, res4);

    res.x +=  small*texture(iChannel0,p.xy).r; 

    return res;
}

vec3 GetNormal(vec3 p)
{   float d = GetDist(p).x;
    vec2 e = vec2(.001, 0);
    vec3 n = d - vec3(GetDist(p-e.xyy).x,GetDist(p-e.yxy).x,GetDist(p-e.yyx).x);
    return normalize(n);
}

vec2 RayMarch(vec3 ro, vec3 rd, int PMaxSteps)
{   vec3 p;
    vec2 hit, object=vec2(0.1,0.0);
    for(int i=0; i <= PMaxSteps; i++) 
    { p = ro + rd*object.x;
      hit = GetDist(p);
      object.x += hit.x;
      object.y = hit.y;
      if (abs(hit.x) < EPSILON || object.x > MAX_DIST) break;
    }    
    return object;
}

float getSoftShadow(vec3 p, vec3 lightPos) {
    float res = 9999.0;
    float dist = 0.01;
    float lightSize = 0.03;
    for (int i = 0; i < MAX_STEPS; i++) {
      float hit = GetDist(p + lightPos * dist).x;
      res = min(res, hit / (dist * lightSize));
      dist += hit;
      if (hit < 0.0001 || dist > 60.0) break;
    }
    return clamp(res, 0.0, 1.0);
}

float occlusion(vec3 pos, vec3 nor)
{   float sca = 2.0, occ = 0.0;
    for(int i = 0; i < 10; i++) {    
      float hr = 0.01 + float(i) * 0.5 / 4.0;
      float dd = GetDist(nor * hr + pos).x;
      occ += (hr - dd)*sca;
      sca *= 0.6;
    }
    return clamp( 1.0 - occ, 0.0, 1.0 );    
}

vec3 lightingv3(vec3 normal,vec3 p, vec3 lp, vec3 rd, vec3 ro,vec3 lightColor, float t) 
{   vec3 lightPos=lp;
    vec3 worldPos=p;
    vec3 V = -rd;
    vec3 N = normal;
    vec3 L = normalize (lightPos - worldPos);
    vec3 R = reflect (-L, N);

    float lightDist = max(length(L), .001);
    float atten=1.0 / (1.0 + lightDist * 0.125 + lightDist * lightDist * .05);
    L /= (lightDist*atten);

    float shadow = getSoftShadow(worldPos, L);// shadows

    float occ = occlusion(worldPos, N);// occ
    vec3 ambient = Ka + Ka * dot(normal, vec3(0., 1., 0.))*lightColor;
    ambient*=0.5;

    vec3 fresnel =  lightColor *  pow(clamp(1.0 + dot(rd, N), 0.0, 1.0), 2.0);;
    float diff= clamp(dot(N, L), 0.0, 1.0);
    vec3 diffuse =  lightColor * diff;
    float shininess=10.0;
    float specular    = pow(max(dot(R, V), 0.0), shininess);

    vec3 back = 0.5 * lightColor * clamp(dot(N, -L), 0.0, 1.0); // back
    vec3 colOut = occ*lightColor*(ambient+diffuse*shadow+.25 +back) + vec3(.7,.9,1)*specular*specular;
    return colOut;
}

vec3 getColorTextura( vec3 p, vec3 nor,  int i)
{	if (i==100 )
    { 
    //float h= tex3D(iChannel1, p/16., vec3(0.0,1.0,0.0)).r;
    ///vec3 col=tex3D(iChannel0, p/16.+h, nor); return col*2.0; 
    vec3 col=tex3D(iChannel0, p/16., nor); return col*2.0; 
    }
	if (i==101 ) { return tex3D(iChannel1, p/32., nor)+0.2; }
}

vec3 render_sky_color(vec3 rd)
{   float t = (rd.x + 1.0) / 2.0;
    vec3 col= vec3((1.0 - t) + t * 0.3, (1.0 - t) + t * 0.5, (1.0 - t) + t);
    vec3  sky = mix(vec3(.0, .1, .4)*col, vec3(.3, .6, .8), 1.0 - rd.y);
	return sky;
}


vec3 GetMaterial(vec3 p,  vec3 nor, vec3 ro,  vec3 rd, int id_color)
{  	vec3 colobj; 
     //if (id_color<50) { colobj=getColor(int( id_color));  return colobj; }
   if (id_color>=100 ){ return  getColorTextura( p, nor,id_color); }
}

vec3 linear2srgb(vec3 c) 
{ return mix(12.92 * c,1.055 * pow(c, vec3(1.0/1.8)) - 0.055, step(vec3(0.0031308), c)); }

vec3 exposureToneMapping(float exposure, vec3 hdrColor) 
{ return vec3(1.0) - exp(-hdrColor * exposure); }

vec3 ACESFilm(vec3 x)
{   float a,b,c,d,e;
    a = 2.51; b = 0.03; c = 2.43;d = 0.59; e = 0.14;
    return (x*(a*x+b))/(x*(c*x+d)+e);
}

vec3 Render(vec3 ro, vec3 rd)
{  vec3 col = vec3(0);
  vec3 p;
     vec2 hit=RayMarch(ro,rd, MAX_STEPS);
      if(hit.x<MAX_DIST) 
       {   p = (ro + rd * hit.x );  
        vec3 nor=GetNormal(p);
        vec3 colobj;
        colobj=GetMaterial( p, nor, ro, rd,  int(hit.y));        
        vec3 result;
         result= lightingv3(nor, p,light_pos1, rd,ro,colobj,hit.x)*light_color1;
        result+= lightingv3(nor, p,light_pos2,rd, ro,colobj,hit.x)*light_color2;
        col= result/2.0;
        col= (ACESFilm(col)+linear2srgb(col)+col+ exposureToneMapping(3.0, col))/4.0 ;        
    }
    else if(hit.x>MAX_DIST)
    col= render_sky_color(rd);
   return col;
}

Ray RotarEnCirculo(vec3 ro,vec3 rd)
{
   float r=28.0;
   float veltime=30.0;  
    float tt = radians( -iTime*veltime);
    vec3  pos=vec3(0.0,0.0,35.0);
    vec3 rotation1 = pos+vec3(r*sin(tt), 0.0, r*cos(tt));
    ro +=rotation1;   
    rd=rotate_y( vec3(-rd.x,rd.y,-rd.z), tt);
    return  Ray(ro,rd);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{  vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    float t;
    t=mod(iTime*1.0,500.0);
 	light_pos1= vec3(-10.0, 120.0, -25.); light_color1=vec3( 1.0,1.0,1.0 );
 	light_pos2= vec3(10.0, 20.0, -25.0 ); light_color2 =vec3( 1.0,1.0,1.0 ); 
   vec3 ro=vec3(0.0,8.0+6.0*sin(t),-35.0);
   vec3 rd=normalize( vec3(uv.x,uv.y,1.0));
   Ray rt=RotarEnCirculo(ro,rd);
   ro=rt.ro;
   rd=rt.rd;
    //light_pos1+=ro;
    //light_pos2+=ro;
    vec3 col= Render( ro,  rd);
    fragColor = vec4(col,1.0);
}

//-------common
#define PI 3.14159265

struct Ray
{   vec3 ro; // origin
    vec3 rd; // direction
};

///Gracias a Shane...16-jun-2020
vec3 tex3D( sampler2D tex, in vec3 p, in vec3 n ){    
  n = max(n*n - .2, .001); // max(abs(n), 0.001), etc.
  n /= dot(n, vec3(1)); 
  vec3 tx = texture(tex, p.yz).xyz;
  vec3 ty = texture(tex, p.zx).xyz;
  vec3 tz = texture(tex, p.xy).xyz;
  return mat3(tx*tx, ty*ty, tz*tz)*n; 
}
