void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - .5*iResolution.xy)/iResolution.y;

    float size = 10.;
    uv*=size;

    float a = Voronoi2D(uv);
    fragColor = vec4(a);
}

float hash12(vec2 p){
	return fract(sin(dot(p, vec2(12.9898, 78.233))) * 43758.5453);
}

vec2 hash22(vec2 uv){
    float n = hash12(uv);
    return vec2(n, hash12(uv + n));
}

vec3 hash33(vec3 v){
    return vec3(hash12(v.xy), hash12(v.yz), hash12(v.xz));
}
