float mDist(vec2 v1,vec2 v2){
    return abs(v1.x - v2.x) + abs(v1.y - v2.y);
}

float Voronoi2D(vec2 uv){
    vec2 id = floor(uv);
    vec2 gv = uv-id;
    float d = 1.;

    for(int i=-1 ; i<=1 ; i++){
        for(int j=-1 ; j<=1 ; j++){
            vec2 off = vec2(i,j);
            vec2 v = hash22(id+off)+off;
            d = min(d,distance(gv,v));
        }
    }

    return 1.-d;
}
