vec3 Cell( in ivec2 p )
{
    // do wrapping
    ivec2 r = ivec2(textureSize(iChannel0, 0));
    p = (p+r) % r;

    // fetch texel
   // return (texelFetch(iChannel0, p, 0 ).x > 0.5 ) ? 1 : 0;
   return texelFetch(iChannel0, p, 0 ).rgb;
}

vec3 erot(vec3 p, vec3 ax, float ro) {
  return mix(dot(ax, p)*ax, p, cos(ro)) + cross(ax,p)*sin(ro);
}

bool key_down(int key) {
    return int(texelFetch(KEYBOARD, ivec2(key, 0), 0).x) == 1;
}

float test(float x) {
    return abs(fract(x + 0.5) - 0.5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0);

    ivec2 px = ivec2( fragCoord );

    if (iFrame == 0 || key_down(KEY_RESET)) {
        vec2 f = fragCoord + 0.01 * iTime; 
        float o = 0.01;
        vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
        float s = step(abs(length(uv)-0.), 0.05);

        float h = h21(f);
        if (h > 0.99)
            h = 1.;
        else
            h = 0.;
       // h *= s;
       // float h = step(fragCoord.x, 1.) * step(fragCoord.y,1.);
        fragColor.r = h;
        float sc = 0.01;
       // if (fragCoord.x > 0.5 * iResolution.x && fragCoord.y > 0.1 * iResolution.y)
        fragColor.g = cos(sc * fragCoord.x) * cos(sc * fragCoord.y) * 0.5 + 0.5;
      //  fragColor.b = 0.;h21(f);
        return;
    }

    // center cell
    vec3 e = Cell(px); 

    // neighbour cells
    vec3 t = Cell(px + ivec2(0,-1));
    vec3 b = Cell(px + ivec2(0,1));
    vec3 l = Cell(px + ivec2(-1,0));
    vec3 r = Cell(px + ivec2(1,0));   

    float k = max( max(l.x, r.x), max(t.x, b.x) );

    // how much y val changes by (used for rotation)
    float sa = 1./16.;

    // m = 0.125 checks exact quadrant for each direction
    // m > 0.125 oversteps and creates more particles where e.x = 1
    float m = 0.13;
    if (l.x == 1. && (test(l.y + 0.) < m) || 
        r.x == 1. && (test(r.y + 0.5) < m) || 
        t.x == 1. && (test(t.y + 0.25) < m) || 
        b.x == 1. && (test(b.y + 0.75) < m)) { 

        e.x = 1.;
        e.y = fract(e.y + sa);        
    } else {
        // increasing makes reds more blurry
        // (isn't just visual - e.x greater => e.z greater => e.z > 0.12 more often)
        e.x = 0.1 * k; 

    }

    // stops e.x = 1 particles from duping too much
    if (k == 1. && e.x == 1. && e.z > 0.12) {
        e.x = 0.;
        e.z += 0.01;    
    }
    e.z *= 0.995;
    if (e.x == 1.)
        e.z += 0.008;



   // if (e.x == 0.)
  //      e.x = 1.;
    e = clamp(e, 0., 1.);
	fragColor = vec4( e, 0.0 );
}

// Flicker reduction.
// This CA tends to produce structures that flicker between two states.
// Code here copies CA output on odd frames and stays unchanged on even.
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    if (iFrame % 2 == 1)
        fragColor.rgb = texelFetch(iChannel0, ivec2(fragCoord), 0).rgb;
    else
        fragColor.rgb = texelFetch(iChannel1, ivec2(fragCoord), 0).rgb;
}

#define pi 3.1415926535

#define thc(a,b) tanh(a*cos(b))/tanh(a)
#define ths(a,b) tanh(a*sin(b))/tanh(a)
#define sabs(x) sqrt(x*x+1e-2)
//#define sabs(x, k) sqrt(x*x+k)-0.1

#define Rot(a) mat2(cos(a), -sin(a), sin(a), cos(a))

float cc(float a, float b) {
    float f = thc(a, b);
    return sign(f) * pow(abs(f), 0.25);
}

float cs(float a, float b) {
    float f = ths(a, b);
    return sign(f) * pow(abs(f), 0.25);
}

vec3 pal(in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d) {
    return a + b*cos( 6.28318*(c*t+d) );
}

float h21(vec2 a) {
    return fract(sin(dot(a.xy, vec2(12.9898, 78.233))) * 43758.5453123);
}

float mlength(vec2 uv) {
    return max(abs(uv.x), abs(uv.y));
}

float mlength(vec3 uv) {
    return max(max(abs(uv.x), abs(uv.y)), abs(uv.z));
}

float smin(float a, float b) {
    float k = 0.12;
    float h = clamp(0.5 + 0.5 * (b-a) / k, 0.0, 1.0);
    return mix(b, a, h) - k * h * (1.0 - h);
}
