/*
  Knob controls speed and steering; on-track driving distance shown; view direction
  different at high elevation; tyre tracks fade with time; mouseable.
*/

/*
  No. 6 in "Driving" series
    "Truck Driving"            (wsGBDh)
    "Drive The Rover"          (3lGfzd)
    "Segway Driving"           (WtKBWc)
    "Lunar Buggy"              (NsX3RH)
    "Martian Tango"            (fljGW1)
*/

#define AA  0   // (= 0/1) optional antialiasing (can be slow)

float PrBoxDf (vec3 p, vec3 b);
float PrBox2Df (vec2 p, vec2 b);
float PrRoundBox2Df (vec2 p, vec2 b, float r);
float PrCylDf (vec3 p, float r, float h);
float PrRoundCylDf (vec3 p, float r, float rt, float h);
float PrCapsDf (vec3 p, float r, float h);
float PrTorusDf (vec3 p, float ri, float rc);
float Minv2 (vec2 p);
float Maxv2 (vec2 p);
mat3 StdVuMat (float el, float az);
vec2 Rot2D (vec2 q, float a);
vec2 Rot2Cs (vec2 q, vec2 cs);
float ShowIntPZ (vec2 q, vec2 cBox, float mxChar, float val);
float Fbm1 (float p);
float Fbm2 (vec2 p);
vec3 VaryNf (vec3 p, vec3 n, float f);
vec4 Loadv4 (int idVar);

const int npTrail = 256;  // same for image and buffer
const int npFst = 6;

vec3 sunDir, qHit, carPos, wlBase;
vec2 wlRot;
float dstFar, tCur, wlRad, carRot, strRot, onPath, hgSize, trWidf;
int idObj;
bool isSh;
const int idBod = 1, idAx = 2, idBas = 3, idWhl = 4, idStr = 5, idSeat = 6, idWLit = 7,
   idFLit = 8, idBLit = 9;
const float pi = 3.1415927;

#if 0
#define VAR_ZERO min (iFrame, 0)
#else
#define VAR_ZERO 0
#endif

#define DMINQ(id) if (d < dMin) { dMin = d;  idObj = id;  qHit = q; }

vec3 BPanel (vec3 p)
{
  vec4 ra;
  vec3 ds;
  ra = vec4 (-0.08, -0.012, 0.37, -0.42) * pi;
  ds.x = max (dot (p.zy, sin (ra.z + vec2 (0.5 * pi, 0.))) - 1.03,
     - dot (p.zy, sin (ra.w + vec2 (0.5 * pi, 0.))) - 0.75);
  ds.y = dot (vec2 (abs (p.x), - abs (p.y + 0.18)), sin (ra.x + vec2 (0.5 * pi, 0.))) - 1.3;
  ds.z = max (dot (vec2 (abs (p.z), p.y), sin (ra.y + vec2 (0.5 * pi, 0.))) - 3., ds.x);
  return ds;
}

float ObjDf (vec3 p)
{
  vec3 q, ds;
  float dMin, d;
  p -= carPos;
  p.xz = Rot2D (p.xz, carRot);
  p.z -= wlBase.z;
  dMin = dstFar;
  q = p;
  if (! isSh) d = PrBoxDf (q, vec3 (1.8, 1.5, 3.));
  if (isSh || d < 0.1) {
    ds = BPanel (q);
    d = max (max (abs (q.y - 0.1) - 1., ds.y), ds.z);
    d = max (d, - PrBoxDf (p - vec3 (0., 0.2, -2.), vec3 (1.08, 0.4, 0.9)));
    d = max (d, - PrCylDf (((vec3 (abs (q.xz) - wlBase.xz, q.y + wlRad + 0.35)).xzy).yzx,
       wlRad + 0.06, 0.5));
    d = max (d, - max (max (max (abs (q.y - 0.48) - 0.35, abs (q.z - 0.5) - 1.5), ds.x + 0.05),
       0.08 - abs (q.z - 0.5)));
    d = max (d, - max (ds.y + 0.08, min (max (abs (q.y - 0.68) - 0.2, q.z - 0.5),
       max (abs (q.y - 0.55) - 0.31, 0.5 - q.z))));
    d = max (d, - max (max (max (max (abs (q.y - 0.2) - 0.9, ds.y), ds.z) + 0.06,
       abs (q.z - 0.3) - 1.3), - max (max (length (vec2 (q.y + wlRad + 0.35, q.z - wlBase.z)) -
       (wlRad + 0.06 + 0.02), q.z - wlBase.z), - q.y - wlRad - 0.35)));
    DMINQ (idBod);
    q = p;
    q.z = abs (q.z);
    q.yz -= vec2 (- wlRad - 0.35, wlBase.z);
    d = PrCylDf (q.yzx, 0.08, wlBase.x);
    DMINQ (idAx);
    q = p;
    q.y -= -0.88;
    d = PrBoxDf (q, vec3 (1.3, 0.03, 1.35));
    DMINQ (idBas);
    q = p;
    q = vec3 (abs (q.xz) - wlBase.xz, q.y + wlRad + 0.35).xzy;
    if (p.z > 0.) q.xz = Rot2D (q.xz, - strRot * sign (p.x));
    q.yz = Rot2D (q.yz, - ((p.x < 0.) ? wlRot.x : wlRot.y) * sign (p.z));
    d = max (PrRoundCylDf (q.yzx, wlRad - 0.1, 0.1, 0.14), wlRad - 0.25 - length (q.yz));
    d = min (d, PrCylDf (q.yzx, wlRad - 0.25, 0.2));
    d = min (d, PrCylDf (vec3 (q.x, ((abs (q.y) < abs (q.z)) ? q.yz : q.zy)) -
       vec3 (0.17, 0., 0.), 0.07, wlRad - 0.2));
    DMINQ (idWhl);
    q = p;
    q.yz = Rot2D (q.yz - vec2 (0.72, 0.2), -0.1 * pi);
    q -= vec3 (-0.5, -0.3, 1.5);
    d = PrRoundCylDf (q, 0.03, 0.03, 0.35);
    q.z -= -0.35;
    q.xy = Rot2D (q.xy, -8. * strRot + pi / 6.);
    q.xy = Rot2D (q.xy, 2. * pi * floor (3. * atan (q.y, - q.x) / (2. * pi) + 0.5) / 3.);
    d = min (d, PrTorusDf (q, 0.025, 0.35));
    q.x += 0.17;
    d = min (d, PrCylDf (q.yzx, 0.02, 0.17));
    DMINQ (idStr);
    q = p;
    q.yz -= vec2 (-0.4, 0.5);
    d = PrBoxDf (q, vec3 (1.05, 0.1, 0.35));
    q.yz = Rot2D (q.yz - vec2 (0.4, -0.43), 0.1 * pi);
    d = min (d, PrBoxDf (q, vec3 (1.05, 0.35, 0.05)));
    DMINQ (idSeat);
    q = p;
    q.yz -= vec2 (0.15, 1.7);
    d = PrCapsDf (q.xzy, 0.06, 0.05);
    DMINQ (idWLit);
    q = p;
    q.yz -= vec2 (-0.25, 2.99);
    d = PrCapsDf (q.yzx, 0.05, 1.2);
    DMINQ (idFLit);
    q = p;
    q.yz -= vec2 (-0.07, -3.);
    d = PrCapsDf (q.yzx, 0.05, 1.2);
    DMINQ (idBLit);
  } else dMin = min (dMin, d);
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  vec3 p;
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 120; j ++) {
    p = ro + dHit * rd;
    d = ObjDf (p);
    dHit += d;
    if (d < 0.001 || dHit > dstFar || p.y < 0.) break;
  }
  if (p.y < 0.) dHit = dstFar;
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.0005, -0.0005);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float TrObjDf (vec3 p)
{
  vec3 q, ds;
  p -= carPos;
  p.xz = Rot2D (p.xz, carRot);
  p.z -= wlBase.z;
  q = p;
  ds = BPanel (q);
  return max (max (ds.y + 0.03, ds.z + 0.02), max (abs (q.z - 0.5) - 1.55, - q.y - 0.2));
}

float TrObjRay (vec3 ro, vec3 rd)
{
  vec3 p;
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 60; j ++) {
    p = ro + dHit * rd;
    d = TrObjDf (p);
    if (d < 0.001 || dHit > dstFar || p.y < 0.) break;
    dHit += d;
  }
  if (p.y < 0.) dHit = dstFar;
  return dHit;
}

vec3 TrObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.0005, -0.0005);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = TrObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float ObjSShadow (vec3 ro, vec3 rd)
{
  float sh, d, h;
  sh = 1.;
  d = 0.02;
  isSh = true;
  for (int j = VAR_ZERO; j < 30; j ++) {
    h = ObjDf (ro + d * rd);
    sh = min (sh, smoothstep (0., 0.05 * d, h));
    d += h;
    if (sh < 0.05) break;
  }
  isSh = false;
  return 0.6 + 0.4 * sh;
}

vec4 CarCol (out float refFac)
{
  vec4 col4, colB4;
  float r, s;
  colB4 = vec4 (0.8, 0.8, 0.9, 0.2);
  col4 = colB4;
  if (idObj == idBod) {
    s = 1.;
    if (qHit.z > 2.8 && PrRoundBox2Df (qHit.xy + vec2 (0., 0.53), vec2 (0.78, 0.15), 0.01) < 0.)
       s = 0.7 + 0.3 * step (0.2, cos (8. * pi * qHit.x));
    else if (qHit.y > -0.5 && abs (qHit.x) < 1.05 && abs (qHit.z + 2.) < 0.85)
       s = 1. - 0.1 * cos (16. * pi * qHit.x);
    else if (abs (qHit.z - 1.5) < 0.2 && length (vec2 (abs (qHit.x) - 0.2,
       qHit.y + 0.1)) < 0.15) s = 0.6;
    else s = 0.7 + 0.3 * step (-0.75, qHit.y) * step (0., max (abs (qHit.y) - 0.85,
       abs (qHit.z - 0.5) - 0.01)) *
       step (0., max (abs (qHit.y + 0.09) - 0.3, abs (qHit.z - 2.) - 0.01)) *
       step (0., max (abs (qHit.y + 0.15) - 0.6, abs (qHit.z + 1.) - 0.01));
    s *= 0.5 + 0.5 * step (wlRad + 0.07, length (vec2 (qHit.y + wlRad + 0.35,
       abs (qHit.z) - wlBase.z)));
    col4.rgb *= s;
  } else if (idObj == idAx) {
    col4 = vec4 (0.3, 0.3, 0.4, 0.1);
  } else if (idObj == idBas) {
    col4.rgb *= 0.4;
  } else if (idObj == idWhl) {
    r = wlRad - length (qHit.yz);
    if (r < 0.22) col4 = vec4 (0.3, 0.3, 0.3, 0.) * (1. - 0.5 * step (r, 0.07) *
       abs (step (0., cos (32. * pi * qHit.x)) - step (0.5, mod (32. * atan (qHit.z, - qHit.y) /
       (2. * pi) + 0.5, 1.))));
    else if (r < 0.25 || qHit.x < 0.) col4 *= 0.7;
  } else if (idObj == idStr) {
    col4 = vec4 (0.9, 0.9, 0.9, 0.2);
  } else if (idObj == idSeat) {
    col4 = vec4 (0.9, 0.7, 0.4, 0.05) * (0.95 + 0.05 * cos (64. * qHit.x));
  } else if (idObj == idWLit) {
    col4 = (onPath > 0.) ? vec4 (0., 0.8, 0., -1.) : vec4 (0.8, 0., 0., -1.);
  } else if (idObj == idFLit || idObj == idBLit) {
    if (abs (strRot) > 0.03 * pi && strRot * qHit.x < 0. && abs (qHit.x) > 1.1 &&
       mod (2. * tCur, 1.) > 0.5) col4 = vec4 (0.8, 0.8, 0., -1.);
    else col4 = (idObj == idFLit) ? vec4 (1., 1., 1., -1.) : vec4 (1., 0., 0., -1.);
    col4.rgb *= 0.8 + 0.2 * smoothstep (0., 0.02, abs (abs (qHit.x) - 1.1));
  }
  refFac = 0.;
  if (col4 == colB4 && (qHit.y < -0.5 || PrRoundBox2Df (qHit.xz + vec2 (0., 2.),
     vec2 (1.1, 0.95), 0.01) > 0.)) refFac = 0.3;
  return col4;
}

float TrailShd (vec2 p)
{  // (from "Lunar Buggy")
  vec4 u;
  vec2 gB[2], gF[2], dg, q;
  float st, s, gLen;
  st = 1.;
  gB[0] = carPos.xz;
  gF[0] = gB[0] + Rot2D (vec2 (0., 2. * wlBase.z), - carRot);
  for (int j = VAR_ZERO; j < npTrail; j ++) {
    gB[1] = gB[0];
    gF[1] = gF[0];
    u = Loadv4 (npFst + j);
    if (u.z >= 0.) {
      gB[0] = u.xy;
      gF[0] = gB[0] + Rot2D (vec2 (0., 2. * wlBase.z), - u.z);
      s = 1.;
      dg = gB[0] - gB[1];
      gLen = length (dg);
      if (gLen > 0.) {
        q = Rot2Cs (p - 0.5 * (gB[0] + gB[1]), dg.yx / gLen);
        s = min (s, PrRoundBox2Df (vec2 (abs (q.x) - wlBase.x, q.y), vec2 (0.12, 0.5 * gLen), 0.05));
      }
      for (float k = -1.; k <= 1.; k += 2.) {
        dg = gF[0] - gF[1];
        gLen = length (dg);
        if (gLen > 0.) {
          s = min (s, PrRoundBox2Df (Rot2Cs (p - 0.5 * (gF[0] + gF[1]) +
             Rot2D (vec2 (k * wlBase.x, 0.), - u.z), dg.yx / gLen), vec2 (0.12, 0.5 * gLen), 0.05));
        }
      }
      st = min (st, 1. - 0.3 * sqrt (1. - min (1., (float (j) + 10. * (tCur - u.w)) /
         float (npTrail))) * (1. - smoothstep (0., 0.05, s)));
    } else break;
  }
  return st;
}

vec3 GrndNf (vec2 p)
{
  vec2 e = vec2 (0.01, 0.);
  return vec3 (normalize (vec3 (Fbm2 (p) - vec2 (Fbm2 (p + e.xy),
     Fbm2 (p + e.yx)), 2. * e.x)).xzy);
}

vec3 GrndCol (vec3 ro, float dstGrnd, float sh)
{
  vec3 col, colG, vn;
  vec2 vf, q;
  float a, f, ff, st, s1, s2;
  q = mod (ro.xz + 0.5 * hgSize, hgSize) - 0.5 * hgSize;
  colG = 0.4 * mix (vec3 (0.8, 1., 0.5), vec3 (0.7, 0.9, 0.5), 0.2 +
     0.8 * smoothstep (0.3, 0.7, Fbm2 (4. * ro.xz)));
  vf = vec2 (0.);
  f = abs (PrBox2Df (q, vec2 (0.3 * hgSize)) - 0.1 * hgSize);
  if (f < trWidf + 0.2) {
    col = mix (colG, vec3 (0.3), step (f, trWidf + 0.2));
    s1 = smoothstep (0., 0.02, 0.2 - abs (f - trWidf - 0.2));
    s2 = (1. - smoothstep (0., 0.02, f - 0.2)) * smoothstep (0., 0.01,
       Maxv2 (abs (mod (8. * q / (0.4 * hgSize) + 0.5, 1.) - 0.5) - 0.13));
    col = mix (mix (col, vec3 (0.9, 0.9, 0.5), s1), vec3 (0.8), s2);
    vn = vec3 (0., 1., 0.);
    vf = vec2 (16., 1. - max (s1, s2));
  } else {
    col = colG;
    vn = GrndNf (ro.xz);
  }
  st = TrailShd (ro.xz);
  vf *= 1. + 2. * step (st, 0.99);
  ff = 1. - smoothstep (0.5, 0.8, dstGrnd / dstFar);
  if (vf.x > 0. && ff > 0.) vn = VaryNf (vf.x * ro, vn, vf.y * ff);
  col *= (0.2 + 0.8 * sh * max (dot (vn, sunDir), 0.)) * (0.3 + 0.7 * st);
  return col;
}

vec3 SkyBgCol (vec3 ro, vec3 rd)
{
  vec3 col, clCol, skCol;
  vec2 q;
  float f, fd, ff, sd;
  if (rd.y > -0.02 && rd.y < 0.03 * Fbm1 (16. * atan (rd.z, - rd.x))) {
    col = vec3 (0.3, 0.4, 0.5);
  } else {
    q = 0.01 * (ro.xz + 2. * tCur + ((100. - ro.y) / rd.y) * rd.xz);
    ff = Fbm2 (q);
    f = smoothstep (0.2, 0.8, ff);
    fd = smoothstep (0.2, 0.8, Fbm2 (q + 0.01 * sunDir.xz)) - f;
    clCol = (0.7 + 0.5 * ff) * (vec3 (0.7) - 0.7 * vec3 (0.3, 0.3, 0.2) * sign (fd) *
       smoothstep (0., 0.05, abs (fd)));
    sd = max (dot (rd, sunDir), 0.);
    skCol = vec3 (0.3, 0.4, 0.8) + step (0.1, sd) * vec3 (1., 1., 0.9) *
       min (0.3 * pow (sd, 64.) + 0.5 * pow (sd, 2048.), 1.);
    col = mix (skCol, clCol, 0.1 + 0.9 * f * smoothstep (0.01, 0.1, rd.y));
  }
  return col;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 roo, rdo, col, colR, vn;
  float dstObj, dstGrnd, dstTrObj, nDotL, refFac, sh;
  roo = ro;
  rdo = rd;
  isSh = false;
  dstTrObj = TrObjRay (ro, rd);
  dstObj = ObjRay (ro, rd);
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
    col4 = CarCol (refFac);
    if (dstTrObj < dstObj) refFac = 0.;
    if (col4.a >= 0.) {
      nDotL = max (dot (vn, sunDir), 0.);
      sh = ObjSShadow (ro + 0.01 * vn, sunDir);
      col = col4.rgb * (0.2 + 0.2 * max (dot (vn, sunDir * vec3 (-1., 1., -1.)), 0.) +
         0.8 * sh * nDotL * nDotL) + col4.a * step (0.95, sh) *
         pow (max (dot (normalize (sunDir - rd), vn), 0.), 32.);
      if (refFac > 0.) rd = reflect (rd, vn);
    } else if (col4.a == -1.) {
      col = col4.rgb * (0.5 - 0.5 * dot (rd, vn));
    }
  }
  if (dstObj >= dstFar || refFac > 0.) {
    if (rd.y < 0.) {
      dstGrnd = - ro.y / rd.y;
      ro += dstGrnd * rd;
      sh = (dstGrnd < dstFar) ? ObjSShadow (ro + 0.01 * vec3 (0., 1., 0.), sunDir) : 1.;
      colR = GrndCol (vec3 (ro.xz, 0.).xzy, dstGrnd, sh);
      colR = mix (colR, vec3 (0.3, 0.4, 0.5), pow (1. + rd.y, 16.));
    } else {
      colR = SkyBgCol (ro, rd);
    }
    col = (refFac > 0.) ? mix (col, 0.9 * colR, refFac) : colR;
  }
  if (dstTrObj < min (dstObj, dstFar)) {
    ro = roo + dstTrObj * rdo;
    vn = TrObjNf (ro);
    col *= vec3 (0.9, 1., 0.9);
    rd = reflect (rdo, vn);
    col = mix (col, SkyBgCol (ro, rd), 0.2 + 0.8 * pow (1. - abs (dot (vn, rdo)), 5.));
  }
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 stDat;
  vec3 rd, ro, col, c, wgBox;
  vec2 canvas, uv, ud, cnPos;
  float el, az, asp, zmFac, trvDist, s, sr;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  asp = canvas.x / canvas.y;
  stDat = Loadv4 (1);
  wlBase = stDat.xyz;
  wlRad = wlBase.y;
  stDat = Loadv4 (2);
  hgSize = stDat.x;
  trWidf = stDat.y;
  onPath = stDat.z;
  trvDist = stDat.w;
  stDat = Loadv4 (3);
  az = stDat.x;
  el = stDat.y;
  cnPos = stDat.zw;
  stDat = Loadv4 (4);
  strRot = stDat.y;
  wlRot = stDat.zw;
  stDat = Loadv4 (5);
  carPos = stDat.xyz;
  carRot = stDat.w;
  el = clamp (el - 0.1 * pi, -0.49 * pi, -0.03 * pi);
  if (el > -0.47 * pi) az += carRot;
  vuMat = StdVuMat (el, az);
  ro = carPos + vuMat * vec3 (0., 0., -12.) + vec3 (Rot2D (vec2 (0., wlBase.z), - carRot),
     carPos.y + 1.).xzy;
  carPos.y += wlRad + 0.95;
  zmFac = 5. + 3. * el;
  dstFar = 150.;
  sunDir = normalize (vec3 (1., 2., -1.));
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = float (VAR_ZERO); a < naa; a ++) {
    rd = vuMat * normalize (vec3 (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
       sr * (0.667 * a + 0.5) * pi), zmFac));
    col += (1. / naa) * ShowScene (ro, rd);
  }
  wgBox = vec3 (0.41, -0.32, 0.135);
  ud = 0.5 * uv - wgBox.xy * vec2 (asp, 1.);
  s = (length (ud) - wgBox.z) * canvas.y;
  col = mix (vec3 (0., 1., 1.), col, smoothstep (0., 1., abs (s) - 1.));
  if (s < 0.) {
    col = mix (vec3 (0., 1., 1.), col, step (1., Minv2 (abs (ud)) * canvas.y));
    c = (onPath > 0.) ? vec3 (0., 1., 0.) : vec3 (0.8, 0., 0.);
    col = mix (c, col,
       smoothstep (2.5, 3.5, abs (length (ud - cnPos) * canvas.y - 10.)));
    ud = Rot2D (ud, atan (cnPos.y, - cnPos.x));
    if (ud.x < 0. && (length (cnPos) - length (ud)) * canvas.y > 10.)
       col = mix (c, col, smoothstep (1.5, 2.5, abs (ud.y) * canvas.y));
  }
  col = mix (col, vec3 (0., 1., 1.), ShowIntPZ (0.5 * uv - vec2 (0.44 * asp, -0.15),
     vec2 (0.06 * asp, 0.03), 4., mod (floor (trvDist / (2. * wlRad)), 1e4)));
  fragColor = vec4 (col, 1.);
}

float PrBoxDf (vec3 p, vec3 b)
{
  vec3 d;
  d = abs (p) - b;
  return min (max (d.x, max (d.y, d.z)), 0.) + length (max (d, 0.));
}

float PrBox2Df (vec2 p, vec2 b)
{
  vec2 d;
  d = abs (p) - b;
  return min (max (d.x, d.y), 0.) + length (max (d, 0.));
}

float PrRoundBox2Df (vec2 p, vec2 b, float r)
{
  return length (max (abs (p) - b, 0.)) - r;
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrRoundCylDf (vec3 p, float r, float rt, float h)
{
  return length (max (vec2 (length (p.xy) - r, abs (p.z) - h), 0.)) - rt;
}

float PrCapsDf (vec3 p, float r, float h)
{
  return length (p - vec3 (0., 0., clamp (p.z, - h, h))) - r;
}

float PrTorusDf (vec3 p, float ri, float rc)
{
  return length (vec2 (length (p.xy) - rc, p.z)) - ri;
}

float Minv2 (vec2 p)
{
  return min (p.x, p.y);
}

float Maxv2 (vec2 p)
{
  return max (p.x, p.y);
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

vec2 Rot2Cs (vec2 q, vec2 cs)
{
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

float DigSeg (vec2 q)
{
  return (1. - smoothstep (0.13, 0.17, abs (q.x))) *
     (1. - smoothstep (0.5, 0.57, abs (q.y)));
}

#define DSG(q) k = kk;  kk = k / 2;  if (kk * 2 != k) d += DigSeg (q)

float ShowDig (vec2 q, int iv)
{
  float d;
  int k, kk;
  const vec2 vp = vec2 (0.5, 0.5), vm = vec2 (-0.5, 0.5), vo = vec2 (1., 0.);
  if (iv == -1) k = 8;
  else if (iv < 2) k = (iv == 0) ? 119 : 36;
  else if (iv < 4) k = (iv == 2) ? 93 : 109;
  else if (iv < 6) k = (iv == 4) ? 46 : 107;
  else if (iv < 8) k = (iv == 6) ? 122 : 37;
  else             k = (iv == 8) ? 127 : 47;
  q = (q - 0.5) * vec2 (1.8, 2.3);
  d = 0.;
  kk = k;
  DSG (q.yx - vo);  DSG (q.xy - vp);  DSG (q.xy - vm);  DSG (q.yx);
  DSG (q.xy + vm);  DSG (q.xy + vp);  DSG (q.yx + vo);
  return d;
}

float ShowIntPZ (vec2 q, vec2 cBox, float mxChar, float val)
{
  float nDig, idChar, s, v;
  q = vec2 (- q.x, q.y) / cBox;
  s = 0.;
  if (min (q.x, q.y) >= 0. && max (q.x, q.y) < 1.) {
    q.x *= mxChar;
    nDig = mxChar;
    idChar = mxChar - 1. - floor (q.x);
    q.x = fract (q.x);
    val = max (val, 0.);
    v = val / pow (10., mxChar - idChar - 1.);
    if (idChar >= mxChar - nDig) s = ShowDig (q, int (mod (floor (v), 10.)));
  }
  return s;
}

const float cHashM = 43758.54;

vec2 Hashv2f (float p)
{
  return fract (sin (p + vec2 (0., 1.)) * cHashM);
}

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (dot (p, cHashVA2) + vec2 (0., cHashVA2.x)) * cHashM);
}

float Noiseff (float p)
{
  vec2 t;
  float ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = Hashv2f (ip);
  return mix (t.x, t.y, fp);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm1 (float p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noiseff (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec4 v;
  vec3 g;
  vec2 e = vec2 (0.1, 0.);
  for (int j = VAR_ZERO; j < 4; j ++)
     v[j] = Fbmn (p + ((j < 2) ? ((j == 0) ? e.xyy : e.yxy) : ((j == 2) ? e.yyx : e.yyy)), n);
  g = v.xyz - v.w;
  return normalize (n + f * (g - n * dot (n, g)));
}

#define txBuf iChannel0
#define txSize iChannelResolution[0].xy

const float txRow = 128.;

vec4 Loadv4 (int idVar)
{
  float fi;
  fi = float (idVar);
  return texture (txBuf, (vec2 (mod (fi, txRow), floor (fi / txRow)) + 0.5) / txSize);
}

