vec4 Loadv4 (int idVar);
void Savev4 (int idVar, vec4 val, inout vec4 fCol, vec2 fCoord);

const int npTrail = 256;  // same for image and buffer
const int npFst = 6;

vec3 carPos, wlBase;
float carRot, hgSize, trWidf;
const float pi = 3.1415927;
const float txRow = 128.;

float OnPath ()
{
  vec2 q;
  q = carPos.xz + Rot2D (vec2 (0., wlBase.z), - carRot);
  return (abs (PrBox2Df (mod (q + 0.5 * hgSize, hgSize) - 0.5 * hgSize,
     vec2 (0.3 * hgSize)) - 0.1 * hgSize) < trWidf) ? 1. : 0.;
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  vec4 mPtr, mPtrP, stDat;
  vec3 wgBox;
  vec2 iFrag, canvas, cnPos, wlRot, cp, ud, w;
  float tCur, autoMd, cRotN, strRot, wlRad, rTurn, spd, onPath, trvDist, tc, nStep,
     az, el, asp, s;
  int pxId, wgSel, nFrame;
  bool init;
  iFrag = floor (fragCoord);
  pxId = int (iFrag.x + txRow * iFrag.y);
  if (pxId >= npFst + npTrail) discard;
  nFrame = iFrame;
  canvas = iResolution.xy;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  init = (iFrame <= 5);
  asp = canvas.x / canvas.y;
  if (init) {
    carPos = vec3 (0.);
    carRot = 0.;
    strRot = 0.;
    wlRad = 0.6;
    wlBase = vec3 (1.25, wlRad, 2.);
    wlRot = vec2 (0.);
    az = -0.3 * pi;
    el = 0.;
    nStep = 0.;
    cnPos = vec2 (0.);
    mPtrP = mPtr;
    wgSel = 0;
    autoMd = 1.;
    hgSize = 100.;
    trWidf = 3.;
    onPath = 0.;
    trvDist = 0.;
  } else {
    stDat = Loadv4 (0);
    mPtrP.xyz = stDat.xyz;
    wgSel = int (stDat.w);
    stDat = Loadv4 (1);
    wlBase = stDat.xyz;
    autoMd = stDat.w;
    stDat = Loadv4 (2);
    hgSize = stDat.x;
    trWidf = stDat.y;
    onPath = stDat.z;
    trvDist = stDat.w;
    stDat = Loadv4 (3);
    az = stDat.x;
    el = stDat.y;
    cnPos = stDat.zw;
    stDat = Loadv4 (4);
    nStep = stDat.x;
    strRot = stDat.y;
    wlRot = stDat.zw;
    stDat = Loadv4 (5);
    carPos = stDat.xyz;
    carRot = stDat.w;
  }
  if (pxId < npFst) {
    wgBox = vec3 (0.41, -0.32, 0.135);
    if (mPtr.z > 0.) {
      if (wgSel == 0 && mPtrP.z > 0.) {
        az = -2. * pi * mPtr.x;
        el = - pi * mPtr.y;
      } else {
        ud = mPtr.xy * vec2 (asp, 1.) - wgBox.xy * vec2 (asp, 1.);
        if (wgSel == 1) {
          autoMd = - tCur;
          cnPos = ud;
          s = length (cnPos);
          if (s > 0.) cnPos = min (s, wgBox.z) * cnPos / s;
        } else if (mPtrP.z <= 0. && length (ud) < wgBox.z) wgSel = 1;
      }
    } else {
      wgSel = 0;
      cnPos *= 1. - 5e-3;
    }
    wlRad = wlBase.y;
    ++ nStep;
    if (autoMd > 0.) {
      tc = 0.005 * nStep;
      s = 2. * Noiseff (37.1 * mod (floor (tc), 16.)) - 1.;
      s = max (0.3, abs (s)) * sign (s);
      strRot = 0.15 * pi * s * SmoothBump (0.2, 0.8, 0.1, mod (tc, 1.));
      spd = 0.12 - 0.06 * abs (strRot / (0.15 * pi));
      w = vec2 (- strRot / (0.15 * pi), spd / 0.5);
      w = pow (abs (w), 1. / vec2 (1.5)) * sign (w);
      cnPos = w * wgBox.z;
    } else {
      w = cnPos / wgBox.z;
      w = pow (abs (w), vec2 (1.5)) * sign (w);
      strRot = -0.18 * pi * w.x;
      spd = 0.2 * w.y;
      if (tCur + autoMd > 10.) autoMd = 1.;
    }
    cp = carPos.xz;
    w = vec2 (1.);
    if (abs (strRot) > 1e-4) {
      cRotN = carRot - strRot * spd / pi;
      rTurn = wlBase.z / asin (0.5 * strRot);
      carPos.xz += rTurn * (sin (carRot - vec2 (0.5 * pi, 0.)) - sin (cRotN - vec2 (0.5 * pi, 0.)));
      carRot = mod (cRotN, 2. * pi);
      w += vec2 (-1., 1.) * wlBase.x / rTurn;
    } else {
      carPos.xz += spd * sin (carRot + vec2 (0., 0.5 * pi));
    }
    wlRot += w * spd / wlRad;
    if (pxId == 2) {
      onPath = OnPath ();
      if (onPath > 0.) trvDist += length (carPos.xz - cp);
      else trvDist = 0.;
    }
  }
  if (! init) {
    if (mod (float (nFrame), 6.) == 0.) {
      if (pxId == npFst) stDat = vec4 (Loadv4 (5).xzw, tCur);
      else if (pxId < npFst + npTrail) stDat = Loadv4 (pxId - 1);
    } else if (pxId >= npFst && pxId < npFst + npTrail) stDat = Loadv4 (pxId);
  } else {
    stDat = vec4 (0., 0., -1., 0.);
  }
  if      (pxId == 0) stDat = vec4 (mPtr.xyz, float (wgSel));
  else if (pxId == 1) stDat = vec4 (wlBase, autoMd);
  else if (pxId == 2) stDat = vec4 (hgSize, trWidf, onPath, trvDist);
  else if (pxId == 3) stDat = vec4 (az, el, cnPos);
  else if (pxId == 4) stDat = vec4 (nStep, strRot, wlRot);
  else if (pxId == 5) stDat = vec4 (carPos, carRot);
  Savev4 (pxId, stDat, fragColor, fragCoord);
}

float PrBox2Df (vec2 p, vec2 b)
{
  vec2 d;
  d = abs (p) - b;
  return min (max (d.x, d.y), 0.) + length (max (d, 0.));
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

const float cHashM = 43758.54;

vec2 Hashv2f (float p)
{
  return fract (sin (p + vec2 (0., 1.)) * cHashM);
}

float Noiseff (float p)
{
  vec2 t;
  float ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = Hashv2f (ip);
  return mix (t.x, t.y, fp);
}

#define txBuf iChannel0
#define txSize iChannelResolution[0].xy

vec4 Loadv4 (int idVar)
{
  float fi;
  fi = float (idVar);
  return texture (txBuf, (vec2 (mod (fi, txRow), floor (fi / txRow)) + 0.5) / txSize);
}

void Savev4 (int idVar, vec4 val, inout vec4 fCol, vec2 fCoord)
{
  vec2 d;
  float fi;
  fi = float (idVar);
  d = abs (fCoord - vec2 (mod (fi, txRow), floor (fi / txRow)) - 0.5);
  if (max (d.x, d.y) < 0.5) fCol = val;
}
