#define H(p)   fract(sin(dot(p, vec2(12.9898, 78.233))-floor(iTime)) * 43758.5453)

// the #define version doesn't work on Windows
int t,a; vec2 U;
float n( int d) {
    vec4 T = T( U+ vec2[](vec2(-1,1),vec2(-1,0),vec2(0,-1),vec2(1, -1),vec2(1,0),vec2(0,1))[(d+a)%6] );
    int  A = ( d + a - (int(T.a)%8) +3 +6 )%6,
         n = int(T.a)/8;
    return T[ int[](0, n>1?1:2, n==1?1:2, (n+1)%3, n<2?1:2, n<2?0:1)[A] ];
}
