void mainImage( out vec4 O, vec2 u )
{
    U = u-.5;
    if ( iFrame < 1 || keyDown(32) ) {
        O.a = 8.* floor(3.*H(U))                       // random tile type
            + floor(6.*H(U+.5));                       // random tile rotation
        O.xyz = U.y + U.x*4096. + vec3(0,.3,.6);       // init ids#1,2,3
        return;
    }
    O = T(U);                                 // recover previous state
    t = typ(U), a = ang(U);                   // tile type and angle

    vec4 T;                                   // --- propagate id along connections
                                              // read id in neighbor in direction d

    if (t==0) {       // tile type #0   For each thread, new id = min(connections)
        O.x = min( O.x, min( n(5), n(0) ));
        O.y = min( O.y, min( n(3), n(4) ));
        O.z = min( O.z, min( n(1), n(2) ));
    }
    else if (t==1) {  // tile type #1   For each thread, new id = min(connections)
        O.x = min( O.x, min( n(5), n(0) ));
        O.y = min( O.y, min( n(2), n(4) ));
        O.z = min( O.z, min( n(1), n(3) ));
    }
    else  {           // tile type #2   For each thread, new id = min(connections)
        O.x = min( O.x, min( n(0), n(3) ));
        O.y = min( O.y, min( n(1), n(5) ));
        O.z = min( O.z, min( n(2), n(4) ));
    }
}
