Main {
    U = 2.*(U-.5*R)/R.y;
    float d = 1e9;
    Q = vec4(0);
    for (float i = 0.; i < N; i++ ) {

            vec4 q = A(vec2(i,0));
            vec2 v = q.xy - U;
            v *= ei(-q.z);
            float rb = roundBox(v,vec2(2.*dim-.02),.01);
            vec4 dQ = max(4.*sin(atan(v.y,v.x)+vec4(1,2,3,4))*(1.-exp(-dot(v,v)))+
                sin(-2.+vec4(1,2,3,4)+.5*i),0.);
            dQ *= .5+.75*smoothstep(.02,0.,abs(rb));
            dQ *= exp(-10.*max(rb,0.));
            Q += .7*dQ;


            //vec2 m = vec2(0,1);
            //Q.xy += .1*integral(v.x,v.y,m.x,m.y,dim)*ei(q.z);


    }
    if (U.y > -.75) Q += vec4(.2,.1,.3,1);
}

void impulse (vec4 p, vec4 v, vec2 pos, vec2 im, vec2 dv, inout vec4 f) {
    im -= .2*dv;
    f.xy += .2*im * dot(normalize(p.xy-pos),normalize(im));
    f.z  += .8*dot(vec2(im.y,-im.x),normalize(p.xy-pos));
}
Main {
    U = floor(U);
    if (U.y>1.||U.x>=N) discard;
    vec4 p = A(vec2(U.x,0));
    vec4 v = A(vec2(U.x,1));

    vec2 m = vec2(0,1)*ei(p.z);


    vec4 f = vec4(0,-.0002,0,0);
    for (int j = 0; j < 41; j++) {
        vec3 c = dim*circles[j];
        c.xy *= ei(p.z);
        c.xy += p.xy;
        for (float i = 0.; i < N; i++) if (i!=U.x) {
            vec4 p1 = A(vec2(i,0));
            vec4 v1 = A(vec2(i,1));

            /*vec2 r = p1.xy-c.xy;
            r *= ei(-p1.z);
            vec2 fp = .001*m*(fd_grad_integral(r.x,r.y,0.,1.,dim)*ei(p1.z));

            if (j == 0) f.xy += fp;
            else impulse(p,v,c.xy,fp,vec2(0),f);
            */

            if (length(p.xy-p1.xy)<=4.*dim)
            for (int k = 0; k < 41; k++) {
                vec3 c1 = dim*circles[k];
                c1.xy *= ei(p1.z);
                c1.xy += p1.xy;
                if (length(c.xy-c1.xy)<c.z+c1.z) {
                    vec2 pos = (c.xy*c.z+c1.xy*c1.z)/(c.z+c1.z);
                    vec2 im = .2*(length(c.xy-c1.xy)-c.z-c1.z)*normalize(c1.xy-c.xy);
                    vec2 w = p.xy-pos;
                    w = vec2(-w.y,w.x);
                    vec2 dv = v.xy + w*v.z;
                    vec2 w1 = p1.xy-pos;
                    w = vec2(-w.y,w.x);
                    vec2 dv1 = v1.xy + w1*v1.z;
                    impulse(p,v,pos,im,(dv-dv1),f);
                }
            }
        }
        if (c.y-c.z<-.75) {
            vec2 pos = vec2(c.x,-.75);
            vec2 im = vec2(0,-.2*(c.y-c.z+.75));
            vec2 w = p.xy-pos;
            w = vec2(-w.y,w.x);
            vec2 dv = v.xy + w*v.z;
            impulse(p,v,pos,im,dv,f);
        }
        float bx = R.x/R.y;
        if (c.x-c.z<-bx) {
            vec2 pos = vec2(-bx,c.y);
            vec2 im = vec2(-.2*(c.x-c.z+bx),0);
            vec2 w = p.xy-pos;
            w = vec2(-w.y,w.x);
            vec2 dv = v.xy + w*v.z;
            impulse(p,v,pos,im,dv,f);
        }
        if (c.x+c.z>+bx) {
            vec2 pos = vec2(+bx,c.y);
            vec2 im = vec2(+.2*(c.x+c.z-bx),0);
            vec2 w = p.xy-pos;
            w = vec2(-w.y,w.x);
            vec2 dv = v.xy + w*v.z;
            impulse(p,v,pos,im,dv,f);
        }
    }

    float dt = 1.;
    vec2 mo = 2.*(iMouse.xy-.5*R)/R.y;
    if (iMouse.z>0.&&length(p.xy-mo)<dim) f.xy += .1*(mo-p.xy) - .01*v.xy;

    v.xyz +=    dt*f.xyz - dt*.0001*v.xyz;
    p.xyz += .5*dt*f.xyz + dt*v.xyz;


    if (iFrame < 1) {

        p.xy = vec2(U.x/(N-1.)*2.-1.,.5-.5*U.x/(N-1.));
        p.z =3.+U.x;
        v = vec4(0);

    }

    if (U.y < 1.) Q = p; else Q = v;



}

