void mainImage( out vec4 o, vec2 u )
{
    vec2 f = sign(u = mod(u*8./iResolution.y,4.) - 2.);    

    f *= f==f.yx ? u.yx : u;

    o = 8./((abs(--f.x)-f)*--f*(f.x+f)).yyyy;
}


