const float diffuse_seconds = 7.0;
const float cutoff_radii = 1.1;
const float wave_length = 0.53 / 2.0;
const float wave_amplitude = 0.25; 
const float wave_frequency = 1.0;

float sdf_main(vec2 pos) {
    float diffuse_velocity = 1.13 / diffuse_seconds;
    float angle = atan(pos.y, pos.x) + sin(length(pos) * wave_frequency * 0.5 + iTime) * wave_amplitude * 0.5;
    float radius = length(pos) + sin(angle * 8.0 + iTime) * wave_amplitude * 0.5; 
    float wave = radius - diffuse_velocity * iTime;
    return wave;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 ndc = (fragCoord - iResolution.xy / 2.0) / iResolution.xx;
    float sdf = sdf_main(ndc);
    float value = pow(mod(sdf * (1.0 / wave_length), 1.0), 2.0); 
    float cutoff = 1.0 - smoothstep(0.0, cutoff_radii, length(ndc));

    if (sdf < 0.0) {
        vec3 pink = vec3(0.9, 0.5, 0.7);
        vec3 violet = vec3(0.7, 0.5, 0.9); 
        vec3 baseBlue = vec3(0.1, 0.5, 0.9);
        vec3 lightBlue = vec3(0.4, 0.7, 1.0);

        vec3 colorMix1 = mix(baseBlue, pink, sqrt(value));
        vec3 colorMix2 = mix(lightBlue, violet, sqrt(value));

        fragColor = vec4(mix(colorMix1, colorMix2, cutoff), 1.0);
    } else {
        fragColor = vec4(vec3(0.0), 1.0);
    }
}
