/*
    This attempts to handle transparent materials
    with various indices of refraction
    which directly abut each other.

    Orion Elenzil 2024
*/



/*

Keyboard (iChannel3)
--------------------
1. heat map
2. x-eyed stereo
3. cutting plane
4. zoom
5. AA
6. transmission

*/

#define SUBSURFACE 0   // not-quite-right subsurface scattering
#define HQ         0   // slowww but pretty

//---------------------------------------------------------------
// Viewport and Time
const float gZoom       = 0.8;
      float gFOVZoom    = 2.0;
      float gCamDist    = 24.0;
const float gToggleZoom = 2.0;
const float gStereoSep  = 0.4;

      float gT;         // set in main

//--------------------------------------------------------------------------
// ray handling parameters

// maximum number of rays per pixel.
// reflections, shadows, and transmission all spawn a ray,
// and a single ray travels until it hits a boundary.
#if HQ
const uint  gHQMult         = 3u;
#else
const uint  gHQMult         = 1u;
#endif
const uint  gMaxRays        = 120u * gHQMult;
const uint  gQueueSize      =  18u * gHQMult;

// standard marching parameters
const float gSceneScale     = 2.0f;
const uint  gMaxMarchSteps  = 200u * gHQMult;
const float gMarchEps       = 0.03;
const float gNormEps        = gMarchEps * 0.4;
const float gMarchMaxT      = 5e1 * gSceneScale;
const float gMarchUnderstep = 1.0 / float(gHQMult);
const float gHitBackoff     = gMarchEps * 0.0;

// smallest contribution before extinguishing a ray
const float gMinAmt         = 0.01 / float(gHQMult);

// direction to the light
vec3  gLightDir;
float gAmbient              = 0.02;

// heat map stuff
      float gMapCount       = 0.0;
const float cHeatMapSteps   = 200.0;
const float cHeatMapRays    = 20.0;

// debugging color
      vec3  gDebugRGB       = v0;

//--------------------------------------------------------------------------
// Materials

const uint gMtlAir   = 1u;
const uint gMtlFloor = 2u;
const uint gMtlGlass = 3u;
const uint gMtlWater = 4u;
const uint gMtlIce   = 5u;
const uint gMtlBands = 6u;
mtl_t mtls[] = mtl_t[] (
    //    albedo             opac   ior    absorption
    mtl_t(vX * 1e9         , 1.0  , 1.0  , v41                   ), // 0 invalid
    mtl_t(v1               , 0.0  , 1.0  , v41 * 0.5             ), // 1 air
    mtl_t(v1 * 0.3         , 1.0  , 1.3  , v41 * 1.0             ), // 2 floor
    mtl_t(v1 * 1.0         , 0.0  , 1.52 , V4(0.3, 0.3, 0.3, 0.2)), // 3 glass
    mtl_t(v1 * 1.0         , 0.0  , 1.33 , V4(0.2, 0.2, 0.6, 0.5)), // 4 water
    mtl_t(vX + vY          , 0.0  , 1.31 , V4(0.2, 0.2, 0.2, 0.1)), // 5 ice
    mtl_t(V3(0.9, 0.0, 0.4), 0.015, 1.2  , V4(0.5, 0.8, 0.0, 2.0)), // 6 bands
    mtl_t(v1 * 0.6         , 1.0  , 1.5  , v41                   )  // 7
);

//---------------------------------------------------------------------
// keyboard & mouse stuff part one
bool kPipOn;        // 1. heat map
bool kXEyedStereo;  // 2. x-eyed stereo
bool kCuttingPlane; // 3. cutting plane
bool kZoom;         // 4. zoom
bool kAA;           // 5. AA
bool kTransmission; // 6. transmission
// pbpaste | sed -e 's/.*\/\/ //' | pbcopy

//--------------------------------------------------------------------------
// Scene Setup
vec3  gSceneCenter;
float gFloorGap;
mat2  gObjRot1;
mat2  gObjRot2;
vec3  gCamPos;


void setupScene() {
    gSceneCenter  = gSceneScale * vY * 8.0;
    gLightDir     = normalize(vec3(-0.8, 1.5, 1.9));
    gFOVZoom     *= kZoom ? gToggleZoom : 1.0;

    gObjRot1 = rot2(gT * 0.123);
    gObjRot2 = rot2(gT * 0.321);

    // change the material verrrry slowly according to the wall time.
    vec4 w = mtls[gMtlBands].absorption;
    float t = (iDate.z * 100.0 + iDate.w) * 1e-3;
    w.r  = sin(t * 1.01) * 0.3 + 0.4;
    w.g  = sin(t * 1.21) * 0.3 + 0.4;
    w.b  = sin(t * 1.31) * 0.3 + 0.4;
    w.a += sin(t * 1.41) * 0.3 + 0.5;
    mtls[gMtlBands].absorption = w;

    float no = sin(t * 1.51) * 0.1;
    mtls[gMtlBands].ior += no;
}


//---------------------------------------------------------------------
// The SDF !

MST sdScene(in vec3 p) {
    gMapCount += 1.0;

    p.y -= gSceneCenter.y;

    p /= gSceneScale;
    vec3 q = p;


    vec3 tmp;
    tmp  = opRing(q.xz, 9.0, 5.0);
    q.xz = tmp.xy;
    q.yz *= gObjRot1;
    tmp  = opRing(q.yz, 4.0, 3.0);
    q.yz = tmp.xy;

    q.yz *= gObjRot1;
    q.yx *= gObjRot2;

    float d = 1e9;

    d = min(d,  sdCappedCylinder(q, 1., 2.7));
    d = max(d, -sdCylinder(q.xz, 0.8));
    d -= 0.1;


    MST ms = MST(d, 4u);


    if (kCuttingPlane) {
        ms.dist = max(ms.dist, p.z);
    }

    if (ms.dist > 0.0) {
        ms.mtl = gMtlAir;
    }

    return ms;
}


//------------------------------------------------------
// ray-handling stuff.

// FIFO queue of rays. see Common tab.
#define Q_TYPE ray_t
#define Q_MAX_ENTRIES (gQueueSize)
Q_IMPLEMENTATION
hit_t rayVsScene(ray_t);
vec3  sky(ray_t);
vec3  albedo(hit_t);
MST sdScene(vec3 p);
MST sdSceneWithFloor(vec3 p);

// run a single ray.
vec3 runRay(ray_t ray) {
    hit_t hit = rayVsScene(ray);

    if (!hit.hit) {
        // ray hit nothing.

        if (ray.isLightingRay) {
            // a "lighting ray" is to the light.
            return ray.amt;
        }
        else {
            // it's a direct ray and sees the sky.
            return ray.amt * sky(ray);
        }
    }

    // the ray hit something

    // get map samples on either side of the interface
    MST ms1 = sdSceneWithFloor(hit.pnt + hit.hf_nrm * gMarchEps * 3.0);
    MST ms2 = sdSceneWithFloor(hit.pnt - hit.hf_nrm * gMarchEps * 3.0);
    mtl_t mtl1 = mtls[ms1.mtl];
    mtl_t mtl2 = mtls[ms2.mtl];

    // just traveled through media, attenuate !  (but not through air)
    if (ms1.mtl != gMtlAir) {
        vec4 absorption = mtl1.absorption;
        absorption.w   = mix(absorption.w * 0.7, absorption.w  , smoothstep(0.0, 4.0, hit.pnt.y / gSceneScale));
        absorption.rgb = mix(absorption.brg * 1.7, absorption.rgb, smoothstep(0.0, 6.0, hit.pnt.y / gSceneScale));
        float density = absorption.w;
        float absorptionDist = hit.t / gSceneScale;
        ray.amt *= sat(exp(-density * absorptionDist * (1.0 - absorption.rgb)));
    }

    if (ray.isLightingRay && !QIsFull()) {
        // lighting ray just keeps traveling
        if (kTransmission) {
            float R = reflectAmount(mtls[ms1.mtl].ior, mtl2.ior, dot(ray.rd, -hit.hf_nrm));
            ray.amt *= 1.0 - mtl2.opacity;
            ray.amt *= 1.0 - R;
            ray.ro = hit.pnt - hit.hf_nrm * gMarchEps * 4.0;
            QEnqueue(ray);
        }
        return v0;
    }

    float dif;
    vec4  rfl;
    vec4  trn;
    vec3  tmpAmt;

    thisRayWalksIntoAnInterface(
        ray.rd,
        hit.hf_nrm,
        mtl1.ior,
        mtl2.ior,
        mtl2.opacity,
        dif,
        rfl,
        trn);

    // diffuse
    float normal_dot_light = dot(hit.of_nrm, gLightDir);
    vec3 alb = albedo(hit);
    float shade = sat(normal_dot_light);
    tmpAmt = ray.amt * dif * alb * shade;
    if (vmax(tmpAmt) > gMinAmt && !QIsFull()) {
        // start a new ray a little off the surface
        // towards the light
        ray_t newRay;
        newRay.amt = tmpAmt;
        newRay.ro  = hit.pnt + hit.hf_nrm * gMarchEps * 2.0;
        newRay.rd  = gLightDir;
        newRay.isLightingRay = true;
        QEnqueue(newRay);
    }

    // subsurface
    // this has issues.
    #if SUBSURFACE
    tmpAmt = ray.amt * dif * alb * (sat(-normal_dot_light + 0.5));
    if (vmax(tmpAmt) > gMinAmt && !QIsFull()) {
        // start a new ray a little inside the surface
        // towards the light
        ray_t newRay;
        newRay.amt = tmpAmt;
        newRay.ro  = hit.pnt - hit.hf_nrm * gMarchEps * 5.0;
        newRay.rd  = gLightDir;
        newRay.isLightingRay = true;
        QEnqueue(newRay);
    }
    #endif

    // reflect
    tmpAmt = ray.amt * rfl.w;
    if (vmax(tmpAmt) > gMinAmt && !QIsFull()) {
        // start a new ray a little off the surface
        // in the reflect direction
        ray_t newRay;
        newRay.amt = tmpAmt;
        newRay.ro  = hit.pnt + hit.hf_nrm * gMarchEps * 2.0;
        newRay.rd  = rfl.xyz;
        newRay.isLightingRay = false;
        QEnqueue(newRay);
    }

    // transmit
    tmpAmt = ray.amt * trn.w;
    if (kTransmission && vmax(tmpAmt) > gMinAmt && !QIsFull()) {
        // start a new ray a little inside the surface
        // in the refraction direction
        ray_t newRay;
        newRay.amt = tmpAmt;
        newRay.ro  = hit.pnt - hit.hf_nrm * gMarchEps * 2.0;
        newRay.rd  = trn.xyz;
        newRay.isLightingRay = false;
        QEnqueue(newRay);
    }


    // return ambient lighting, no new ray.
    return ray.amt * gAmbient * alb * dif;
}

//---------------------------------------------------------------------
// keyboard & mouse stuff part two

const float cPipScale = 0.45;
vec2  gMouse;

void readKeyboard() {
    const int ZRO = 48;
    kPipOn        =  KEY_TOGGLED(ZRO + 1, iChannel3);
    kXEyedStereo  =  KEY_TOGGLED(ZRO + 2, iChannel3);
    kCuttingPlane =  KEY_TOGGLED(ZRO + 3, iChannel3);
    kZoom         =  KEY_TOGGLED(ZRO + 4, iChannel3);
    kAA           =  KEY_TOGGLED(ZRO + 5, iChannel3);
    kTransmission = !KEY_TOGGLED(ZRO + 6, iChannel3);

    #if HQ
    kAA = true;
    #endif
}

void readMouse() {
    if (texelFetch(iChannel2, ivec2(3, 0), 0).r == 0.0) {
        // This texel should be != 0,
        // so we conclude buffers are broken on this platform.
        // see https://www.shadertoy.com/view/McsXRM

        if (dot(iMouse.xy, iMouse.xy) < 75.0 * 75.0) {
            gMouse = vec2(gT, 0.75 * RES.y);

        }
        else {
            gMouse = iMouse.xy;
        }
    }
    else {
        gMouse = texelFetch(iChannel2, ivec2(0), 0).xy;
        gMouse.x += 140.0;
    }
}



// sdScene() does not include a floor,
// because ray-marching infinite planes is a drag.
// but sometimes we want such a thing.
MST sdSceneWithFloor(in vec3 p) {
    MST ms = sdScene(p);
    opUnion(ms, MST(p.y, gMtlFloor));
    if (ms.dist > 0.0) {
        ms.mtl = gMtlAir;
    }
    return ms;
}

// https://iquilezles.org/articles/normalsSDF
vec3 normalScene(vec3 p) {
    vec3 n = vec3(0.0);
    for(int i = ZERO; i < 4; i++){
        vec3 e = 0.5773*(2.0*vec3((((i+3)>>1)&1),((i>>1)&1),(i&1))-1.0);
        n += e*sdSceneWithFloor(p+e*gNormEps).dist;
    }
    return normalize(n);
}


//-----------------------------------------------------------
// material stuff


vec3 albedo(hit_t hit) {
    uint mtl = hit.ms.mtl;
    vec3 rgb = mtls[mtl].albedo;
    if (mtl == gMtlFloor) {
        float f  = 0.0;
        vec2  xz = mod(hit.pnt.xz * rot2(pi/6.0) + 10.0, 20.0) - 10.0;
        f  = max(f, smoothstep(1.5, 1.0, abs(xz.x)));
        f  = max(f, smoothstep(1.5, 1.0, abs(xz.y)));
        f *= smoothstep(200.0, 100.0, length(hit.pnt.xz));
        rgb = mix(rgb, rgb * 1.3, f);
    }
    return rgb;
}

float ior(MST ms) {
    const float atmosphere = 1.0;

    if (ms.dist > 0.0) {
        return atmosphere;
    }

    return mtls[ms.mtl].ior;
}

vec3 sky(ray_t ray) {

    vec3 c = v1 * (1.0 - (0.2 + 0.8 * saturate(ray.rd.y)));
    c.r *= 0.2;
    c.g *= 0.4;

    float d = dot(ray.rd, gLightDir) * 0.5 + 0.5;

    c = mix(c, v1, sqr(sqr(sqr(d))) * 0.85);

    float dddd = d * d;
    dddd *= dddd;
    c *= mix(sin(dddd * dddd * 30.0 + gT) * 0.5 + 0.5, 1.0, 1.0 - dddd * 0.1);

    float a = atan(ray.rd.z, ray.rd.x);

    c = mix(c, v1 * (1.0 - sqrt(d)), 0.3 * smoothstep(0.06 + (1.0 - d) * 0.02 * cos(a * 23.0), 0.0, abs(ray.rd.y)));
    return c;
}

//--------------------------------------------------------------------------
// ray-vs scene stuff

void rayVsFloor(ray_t ray, inout hit_t hit) {
    float t = rayVsPlane(ray.ro, ray.rd, vY, 0.0);
    if (t > 0.0 && (!hit.hit || t < hit.t)) {
        hit.t   = t;
        hit.hit = true;
        hit.pnt = ray.ro + ray.rd * t;
        hit.of_nrm = vY;
        hit.hf_nrm = hit.of_nrm * -sign(dot(hit.of_nrm, ray.rd));
        hit.ms  = MST(0.0, gMtlFloor);
    }
}

void rayMarch(ray_t ray, inout hit_t hit) {

    float t = 0.0;

    MST ms;
    uint n;
    vec3 p;
    for (n = 0u; n < gMaxMarchSteps; ++n) {

        // if our hit already has a hit
        // and our t is not closer than it, return.
        if (hit.hit && t >= hit.t) {
            return;
        }

        // sample the map (without floor)
        p        = ray.ro + ray.rd * t;        
        ms       = sdScene(p);
        float d  = abs(ms.dist);

        if (d < gMarchEps) {
            // close to a surface!
            // mark it the hit & return
            hit.hit    = true;
            hit.t      = t;
            hit.pnt    = p;
            hit.of_nrm = normalScene(p + ray.rd * gHitBackoff);
            hit.hf_nrm = hit.of_nrm * -sign(dot(hit.of_nrm, ray.rd));

            // the hit should be from the new material. need to re-sample.
            hit.ms     = sdScene(p - hit.hf_nrm * gMarchEps * 2.0);
            return;
        }

        // advance
        t += d * gMarchUnderstep;

        // if we're just too far away, return.
        if (t > gMarchMaxT) {
            return;
        }
    }

    if (t < 1e4 * gSceneScale) {
        // ran out of steps.
        hit.hit = true;
        hit.t   = t;
        hit.pnt = p;
        hit.of_nrm = normalScene(p + ray.rd * gHitBackoff);
        hit.hf_nrm = hit.of_nrm * -sign(dot(hit.of_nrm, ray.rd));
        hit.ms  = ms;        
    }
}

hit_t rayVsScene(ray_t ray) {
    hit_t hit;
    hit.hit = false;
    hit.t   = 1e9;

    rayVsFloor(ray, hit);
    rayMarch  (ray, hit);

    return hit;
}

vec3 runRays() {

    vec3 rgb = v0;

    uint rayCount = 0u;

    while (!QIsEmpty() && rayCount < gMaxRays) {
        rgb += runRay(QDequeue());
        rayCount += 1u;
    }

    if (rayCount >= gMaxRays) {
    //    gDebugRGB.g = 1.0;
    }

    return rgb;
}

//--------------------------------------------------------------------------
// viewport, camera, initial ray stuff.

ray_t getCameraRay(vec2 xy, float stereoOffset) {
    ray_t ret;

    vec3 lookFrom = vZ * gCamDist * gSceneScale;
    float rX      = gT * 0.1;
    float rY      = 0.0;
    rX = (gMouse.x / RES.x * 2.0 - 1.0) * pi * -1.2;
    rY =  smoothstep(1.0, 0.0, gMouse.y / RES.y) * pi * 0.49;

    lookFrom.yz  *= rot2(rY);
    lookFrom.xz  *= rot2(rX);
    lookFrom.y    = max(0.1, lookFrom.y);
    gCamPos       = lookFrom;

    vec3 lookTo   = gSceneCenter;

    vec3 camFw    = normalize(lookTo - lookFrom);
    vec3 camRt    = normalize(cross(camFw, vY));
    vec3 camUp    = cross(camRt, camFw);

    ret.ro            = lookFrom;
    ret.ro           += camRt * stereoOffset;
    ret.rd            = normalize(camFw + (xy.x * camRt + xy.y * camUp) / gFOVZoom);
    ret.amt           = v1;
    ret.isLightingRay = false;

    return ret;
}


void mainImage(out vec4 RGBA, in vec2 XY) {
    // over-large time introduce precision problems
    gT = mod(iTime, float( 60 * 60 * 4));

    readKeyboard();
    readMouse();

    // modify XY for picture-in-picture
    bool isPip = kPipOn && (XY.x < RES.x * cPipScale && XY.y < RES.y * cPipScale);
    if (isPip) {
        XY /= cPipScale;
    }

    vec2  xy           = VIEWFROMSCREEN(XY);
    float stereoOffset = 0.0;

    if (kXEyedStereo) {
        float f = sign(xy.x);
        xy.x -= f * VIEWFROMSCREEN_DIST(RES.x / 2.0);
        xy   *= 2.0;
        stereoOffset = -f * gStereoSep * gSceneScale;
    }

    setupScene();

    QClear();

    if (kAA) {
        vec2 aa = vec2(1, -1) * 0.3 / MINRES;
        QEnqueue(getCameraRay(xy + aa.xx, stereoOffset));
        QEnqueue(getCameraRay(xy + aa.xy, stereoOffset));
        QEnqueue(getCameraRay(xy + aa.yx, stereoOffset));
        QEnqueue(getCameraRay(xy + aa.yy, stereoOffset));
    }
    else {
        QEnqueue(getCameraRay(xy, stereoOffset));
    }

    float rootRayCount = float(QCount());

    vec3 c  = runRays();

    c      /= rootRayCount;
    c      *= 1.0 - pow(lengthSqr(XY/RES - 0.5) * 1.8, 3.0);
    c       = pow(c, vec3(1.0 / 2.2));
    c      += gDebugRGB;

    if (isPip) {
        const vec3 cCool = vec3(0.0, 0.0, 0.2);
        const vec3 cHot  = vec3(1.0, 0.9, 0.2);
        float temp = gMapCount / float(cHeatMapSteps * cHeatMapRays);
        c = mix(cCool, cHot, pow(temp, 0.6));
    }

    RGBA    = vec4(c, 1.0);
}

const vec2  v01 = vec2(0.0, 1.0);
const vec3  vX  = v01.yxx;
const vec3  vY  = v01.xyx;
const vec3  vZ  = v01.xxy;
const vec3  v0  = v01.xxx;
const vec3  v1  = v01.yyy;
const vec4  v40 = v01.xxxx;
const vec4  v41 = v01.yyyy;
const float pi  = 3.14159265359;

//---------------------------------------------------
// Screen-to-View stuff

#define RES    (iResolution.xy)
#define MINRES (min(RES.x, RES.y))
#define ZERO   (min(iFrame,0))

#define VIEWFROMSCREEN_DIST(distance)                     \
        ( (distance) / MINRES / gZoom                     )
#define VIEWFROMSCREEN(xy)                                \
        ( vec2(VIEWFROMSCREEN_DIST((xy).x * 2.0 - RES.x), \
               VIEWFROMSCREEN_DIST((xy).y * 2.0 - RES.y)) )


//---------------------------------------------------
// math conveniences
#define saturate(v) ( clamp((v), 0.0, 1.0) )
float lengthSqr(vec3 a) { return dot(a, a); }
float lengthSqr(vec2 a) { return dot(a, a); }
float sqr(float a) { return a * a; }

mat2 rot2(in float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat2(c, s, -s, c);
}

int   vmax(ivec2 A) { return max(    A.x, A.y); }
float vmax( vec2 A) { return max(    A.x, A.y); }
float vmax( vec3 A) { return max(max(A.x, A.y), A.z); }
int   vmin(ivec2 A) { return min(    A.x, A.y); }
float vmin( vec2 A) { return min(    A.x, A.y); }
float vmin( vec3 A) { return min(min(A.x, A.y), A.z); }
float sat (float A) { return clamp(A,      0.0 ,      1.0 ); }
vec2  sat ( vec2 A) { return clamp(A, vec2(0.0), vec2(1.0)); }
vec3  sat ( vec3 A) { return clamp(A, vec3(0.0), vec3(1.0)); }
#define V2(A, B   )    ( vec2(A, B      ) )
#define V3(A, B, C)    ( vec3(A, B, C   ) )
#define V4(A, B, C, D) ( vec4(A, B, C, D) )

//--------------------------------------------------------------------------
// keyboard conveniences
//
#define KEY_IS_DOWN(key, chan) (texelFetch(chan, ivec2(key, 0), 0).r > 0.0)
#define KEY_TOGGLED(key, chan) (texelFetch(chan, ivec2(key, 2), 0).r > 0.0)


//--------------------------------------------------------------------------
// ray stuff
//
struct ray_t {
    vec3  ro;
    vec3  rd;
    vec3  amt;   // [0, 1] amount of light left in the ray

    // if true, this is a ray looking for the light.
    bool  isLightingRay;
};

struct mapSample_t {
    float dist;
    uint  mtl;
};
#define MST mapSample_t

struct hit_t {
    bool  hit;
    float t;
    vec3  pnt;
    vec3  hf_nrm;
    vec3  of_nrm;
    MST   ms;
};

struct mtl_t {
    vec3  albedo;
    float opacity;         // opacity at the surface
    float ior;
    vec4  absorption;      // <vec3, permeability>
};

//--------------------------------------------------------------------------
// optics

// A modified Schlick's approximation
// where reflectamount -> 0 as n2 -> n1.
float reflectAmount(float n1, float n2, float incidentDotNormal) {
    // from https://en.wikipedia.org/wiki/Schlick%27s_approximation 
    float R0 = sqr((n1 - n2) / (n1 + n2));
    float R  = R0 + (1.0 - R0) * pow(1.0 - incidentDotNormal, 5.0);

    // Schlick's approximation seems invalid when n1 ~= n2.
    // Compensate with an additional fudge.
    // https://physics.stackexchange.com/q/794963
    float ndiffSqr      = sqr(n1 - n2);
    float elenzilFudge  = ndiffSqr / (ndiffSqr + 0.01);
    R                  *= elenzilFudge;

    return R;
}

// n1 == n2          -> no reflection
void thisRayWalksIntoAnInterface(
    //  direction of the incoming ray.
    in  vec3  incident,

    //  surface normal.
    in  vec3  hit_facing_normal,

    //  indices of refraction.
    in  float n1,
    in  float n2,

    in  float opacity,

    //  amount allocated to diffuse lighting
    out float out_sDiffuse,

    //  direction and amount for specular reflection
    out vec4  out_vReflect,

    //  direction and amount for transmission
    out vec4  out_vTransmit
    ) {

    // 0 = transmit 1 = reflect 1
    float transmitVsReflect = reflectAmount(n1, n2, dot(incident, -hit_facing_normal));

    // check for total internal reflection.
    float eta     = n1 / n2;
    out_vTransmit.xyz = refract(incident, hit_facing_normal, eta);
    if (dot(out_vTransmit.xyz, out_vTransmit.xyz) == 0.0) {
        // total internal
        transmitVsReflect = 1.0;
    }

    float amtDiffuse  = (1.0 - transmitVsReflect) * opacity;
    float amtReflect  = transmitVsReflect;
    float amtTransmit =  1.0 - amtDiffuse - amtReflect;

    out_sDiffuse      = amtDiffuse;
    out_vTransmit.w   = amtTransmit;
    out_vReflect.w    = amtReflect;
    out_vReflect.xyz  = reflect(incident, hit_facing_normal);
}

