// Queue.
// This is a basic ringbuffer.
// NO ERROR CHECKING
//
// usage:
// #define Q_TYPE and Q_MAX_ENTRIES,
// then put Q_IMPLEMENTATION.
// (Q_MAX_ENTRIES is a uint)
//
// for example:
// #define Q_TYPE        ray_t
// #define Q_MAX_ENTRIES 16u
// Q_IMPLEMENTATION
//
// orion elenzil 2022.
#define Q_IMPLEMENTATION                           \
const uint gQCapacity = Q_MAX_ENTRIES;             \
const uint gQNumSlots = gQCapacity + 1u;           \
Q_TYPE gQ[gQNumSlots];                             \
uint gQHead = 0u;                                  \
uint gQTail = 0u;                                  \
                                                   \
void QClear() {                                    \
    gQHead = gQTail = 0u;                          \
}                                                  \
                                                   \
uint QCount() {                                    \
    if (gQHead >= gQTail) {                        \
        return gQHead - gQTail;                    \
    }                                              \
    else {                                         \
        return gQNumSlots - (gQTail - gQHead);     \
    }                                              \
}                                                  \
                                                   \
uint QSpaceLeft() {                                \
    return gQCapacity - QCount();                  \
}                                                  \
                                                   \
bool QIsFull() {                                   \
    return QSpaceLeft() == 0u;                     \
}                                                  \
                                                   \
bool QIsEmpty() {                                  \
    return QCount() == 0u;                         \
}                                                  \
                                                   \
uint _QEnqueueIndex() {                            \
    gQHead = (gQHead + 1u) % gQNumSlots;           \
    return gQHead;                                 \
}                                                  \
                                                   \
uint _QDequeueIndex() {                            \
    gQTail = (gQTail + 1u) % gQNumSlots;           \
    return gQTail;                                 \
}                                                  \
                                                   \
void QEnqueue(Q_TYPE item) {                       \
    gQ[_QEnqueueIndex()] = item;                   \
}                                                  \
                                                   \
Q_TYPE QDequeue() {                                \
    return gQ[_QDequeueIndex()];                   \
}

//--------------------------------------------------------------------------------
// CSG Operations.
// These all mutate the first argument and return void.
// MST types are used only where sensible.

void opUnion(inout MST A, MST B) {
    if (A.dist > B.dist) {
        A = B;
    }
}

void opSub(inout float d1, in float d2) {
    d1 = max(d1, -d2);
}

void opSub(inout MST ms1, in float d2) {
    opSub(ms1.dist, d2);
}

void opReplace(inout float d1, in float d2) {
    opSub(d1, d2);
    d1 = min(d1, d2);
}

void opReplace(inout MST ms1, in MST ms2) {
    opSub(ms1.dist, ms2.dist);
    opUnion(ms1, ms2);
}

// Inigo Quilez
// https://iquilezles.org/articles/smin/
// polynomial smooth min
float sminCubic( float a, float b, float k )
{
    float h = max( k-abs(a-b), 0.0 )/k;
    return min( a, b ) - h*h*h*k*(1.0/6.0);
}

// Inigo Quilez
// https://iquilezles.org/articles/distfunctions
// https://www.shadertoy.com/view/Ml3fWj
// return.w must be added to the distance.
vec4 opElongate( in vec3 p, in vec3 h )
{
    vec3 q = abs(p)-h;
    return vec4( max(q,0.0), min(max(q.x,max(q.y,q.z)),0.0) );
}

// Orion Elenzil
// From https://www.shadertoy.com/view/43l3zr
//
// repeats the origin 'reps' times
// around a ring of radius 'rad'.
//
// reps must be a positive integer.
//
// returns:
//   v.xy = new coordinates
//   v.z  = cell index, [0, reps - 1].   
vec3 opRing(in vec2 p, float rad, float reps) {
    float scale = pi * 2.0 / reps;
    float theta;
    float cell;
    float isEven;

    // theta in [-ùúã, ùúã]
    theta  = atan(p.y, p.x);

    // scale theta to [-reps/2, reps/2]
    theta /= scale;

    // determine cell index
    isEven = float((int(reps) + 1) % 2);
    cell   = theta + (reps + isEven) / 2.0;
    cell   = floor(mod(cell, reps));

    // repeat space. theta in [-1/2, 1/2]
    theta  = fract(theta + 0.5) - 0.5;

    // scale up again
    theta *= scale;

    // polar to cartesian
    p      = vec2(cos(theta), sin(theta)) * length(p);

    // move the origin onto the ring
    p.x   -= rad;

    // align +x to be counter-clockwise along the ring.
    // this might not be what you want,
    // but it seems natural to me.
    p      = vec2(p.y, -p.x);

    return vec3(p, cell);
}


//--------------------------------------------------------------------------------
// Signed Distance Field Primitives

float sdDisk(in vec2 p, in float r) {
    return length(p) - r;
}

float sdSphere(in vec3 p, in float r) {
    return length(p) - r;
}

// Inigo Quilez
// https://iquilezles.org/articles/distfunctions
float sdBox( vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

// Inigo Quilez
// https://iquilezles.org/articles/distfunctions/
float sdCapsule( vec3 p, vec3 a, vec3 b, float r )
{
  vec3 pa = p - a, ba = b - a;
  float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
  return length( pa - ba*h ) - r;
}

// Inigo Quilez
// https://iquilezles.org/articles/distfunctions/
float sdCappedCylinder( vec3 p, float h, float r )
{
  vec2 d = abs(vec2(length(p.xz),p.y)) - vec2(r,h);
  return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

float sdCylinder(vec2 p, float r) {
    return length(p) - r;
}

// Inigo Quilez
// https://iquilezles.org/articles/distfunctions/
float sdBoxFrame( vec3 p, vec3 b, float e )
{
       p = abs(p  )-b;
  vec3 q = abs(p+e)-e;
  return min(min(
      length(max(vec3(p.x,q.y,q.z),0.0))+min(max(p.x,max(q.y,q.z)),0.0),
      length(max(vec3(q.x,p.y,q.z),0.0))+min(max(q.x,max(p.y,q.z)),0.0)),
      length(max(vec3(q.x,q.y,p.z),0.0))+min(max(q.x,max(q.y,p.z)),0.0));
}

// Inigo Quilez
// https://iquilezles.org/articles/distfunctions/
float sdTorus( vec3 p, vec2 t )
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}



//--------------------------------------------------------------------------------
// Analytic ray-vs-primitives

// from https://www.cs.princeton.edu/courses/archive/fall00/cs426/lectures/raycast/sld017.htm
float rayVsPlane(in vec3 ro, in vec3 rd, in vec3 n, in float d) {
    float t = -(dot(ro, n) + d) / (dot(rd, n));
    return t;
}


