#define LFO_RATE_MS 25000.0 // LFO Rate in milliseconds
#define TOP_ZOOM .01 // Closest zoom (smallest value)
#define BOTTOM_ZOOM 1.310 // Farthest zoom out (largest value)
#define ELASTICITY 1.0 // Logarithmic elasticity control
#define ELASTICITY_POLARITY -1 // Set to 1 for exponential, -1 for logarithmic elasticity
#define WRAP_OR_MIRROR_FLAG -1 // Set to 1 to enable mirroring of the zoomed area, -1 to enable wrapping
#define DIRECTION_ROTATION 1.0 // Set to -1.0 for full left, 1.0 for full right
#define ROTATION_LFO_MS 25007.0 // LFO for a full 360-degree rotation in milliseconds
#define POST_ROTATION_DIRECTION -1.0 // Set to -1.0 for full left, 1.0 for full right post rotation
#define POST_ROTATION_LFO_MS 20000.0 // LFO for a full 360-degree rotation in milliseconds for post rotation

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord.xy / iResolution.xy;

    // Calculate the time in seconds
    float time = iTime;

    // Calculate the LFO based on the specified rate in milliseconds
    float lfo = 0.5 * (1.0 + sin(2.0 * 3.141592 * (1.0 / (LFO_RATE_MS / 1000.0)) * time));

    // Smooth the LFO for symmetrical elasticity
    float easedLfo = 0.5 * (1.0 + sin(3.141592 * lfo - 3.141592 / 2.0));

    // Interpolate between TOP_ZOOM and BOTTOM_ZOOM
    float baseZoomFactor = mix(TOP_ZOOM, BOTTOM_ZOOM, easedLfo);

    // Apply logarithmic or exponential elasticity based on polarity control
    float elasticZoomFactor;
    if (ELASTICITY_POLARITY == 1) {
        elasticZoomFactor = TOP_ZOOM + (BOTTOM_ZOOM - TOP_ZOOM) * pow(easedLfo, ELASTICITY);
    } else {
        elasticZoomFactor = TOP_ZOOM + (BOTTOM_ZOOM - TOP_ZOOM) * pow(1.0 - easedLfo, ELASTICITY);
    }

    // Rotate the interior LFO
    float rotationAngle = DIRECTION_ROTATION * 2.0 * 3.141592 * (time / (ROTATION_LFO_MS / 1000.0));
    mat2 rotationMatrix = mat2(cos(rotationAngle), -sin(rotationAngle), sin(rotationAngle), cos(rotationAngle));
    vec2 rotatedUv = (rotationMatrix * (uv - 0.5)) + 0.5;

    // Apply zoom to UV coordinates
    vec2 zoomedUv = (rotatedUv - 0.5) / elasticZoomFactor + 0.5;

    // Apply wrapping or mirroring to the zoomed UV coordinates based on the flag
    if (WRAP_OR_MIRROR_FLAG == 1) {
        zoomedUv = abs(fract(zoomedUv * 0.5) * 2.0 - 1.0);
    } else {
        zoomedUv = fract(zoomedUv);
    }

    // Sample the output from Buffer B
    vec3 color = texture(iChannel2, zoomedUv).rgb;

    // Apply post-rotation
    float postRotationAngle = POST_ROTATION_DIRECTION * 2.0 * 3.141592 * (time / (POST_ROTATION_LFO_MS / 1000.0));
    mat2 postRotationMatrix = mat2(cos(postRotationAngle), -sin(postRotationAngle), sin(postRotationAngle), cos(postRotationAngle));
    vec2 postRotatedUv = (postRotationMatrix * (zoomedUv - 0.5)) + 0.5;

    // Apply final wrapping or mirroring
    if (WRAP_OR_MIRROR_FLAG == 1) {
        postRotatedUv = abs(fract(postRotatedUv * 0.5) * 2.0 - 1.0);
    } else {
        postRotatedUv = fract(postRotatedUv);
    }

    // Sample the final output
    vec3 postRotatedColor = texture(iChannel2, postRotatedUv).rgb;

    fragColor = vec4(postRotatedColor, 1.0);
}

#define COLOR1 vec3(1.1, 1.1, 1.25) // Color of the first set of squares
#define COLOR2 vec3(0.23, 0.5, 0.2) // Color of the second set of squares
#define GLOW_COLOR vec3(.4, 4, .5) // Color of the glow
#define GLOW_RADIUS 0.5 // Radius control for the glow
#define GLOW_INTENSITY 1.3 // Intensity control for the glow
#define LFO_PERIOD_MS 18000.0 // LFO period in milliseconds

void mainImage(out vec4 fragColor, in vec2 fragCoord) {

    //***********    Basic setup    **********

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord.xy / iResolution.xy;

    // Calculate LFO value for checker size
    float time = iTime * 1000.0; // Convert time to milliseconds
    float lfo = 0.5 * (1.0 + sin(2.0 * 3.141592 * time / LFO_PERIOD_MS)); // LFO oscillates between 0 and 1
    float checkerSize = mix(4.0, 12.0, lfo); // Map LFO value to range 1 to 16

    // Scale UV coordinates to checkerboard size
    vec2 checkUv = uv * checkerSize;

    // Calculate checkerboard pattern
    float checker = mod(floor(checkUv.y) + floor(checkUv.x), 2.0);

    // Determine the color of the checkerboard cell
    vec3 baseColor = mix(COLOR1, COLOR2, checker);

    // Position of fragment relative to center of the checkerboard cell
    vec2 cellCenter = (floor(checkUv) + 0.5) / checkerSize;
    vec2 pos = uv - cellCenter;

    // Adjust y by aspect for uniform transforms
    pos.y *= iResolution.y / iResolution.x;

    //**********         Glow        **********

    // Hyperbolic falloff for the glow
    float dist = length(pos);
    float glowDist = GLOW_RADIUS / dist;

    // Dampen the glow to control the radius
    glowDist *= 0.1;

    // Intensity control for the glow
    glowDist = pow(glowDist, GLOW_INTENSITY);

    // Color the glow
    vec3 glowColor = glowDist * GLOW_COLOR;

    // Combine the base color and the glow
    vec3 color = baseColor + glowColor;

    // Tonemapping
    color = 1.0 - exp(-color);

    // Output to screen
    fragColor = vec4(color, 1.0);
}

#define BARS_COUNT 32
#define COLOR_POLARITY_FLAG 1
#define BAR_HEIGHT 0.5
#define BAR_SPACING_PERCENTAGE 0.45
#define BAR_COLOR vec3(.1,.0,.0)
#define BACKGROUND_COLOR vec3(0.15, 0.415, 0.2)
#define MIRROR_FLAG 1
#define ORIENTATION_FLAG 1
#define CENTER_OVERLAP 0.00395
#define GRADIENT_START_COLOR vec3(0.2, 1.0, 0.0)
#define GRADIENT_END_COLOR vec3(0.0, 2.0, 2.0)
#define GRADIENT_INTENSITY .7
#define GRADIENT_CONTRAST 4.2
#define MIRROR_Y_BOTTOM_FLAG 0
#define MIRROR_Y_TOP_FLAG 1
#define LOUDNESS_EXPONENT 2.7
#define TRAIL_COUNT 3
#define TRAIL_SCALE 0.979
#define TRAIL_DECAY .625
#define BASS_WARP 1.4
#define AUDIO_ELASTICITY 1.8
#define FLIP_VERTICAL 1
#define TRAIL_DELAY_MS 00.0 // Delay for trails in milliseconds

#define RMS_TIME_WINDOW 10.1 // Time window for RMS calculation in milliseconds
#define BAR_ASPECT_RATIO 5.4 // Aspect ratio for the bars

// Zoom control
#define ZOOM_BASE 1.0
#define BKGD_ELASTICITY 1.8
#define ZOOM_RANGE 3.2
#define ZOOM_POLARITY -1 // 1 for zoom in with amplitude, -1 for zoom out with amplitude
#define ZOOM_WRAP_FLAG 0 // Set to 1 to enable wrapping of the zoomed area
#define ZOOM_MIRROR_FLAG 1 // Set to 1 to enable mirroring of the zoomed area
#define CUTOFF_FREQ 150.0 // Low-pass filter cutoff frequency in Hz

// Composite control
#define DIFFERENCE_COMPOSITE_FLAG 1 // Set to 1 for difference mode for bars
#define DIFFERENCE_BACKGROUND_COMPOSITE_FLAG 0 // Set to 1 for difference mode for background
#define BACKGROUND_TRANSPARENCY 0.5 // Transparency level for unzoomed background
#define BACKGROUND_MOTION_PERCENTAGE 0.5 // Percentage of zoom applied to the stationary background

float previousAmplitude = 0.0;

// Precompute constants
const float totalBars = float(BARS_COUNT) * (MIRROR_FLAG == 1 ? 2.0 : 1.0);
const float barWidth = 1.0 / (totalBars + (totalBars - 1.0) * BAR_SPACING_PERCENTAGE);
const float barSpacing = barWidth * BAR_SPACING_PERCENTAGE;

// Function to get the frequency for a given band index
float getFrequency(int index) {
    float normIndex = float(index) / float(BARS_COUNT);
    float warpedIndex = pow(normIndex, mix(1.0, 0.5, BASS_WARP));
    if (ORIENTATION_FLAG == 1) {
        warpedIndex = 1.0 - warpedIndex;
    }
    return warpedIndex;
}

// Function to get the band value from the texture
float getBand(int index) {
    float freq = getFrequency(index);
    return texture(iChannel0, vec2(freq, 0.0)).r;
}

// Function to calculate RMS for a given time window in milliseconds
float calculateRMS(float timeWindowMs) {
    float rms = 0.0;
    float sum = 0.0;
    float sampleRate = 44100.0; // Assuming audio sample rate of 44100 Hz
    int sampleCount = int(timeWindowMs * sampleRate / 1000.0);
    int sampleStep = max(1, sampleCount / 128); // Reduce the number of samples to 64

    // Sum the squares of the audio texture samples
    for (int i = 0; i < sampleCount; i += sampleStep) {
        float sampleIndex = float(i) / sampleRate;
        float amplitude = texture(iChannel0, vec2(0.5, sampleIndex)).r;
        sum += amplitude * amplitude;
    }

    // Calculate the mean of the squares
    float meanSquare = sum / float(sampleCount / sampleStep);

    // Calculate the RMS value as the square root of the mean square
    rms = sqrt(meanSquare);

    return rms;
}

// Function to get gradient color based on height and gradient parameters
vec4 getGradientColor(float t) {
    t = pow(clamp(t, 0.0, 1.0), GRADIENT_CONTRAST) * GRADIENT_INTENSITY;
    vec3 color = mix(GRADIENT_START_COLOR, GRADIENT_END_COLOR, t);
    return vec4(color, 1.0);
}

// Function to apply trail effect with perspective scaling, luminosity decay, and delay
vec4 applyTrailEffect(vec4 color, float trailIndex, float delayTime) {
    float timeFactor = delayTime * float(trailIndex) / TRAIL_DELAY_MS;
    float scale = pow(TRAIL_SCALE, trailIndex);
    float decayFactor = mix(1.0, 0.0, TRAIL_DECAY) + (1.0 - TRAIL_DECAY) * (1.0 - trailIndex / float(TRAIL_COUNT));
    color.rgb *= decayFactor * scale;
    color.a *= decayFactor;
    return mix(color, vec4(0.0), step(timeFactor, mod(iTime, TRAIL_DELAY_MS) / TRAIL_DELAY_MS));
}

// Function to draw bars
void drawBars(inout vec4 color, vec2 uv, float barWidth, float barSpacing, float scaleX, float rmsAmplitude) {
    for (int i = 0; i < BARS_COUNT; i++) {
        float bandValue = pow(getBand(i) * BAR_HEIGHT, LOUDNESS_EXPONENT) * rmsAmplitude;

        for (int t = 0; t < TRAIL_COUNT; t++) {
            float trailScale = pow(TRAIL_SCALE, float(t));
            float scaledBandValue = bandValue * trailScale;
            float barX = (barWidth + barSpacing) * float(i) * trailScale * scaleX + 0.5 * (1.0 - trailScale * scaleX);

            // Draw bars on the left side
            if (uv.x > barX && uv.x < barX + barWidth * trailScale * scaleX) {
                float barTop = 0.5 + (scaledBandValue / 2.0 * AUDIO_ELASTICITY) * BAR_ASPECT_RATIO;
                float barBottom = 0.5 - (scaledBandValue / 2.0 * AUDIO_ELASTICITY) * BAR_ASPECT_RATIO;
                if (uv.y > barBottom && uv.y < barTop) {
                    float gradientFactor = (uv.y - barBottom) / (barTop - barBottom);
                    vec4 barColor = applyTrailEffect(getGradientColor(gradientFactor), float(t), TRAIL_DELAY_MS);
                    color = mix(color, barColor, barColor.a); // Blend using alpha
                }
            }

            // Draw mirrored bars on the right side
            if (MIRROR_FLAG == 1) {
                float overlap = CENTER_OVERLAP;
                barX = 1.0 - (barWidth + barSpacing) * float(i + 1) * trailScale * scaleX - 0.5 * (1.0 - trailScale * scaleX) + overlap;
                if (uv.x > barX && uv.x < barX + barWidth * trailScale * scaleX) {
                    float barTop = 0.5 + (scaledBandValue / 2.0 * AUDIO_ELASTICITY) * BAR_ASPECT_RATIO;
                    float barBottom = 0.5 - (scaledBandValue / 2.0 * AUDIO_ELASTICITY) * BAR_ASPECT_RATIO;
                    if (uv.y > barBottom && uv.y < barTop) {
                        float gradientFactor = (uv.y - barBottom) / (barTop - barBottom);
                        vec4 barColor = applyTrailEffect(getGradientColor(gradientFactor), float(t), TRAIL_DELAY_MS);
                        color = mix(color, barColor, barColor.a); // Blend using alpha
                    }
                }
            }
        }
    }
}

// Low-pass filter function
float applyLowPassFilter(float amplitude, float cutoffFreq) {
    float rc = 1.0 / (2.0 * 3.141592 * cutoffFreq);
    float dt = 1.0 / 60.0; // Assuming 60 FPS for the time step
    float alpha = dt / (rc + dt);

    // Apply the low-pass filter to smooth the amplitude
    float filteredAmplitude = previousAmplitude + alpha * (amplitude - previousAmplitude);
    previousAmplitude = filteredAmplitude;
    return filteredAmplitude;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord.xy / iResolution.xy;

    // Flip the screen vertically for phone displays
    if (FLIP_VERTICAL == 1) {
        uv = vec2(uv.y, 1.0 - uv.x);
    }

    // Calculate RMS amplitude for a time window
    float rmsAmplitude = calculateRMS(RMS_TIME_WINDOW);

    // Apply low-pass filter to the RMS amplitude for zoom factor calculation
    float filteredAmplitude = applyLowPassFilter(rmsAmplitude, CUTOFF_FREQ);

    // Calculate zoom factor based on filtered amplitude
    float zoomFactor = ZOOM_BASE + ZOOM_RANGE * pow(filteredAmplitude, BKGD_ELASTICITY);
    zoomFactor = (ZOOM_POLARITY == 1) ? 1.0 / zoomFactor : zoomFactor;

    // Apply zoom to UV coordinates for background
    vec2 zoomedUv = (uv - 0.5) / zoomFactor + 0.5;

    // Apply wrapping or mirroring to the zoomed UV coordinates
    if (ZOOM_WRAP_FLAG == 1) {
        zoomedUv = fract(zoomedUv);
    } else if (ZOOM_MIRROR_FLAG == 1) {
        zoomedUv = abs(fract(zoomedUv * 0.5) * 2.0 - 1.0);
    }

    // Pull the zoomed background from Buffer A
    vec3 backgroundColor = texture(iChannel1, zoomedUv).rgb;

    // Apply motion percentage to the unzoomed background
    vec2 motionUv = (uv - 0.5) / (1.0 + (zoomFactor - 1.0) * BACKGROUND_MOTION_PERCENTAGE) + 0.5;
    if (ZOOM_WRAP_FLAG == 1) {
        motionUv = fract(motionUv);
    } else if (ZOOM_MIRROR_FLAG == 1) {
        motionUv = abs(fract(motionUv * 0.5) * 2.0 - 1.0);
    }
    vec3 motionBackgroundColor = texture(iChannel1, motionUv).rgb;

    // Combine the motion background with the zoomed background using the specified composite mode
    if (DIFFERENCE_BACKGROUND_COMPOSITE_FLAG == 1) {
        backgroundColor = abs(motionBackgroundColor - backgroundColor);
    } else {
        backgroundColor = mix(backgroundColor, motionBackgroundColor, BACKGROUND_TRANSPARENCY);
    }

    // Initial color based on background from Buffer A
    vec4 finalColor = vec4(backgroundColor, 1.0);

    // Draw bars
    vec4 barColor = vec4(0.0, 0.0, 0.0, 0.0);
    drawBars(barColor, uv, barWidth, barSpacing, 1.0, rmsAmplitude);

    // Buffer the bar calculations
    vec4 bufferedColor = barColor;

    // Apply post-process mirroring effect to the buffered color
    if (MIRROR_Y_BOTTOM_FLAG == 1) {
        vec2 mirroredUv = uv;
        mirroredUv.y = 1.0 - uv.y;
        vec4 mirrorColor = vec4(0.0, 0.0, 0.0, 0.0);
        drawBars(mirrorColor, mirroredUv, barWidth, barSpacing, 1.0, rmsAmplitude);
        bufferedColor.rgb = mix(bufferedColor.rgb, mirrorColor.rgb, step(0.5, uv.y));
    }

    if (MIRROR_Y_TOP_FLAG == 1) {
        vec2 mirroredUv = uv;
        mirroredUv.y = 1.0 - uv.y;
        vec4 mirrorColor = vec4(0.0, 0.0, 0.0, 0.0);
        drawBars(mirrorColor, mirroredUv, barWidth, barSpacing, 1.0, rmsAmplitude);
        bufferedColor.rgb = mix(bufferedColor.rgb, mirrorColor.rgb, step(uv.y, 0.5));
    }

    // Combine final background and buffered bar colors with the chosen composite mode
    if (DIFFERENCE_COMPOSITE_FLAG == 1) {
        finalColor.rgb = abs(finalColor.rgb - bufferedColor.rgb);
    } else {
        finalColor.rgb = mix(finalColor.rgb, bufferedColor.rgb, bufferedColor.a);
    }

    if (COLOR_POLARITY_FLAG == 1) {
        finalColor.rgb = vec3(1.0) - finalColor.rgb;
    }

    fragColor = finalColor;
}
