void mainImage( out vec4 o, vec2 u )
{
    o.xyz = iResolution;
    u = (u+u-o.xy)/o.y;
    o -= o; 

    for (int a; a < 3; a++)
    for (float i; i++ < 7.; u /= .998)             
        o[a] += .7/i/length(exp(-9.*cos(i/(.3+u*u) + iTime)) 
                          + tan(o[a]*5.-iTime/i)              
                          + tan(u/i)*i/2.);
}


