void squares( out vec4 color, in vec2 fragCoord )
{
    vec2 uv = fragCoord;

    vec3 col = vec3(sin(uv.x * 64.), cos(uv.y * 128.), tan(uv.xy * 64.));

    color = vec4(col, 1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2. * fragCoord - iResolution.xy) / min(iResolution.x, iResolution.y);

    uv *= min(iTime / 20., .5) * (.01 * sin(iTime)) + .2;

    vec4 uvcolor;
    squares(uvcolor, uv.yx);

    vec4 color;
    squares(color, uv.xy);


    color /= uvcolor;
    color *= iTime *(iTime / 5.);

    fragColor = color;
}
