#define f(x,y) exp( 1.504 - 3.* length( U - vec2(x,y) ) ) // disc pattern

void mainImage( out vec4 O, vec2 U )
{
    vec2 R = iResolution.xy;
         U += U - R;
    float l = length(U+U)/R.y, v,  
          a = mod( atan(U.y,U.x)+.52, 2.094 ) - .52;
    U = l * vec2( cos(a), sin(a) );        // 3-rot symmetry
    v = f(0,-1);
    v = v > .224                           // if bottom disc overlap
          ? v                              //   draw bottom disc
          : l < 1. && a*a < .27            // 
            ? 1./f(0,0)                    //   draw central disc
            : f(.87,.5);                   //   draw top-right disc
    v = cos( 6.9 * v );                    // produce periodic contours
    O = vec4(sqrt(abs(v)/fwidth(v)));      // AA-draw & to sRGB
 // O = vec4(.38/sqrt(abs(tan(6.9*v)*fwidth(v))));        
}






