{ R = iResolution.xy;
    if (iFrame%4>0)discard;
     U = U/N;
     Q = vec4(0.);
     for (float i = 4.; i < N*N-4.; i+=.5) {
         vec2 u = (U-.5*R3.xy)*(1.+.01*i)-vec2(0,.2*i-5.)+.5*R3.xy;
         if (u.x>4.&&u.x<R3.x-4.&&u.y>4.&&u.y<R3.y-4.) {
             vec4 b = B(vec3(u,i));
             vec4 a = A(vec3(u,i));
             Q.yxz += vec3(1.3,.7,1.8)*a.w*a.w*length(b.xyz)*
                 texture(iChannel2,reflect(vec3(0,0,1),normalize(b.xyz))).xyz
                 *exp(-5.*length(Q.xyz));
         }
     }
}

// 3D utilities 
#define N 10.
#define dt .5
vec2 R;
//  [ [0,R/N]; [0,N*N]  ] < -- > [0,R]
#define Main void mainImage (out vec4 Q, in vec2 _U) {R = iResolution.xy;vec3 U = d3(_U);
#define A(U) s3d(iChannel0,U)
#define B(U) s3d(iChannel1,U)
#define R3 vec3(R/N,N*N)
vec2 d2 (vec3 U) {
	return U.xy+vec2(mod(U.z,N),floor(U.z/N))*R/N;
}
vec3 d3 (vec2 u) {
    vec2 o = floor(u/R*N);
	return vec3(mod(u,R/N),o.x+o.y*N);
}
vec4 s3d (sampler2D T,vec3 U) {
    vec3 U0 = vec3 (U.xy,floor(U.z)),
         U1 = vec3 (U.xy, ceil(U.z));
    vec4 o = mix(
        texture(T,d2(U0)/R),
        texture(T,d2(U1)/R),
        fract(U.z)
    );
    return o;
}
#define vel(v) .5*(v)*inversesqrt(1.+5.*dot(v,v))
#define inside(v) ((v).x>=1.&&(v).y>=1.&&(v).z>=1.&&(v).x<R3.x-1.&&(v).y<R3.y-1.&&(v).z<R3.z-1.)

