    for (int z = -1; z <= 1; z++)
    if (x!=0||y!=0||z!=0) {
        vec3 v = vec3(x,y,z);
        vec3 W = U+v;
        vec4 q = A(W);

        vec3 a = vel(Q.xyz),
             b = vel(q.xyz)+v;
        float ab = dot(v,b-a);
        if (inside(W)&&abs(ab)>0.) {
            float i = dot(v,(0.5*v-a))/ab;
            float j = .5+.1*clamp(1.-q.w,0.,1.);
            float k = .5+.1*clamp(1.-Q.w,0.,1.);
            float wa = K*Q.w*min(i,j)/j;
            float wb = K*q.w*max(k+i-1.,0.)/k;
            dQ += vec4(Q.xyz,1)*wa+vec4(q.xyz,1)*wb;
        } else {
            dQ += K*Q.w*vec4(Q.xyz,1);
        }

    }
    if (dQ.w>0.) dQ.xyz /= dQ.w;
    Q = dQ;
}

