const int SIZE = 21;
const float SCALE = 1.;

float leaves( vec2 uv )
{
    return step(texture(iChannel0,uv).x,.4);
}

vec2 anim( int phase )
{
    return (sin(vec2(.7,.6)*iTime+vec2(phase,phase+2)*6.283*.618))*.03;
}

vec3 render( vec2 uv )
{
    vec3 col = vec3(6,5,4); // very bright sunlit sky

    uv *= .5;

    for ( int i=0; i < 5; i++ )
    {
        col = mix( col, 1.2*pow(vec3(.73,.87,.2),4.*vec3(i+1)), leaves(uv+anim(i)*(length(uv)*.5+.3)) );
        uv = uv*sqrt(3./4.) + uv.yx*vec2(-1,1)*sqrt(1./4.)+vec2(.618,1.-.618);
    }

    return col;
}

void mainImage( out vec4 fragColour, in vec2 fragCoord )
{
    fragColour = vec4(0,0,0,1);

    float n=0.;
    for ( int i=0; i<=SIZE; i++ )
    {
        for ( int j=0; j<=SIZE; j++ )
        {
            vec2 uvo = vec2(i,j)-float(SIZE)/2.;
            if ( length(uvo) > float(SIZE)*.5+.5 ) continue;
            fragColour.rgb += render((fragCoord+uvo*SCALE)/iResolution.y);
            n++;
        }
    }
    fragColour.rgb /= n;

    fragColour.rgb = pow(fragColour.rgb,vec3(1./2.2));
}
