#define pi 3.14159265359 

mat2 r2d(float a) {
    return mat2(cos(a),sin(a),-sin(a),cos(a));
}

vec2 mouseRotZoom(vec2 uv) {
    // allow mouse zoom and rotate    
    vec2 mouse = (iMouse.xy == vec2(0.)) ? vec2(1.0,0.1) : iMouse.xy/iResolution.xy;
    uv.xy *= r2d(-(mouse.x)*pi*2.);
    uv *= clamp((1./(10.0*mouse.y)),0.2,1.3);
    return uv;
}

#define MOUSE_ROTZOOM
//#define MOUSECOLOR

void mainImage(out vec4 o, vec2 u)
{
 vec2 mouse = (iMouse.xy == vec2(0.)) ? vec2(1.0) : iMouse.xy/iResolution.xy;

    vec2 v = iResolution.xy, w, k = u = .2 * (u + u - v) / v.y;
    o = vec4(1, 2, 3, 0);
    float a = .5, t = iTime;

#ifdef MOUSE_ROTZOOM
    u = mouseRotZoom(u);
#endif

    for (float i=0.; i++ < 25.; i++) {
        v = cos(++t - (8.) * u * pow(a += .03, i)) - 6. * u,
        u *= mat2(cos(i + t * .03 - vec4(0, 11, 33, 0))),
        u += .006 * tanh(50. * dot(u, u) * cos(1e2 * u.yx + t))
           + .3 * a * u 
           + .004 * cos(t + 5. * exp(-.02 * dot(o, o))),
        w = u / (1. - 3. * dot(u, u));
        o += (1. + cos(vec4(0, 1, 3, 0) + t)) / length((1. + i * dot(v, v)) * sin(w * 4. - 12. * u.yx + t));
    }

    o = pow(o = 1. - sqrt(exp(-o * o * o / 4e2)), -(.3) * o / o) 
                        - dot(k -= u, k) / 10.;

    o = vec4(sin(o.xyz + iTime * vec3(.2, .3, .4)) * .5 + .5, 1.0);

#ifdef MOUSECOLOR
    o = vec4( o.xyz - pow(o.xyz,vec3((mouse.y+0.5)/mouse.x*4.75)), 1.0 );
#else
    o = vec4( o.xyz - pow(o.xyz,vec3((1.+0.5)/1.*4.75)), 1.0 );
#endif
}
