void mainImage(out vec4 O, vec2 I)
{
    for(O -= O;
        O.w++ < 3e2 && (O.z = log2(length(O))) < 9.;
        O.xy = mat2(O.xy, -O.y, O.x) * O.xy + 2. * I / iResolution.y - 1.,
        O.x -= 1.5);

    O = .5 - .5 * cos((O.w - log2(O.z) + vec4(0, 3, 6, 0)) / 6.);
}

