mat2 rotate(float angle) {
    float s = sin(angle);
    float c = cos(angle);
    return mat2(c, -s, s, c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (2.*fragCoord.xy-iResolution.xy)/min(iResolution.x, iResolution.y);
    uv *= 2.;

    vec2 huv = HexCoord(uv).xy;

    float ts = .5+.5*sin(iTime*.6);
    float tc = .5+.5*cos(iTime*.5)*.4+.1;

    float s =  .5/sqrt(3.);
    float c =     ring(huv, vec2(  .5,     s ), ts*.5+.2);
    c = min(c,    ring(huv, vec2( -.5,     s ), ts*.5+.2));
    c = min(c,    ring(huv, vec2(  .5,    -s ), ts*.5+.2));
    c = min(c,    ring(huv, vec2( -.5,    -s ), ts*.5+.2));
    c = min(c,    ring(huv, vec2(  0.,  2.*s ), ts*.5+.2));
    c = min(c,    ring(huv, vec2(  0., -2.*s ), ts*.5+.2));
    c = min(c, hexRing(huv, vec2(  .5,     s ), tc));
    c = min(c, hexRing(huv, vec2( -.5,     s ), tc));
    c = min(c, hexRing(huv, vec2(  .5,    -s ), tc));
    c = min(c, hexRing(huv, vec2( -.5,    -s ), tc));
    c = min(c, hexRing(huv, vec2(  0.,  2.*s ), tc));
    c = min(c, hexRing(huv, vec2(  0., -2.*s ), tc));
    c = min(c, starRing(rotate(sin(iTime))*huv, vec2(0.), ts*.2));
    c = clamp(.0125/abs(c)*(dot(uv, uv)+.5), 0., 1.);

    vec3 col = palette( c, vec3(.7), vec3(1.), vec3(1.), vec3(.0, .1, .2) );
    col *= col;
    col = grain(uv, col, vec3(hash12(uv*314159.26)), .25);

    fragColor = vec4(col, 1.);
}

