#define TWOPI 6.28318530718

vec3 permute(vec3 x) { return mod(((x*34.0)+1.0)*x, 289.0); }

float snoise(vec2 v){
  const vec4 C = vec4(0.211324865405187, 0.366025403784439, -0.577350269189626, 0.024390243902439);
  vec2 i = floor(v + dot(v, C.yy));
  vec2 x0 = v - i + dot(i, C.xx);
  vec2 i1 = (x0.x > x0.y) ? vec2(1.0, 0.0) : vec2(0.0, 1.0);
  vec4 x12 = x0.xyxy + C.xxzz;
  x12.xy -= i1;
  i = mod(i, 289.0);
  vec3 p = permute(permute(i.y + vec3(0.0, i1.y, 1.0 )) + i.x + vec3(0.0, i1.x, 1.0));
  vec3 m = max(0.5 - vec3(dot(x0,x0), dot(x12.xy,x12.xy), dot(x12.zw,x12.zw)), 0.0);
  m = m*m;
  m = m*m;
  vec3 x = 2.0 * fract(p * C.www) - 1.0;
  vec3 h = abs(x) - 0.5;
  vec3 ox = floor(x + 0.5);
  vec3 a0 = x - ox;
  m *= 1.79284291400159 - 0.85373472095314 * (a0*a0 + h*h);
  vec3 g;
  g.x  = a0.x  * x0.x  + h.x  * x0.y;
  g.yz = a0.yz * x12.xz + h.yz * x12.yw;
  return 130.0 * dot(m, g);
}

const int cellsLength = 5;

vec3 colors[cellsLength] =  vec3[cellsLength](
    vec3(0.8, 0.25, 0.25), 
    vec3(0.25, 0.8, 0.25),
    vec3(0.25, 0.25, 0.8),
    vec3(0.8, 0.5, 0.25),     
    vec3(0.25, 0.5, 0.8)
);

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy;
    uv = fract(uv); // idk, glitches without it

    uv = floor(uv*200.0)/200.0;

    float h = snoise(uv * 3.0) / 3.0;
    h += snoise(uv * 5.0) / 5.0;
    h += snoise(uv * 10.0) / 10.0;
    h += snoise(uv * 15.0) / 15.0;
    h += snoise(uv * 25.0) / 25.0;
    h = h * 0.5 + 0.5;

    float noiseDisplace = snoise(uv * 2.0);
	uv = vec2(
        uv.x + 0.02 * sin(noiseDisplace * TWOPI),
        uv.y + 0.02 * cos(noiseDisplace * TWOPI)
    );

    float noiseDisplace2 = snoise(uv * 4.0);
    uv = vec2(
        uv.x + 0.04 * sin(noiseDisplace2 * TWOPI),
        uv.y + 0.04 * cos(noiseDisplace2 * TWOPI)
    );

    float noiseDisplace3 = snoise(uv * 8.0);
    uv = vec2(
        uv.x + 0.01 * sin(noiseDisplace3 * TWOPI),
        uv.y + 0.01 * cos(noiseDisplace3 * TWOPI)
    );

    vec2 cells[cellsLength] = vec2[cellsLength](
        iMouse.xy / iResolution.xy, 
        vec2(0.3, 0.5),
        vec2(0.5, 0.6),
        vec2(0.7, 0.1),         
        1.0 - iMouse.xy / iResolution.xy
    );

	vec2 fromUVtoNearestCell;
    float minDistanceToCell = 1.0;
    int nearestCellIndex = 0;

    for(int i = 0; i < cellsLength; i++) {
        vec2 fromUVtoCurrentCell = cells[i] - uv;
        float distanceToCell = length(fromUVtoCurrentCell);

        if(distanceToCell < minDistanceToCell) {
            nearestCellIndex = i; 
            minDistanceToCell = distanceToCell;
            fromUVtoNearestCell = fromUVtoCurrentCell;
        }
    }

    float minDistanceToEdge = 1.0;

    for(int i = 0; i < cellsLength; i++) {
        vec2 distanceToCell = cells[i] - uv;

        minDistanceToEdge = min(
            minDistanceToEdge,
            dot(0.5 * (fromUVtoNearestCell + distanceToCell), normalize(distanceToCell - fromUVtoNearestCell))
        );
    }

    vec3 color = colors[nearestCellIndex] * pow(minDistanceToEdge, 0.5);
    fragColor = vec4(floor(color*16.0)/16.0, 1.0);
}
