//Shadertoy implementation of Growing (and regenerating) Neural Cellular Automatons
//https://distill.pub/2020/growing-ca/

//Given that there were already neural networks here, I'm surprised nobody made a port of this. 
//Tho I guess its not as obvious how to put 16(12 here) channels into a single buffer here.

//I trained the model from scratch using my own library TensorFrost.
//https://github.com/MichaelMoroz/TensorFrost/tree/main/examples/ML/NCA

//This version is different from original NCAs in 2 ways, I've added a Laplacian kernel on top of the Sobel
//kernels, this reduces checkerboard artifacts significantly (as now it can just learn diffusion equations)
//Also the state was trained while quantized to 9 bits, as well as clamped between -1 1, 
//and with added constant noise

//This iteration uses 128 neurons just as the original

//Press Space to change between Draw and Erase mode

//MIT License

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    fragColor = texture(ch0, fragCoord*ZOOM/R); //zoomed in cause lizards are very tiny
}

const int KEY_SPACE = 32;
const int KEY_LEFT  = 37;
const int KEY_UP    = 38;
const int KEY_RIGHT = 39;
const int KEY_DOWN  = 40;

bool isKeyPressed(int KEY)
{
	return texelFetch( iChannel3, ivec2(KEY,2), 0 ).x > 0.5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragCoord = floor(fragCoord);
    ivec2 pos = ivec2(fragCoord);

    vec12 state = ComputeUpdate(ch0, pos, iTime+fragCoord.x*0.25);

    if(iFrame < 5) 
    {
        state.a = vec4(0.0);
        state.b = vec4(0.0);
        state.c = vec4(0.0);

        if(pos.x % 60 == 25 && pos.y % 60 == 25)
            state.a.w = 1.0;
    }

    ivec2 mouse = ivec2(ZOOM*iMouse.xy);

    if(isKeyPressed(KEY_SPACE))
    {
        if(iMouse.z > 0. && distance(fragCoord, vec2(mouse))<20.0)
        {
            state.a = vec4(0.0);
            state.b = vec4(0.0);
            state.c = vec4(0.0);
        }
    }
    else
    {
        if(iMouse.z > 0. && (mouse.x == pos.x) && (mouse.y == pos.y))
        {
            state.a.w = 1.0;
        }
    }


    fragColor = StoreState(state);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragCoord = floor(fragCoord); 
    vec12 state = LoadState(ch0, ivec2(fragCoord));

    fragColor = 1.0 - state.a.w + state.a; //change state.a to .b/.c to see hidden channels
}
