#define R(p,a,t) mix(a*dot(p,a),p,cos(t))+sin(t)*cross(p,a)
#define H(h) (cos((h)*6.3+vec3(0,23,21))*.5+.5)
float happy_star(vec2 uv, float anim)
{
    uv = abs(uv);
    vec2 pos = min(uv.xy/uv.yx, anim);
    float p = (2.0 - pos.x - pos.y);
    return (2.0+p*(p*p-1.5)) / (uv.x+uv.y);      
}

void mainImage(out vec4 O, vec2 C)
{
vec2 uv = (C.xy / iResolution.xy) - .5;
	float t2 = iTime * .1 + ((.25 + .05 * sin(iTime * .1))/(length(uv.xy) + .07)) * 2.2;
	float si = sin(t2);
	float co = cos(t2);
	mat2 ma = mat2(co, si, -si, co);
    vec3 r=iResolution,c=vec3(0),
    d=normalize(vec3(C-.5*r.xy,r.y));

    float i=0.,s,e,g=0.,t=iTime;
	for(;i++<100.;){

        vec4 p=vec4(g*d,.07);
        p.z-=.5;

        p.xyz=R(p.xyz,normalize(H(t*.105)),0.);
        s=1.;
        for(float j=0.;j<12.; j++){
            p=.04-abs(p);
            s*=e=max(1./dot(p,p),1.7-0.1+0.1*cos(iTime));



            p=abs(p.x<p.y?p.wzxy:p.wzyx)*e-1.;
              p=(p.z<p.y?p.wyxz:p.wzxy);
        e=abs(length(p.zx*p.w))/s;

	    g+=e+.00005004;
	    c+=mix(vec3(1),H(log(s)),0.05)*.025/exp(i*i*e);
	}}
	c*=c;
  O=vec4(c,1);
   float anim = sin(iTime * 12.0) * 0.1 + 1.0;  
    O*= vec4(happy_star(uv, anim) * vec3(0.535,0.5,10.55)*0.0035, 1.0);

}
