#define I iResolution
#define A min( I.x, I.y )
#define e iDate.a

float rep(inout vec2 u, float rep){

    float r = 6.28 / rep; // each radian

    float a = round( atan(u.y, u.x) / r ) * r; // aligned uv

    u = vec2(
        dot( u, vec2( cos(a), sin(a) ) ),
        dot( u, vec2( -sin(a), cos(a) ) )
    );

    return floor( mod( ( round( a / r ) + rep * .5 ), rep ) ); // index

}

void mainImage( out vec4 U, in vec2 V )
{
    vec2 x = ( V * 2. - I.xy ) / A, y = x, z = x;

    float p, t = float( int(e) / 3600 % 24 );

    x.x -= ( mod( rep( x, 12. )+3., 12. ) == mod( 12. - mod( floor( e / 60. / 60. ), 12. ), 12. ) ) ? .9 : .8;

    p = max( abs(x.x) - .08, abs(x.y) - .01 );

    y.x -= ( mod( rep( y, 60. )+15., 60. ) == mod( 60. - mod( floor( e / 60. ), 60. ), 60. ) ) ? .55 : .6;

    p = min(
        p,
        max( abs(y.x) - .06, abs(y.y) - .01 )
    );

    z.x -= ( mod( rep( z, 60. )+15., 60. ) == mod( 60. - mod( floor( e ), 60. ), 60. ) ) ? .35 : .4;

    p = min(
        p,
        max( abs(z.x) - .04, abs(z.y) - .01 )
    );

    float AA = ( t >= 8. && t <= 19. ) ? -2. / A : 2. / A;

    U = vec4( smoothstep( AA, -AA, p ) );
}
