#define MAX_ITER 8.0
#define MAX_MARCHING_STEPS 100
#define EPSILON 0.001
#define MARCHING_END 100.0
#define POWER 8.0

vec3 hue2rgb(float hue) {
    float r = abs(hue * 6.0 - 3.0) - 1.0;
    float g = 2.0 - abs(hue * 6.0 - 2.0);
    float b = 2.0 - abs(hue * 6.0 - 4.0);
    return clamp(vec3(r, g, b), 0.0, 1.0);
}

float lerp(float a, float b, float t) {
    return a + (b - a) * t;
}

// Distance estimator for the Mandelbulb
float mandelbulbDE(vec3 pos) {
    vec3 z = pos;
    float dr = 1.0;
    float r = 0.0;

    for (float i = 0.0; i < MAX_ITER; i++) {
        r = length(z);
        if (r > 2.0) break;

        // Convert to polar coordinates
        float theta = atan(sqrt(z.x * z.x + z.y * z.y), z.z);
        float phi = atan(z.y, z.x);
        float zr = pow(r, POWER - 1.0);
        dr = zr * dr * POWER + 1.0;

        // Scale and rotate the point
        zr = zr * r;
        z = zr * vec3(sin(theta * POWER) * cos(phi * POWER), sin(theta * POWER) * sin(phi * POWER), cos(theta * POWER));
        z += pos;
    }
    return 0.5 * log(r) * r / dr;
}

float map(vec3 p) {
    return mandelbulbDE(p);
}

// Ray marching to find the surface
float shortestDistanceToSurface(vec3 eye, vec3 marchingDirection, float start, float end) {
    float depth = start;
    for (int i = 0; i < MAX_MARCHING_STEPS; i++) {
        float dist = map(eye + depth * marchingDirection);
        if (dist < EPSILON) {
            return depth;
        }
        depth += dist;
        if (depth >= end) {
            return end;
        }
    }
    return end;
}

vec3 rayDirection(float fieldOfView, vec2 size, vec2 fragCoord) {
    vec2 xy = fragCoord - size / 2.0;
    float z = size.y / tan(radians(fieldOfView) / 2.0);
    return normalize(vec3(xy, -z));
}

vec3 estimateNormal(vec3 p) {
    return normalize(vec3(
        mandelbulbDE(vec3(p.x + EPSILON, p.y, p.z)) - mandelbulbDE(vec3(p.x - EPSILON, p.y, p.z)),
        mandelbulbDE(vec3(p.x, p.y + EPSILON, p.z)) - mandelbulbDE(vec3(p.x, p.y - EPSILON, p.z)),
        mandelbulbDE(vec3(p.x, p.y, p.z + EPSILON)) - mandelbulbDE(vec3(p.x, p.y, p.z - EPSILON))
    ));
}

vec3 phongContribForLight(vec3 k_d, vec3 k_s, float alpha, vec3 p, vec3 eye, vec3 lightPos, vec3 lightIntensity) {
    vec3 N = estimateNormal(p);
    vec3 L = normalize(lightPos - p);
    vec3 V = normalize(eye - p);
    vec3 R = reflect(-L, N);

    float dotLN = dot(L, N);
    float dotRV = dot(R, V);

    if (dotLN < 0.0) {
        return vec3(0.0, 0.0, 0.0);
    }

    if (dotRV < 0.0) {
        return lightIntensity * (k_d * dotLN);
    }
    return lightIntensity * (k_d * dotLN + k_s * pow(dotRV, alpha));
}

vec3 phongIllumination(vec3 k_a, vec3 k_d, vec3 k_s, float alpha, vec3 p, vec3 eye) {
    const vec3 ambientLight = 0.5 * vec3(1.0, 1.0, 1.0);
    vec3 color = ambientLight * k_a;

    vec3 light1Pos = vec3(2.0 * sin(iTime), 2.0, 2.0 * cos(iTime));
    vec3 light1Intensity = vec3(0.4, 0.4, 0.4);

    color += phongContribForLight(k_d, k_s, alpha, p, eye, light1Pos, light1Intensity);

    vec3 light2Pos = vec3(2.0 * sin(0.5 * iTime), 2.0 * cos(0.5 * iTime), 2.0);
    vec3 light2Intensity = vec3(0.4, 0.4, 0.4);

    color += phongContribForLight(k_d, k_s, alpha, p, eye, light2Pos, light2Intensity);
    return color;
}

mat3 viewMatrix(vec3 eye, vec3 center, vec3 up) {
    vec3 f = normalize(center - eye);
    vec3 s = normalize(cross(f, up));
    vec3 u = cross(s, f);
    return mat3(s, u, -f);
}

void mainImage(out vec4 O, vec2 fc) {
    vec2 uv = (fc.xy - 0.5 * iResolution.xy) / iResolution.y;
    vec3 eye = vec3(0.0, 0.0, 5.0);
    vec3 center = vec3(0.0, 0.0, 0.0);
    vec3 up = vec3(0.0, 1.0, 0.0);

    mat3 viewToWorld = viewMatrix(eye, center, up);
    vec3 rayDir = viewToWorld * rayDirection(45.0, iResolution.xy, fc.xy);
    float dist = shortestDistanceToSurface(eye, rayDir, 0.0, MARCHING_END);
    vec3 p = eye + dist * rayDir;

    vec3 color = phongIllumination(vec3(0.1), vec3(0.7), vec3(0.2), 20.0, p, eye);
    color = pow(color, vec3(0.454545));
    O = vec4(color, 1.0);
}
